    padding-right: -17px !important;

}





#home_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url(http://static.tumblr.com/zxyjrbe/uXZnfd2ka/1-acn-smaller.png) !important;}

/* Explore all of Tumblr - Will */



