    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url(http://static.tumblr.com/zxyjrbe/8b8nfd2b2/2-suit.png) !important;}


/* Inbox - Mac */



