css:
@-moz-document domain("wikipedia.org") {

@font-face {
  font-family: 'Lora';
  font-style: normal;
  font-weight: 400;
  src: local('Lora'), local('Lora-Regular'), url(http://fonts.gstatic.com/s/lora/v9/TcsNKdoPLB0tZA6xZSRyAg.woff2) format('woff2'), url(http://fonts.gstatic.com/s/lora/v9/YQixcBt4h8DmZwK9P1bhEQ.woff) format('woff');
}
@font-face {
  font-family: 'Lora';
  font-style: normal;
  font-weight: 700;
  src: local('Lora Bold'), local('Lora-Bold'), url(http://fonts.gstatic.com/s/lora/v9/jPUN-Jv9o8_YY1w-VfM3DA.woff2) format('woff2'), url(http://fonts.gstatic.com/s/lora/v9/ygC2Fp8ylofzQwABjiUjiA.woff) format('woff');
}
@font-face {
  font-family: 'Lora';
  font-style: italic;
  font-weight: 400;
  src: local('Lora Italic'), local('Lora-Italic'), url(http://fonts.gstatic.com/s/lora/v9/-kdDKU8oN9KFA5ZRoLcwgg.woff2) format('woff2'), url(http://fonts.gstatic.com/s/lora/v9/dkGoamsR8Uep2XQql1FplA.woff) format('woff');
}

  
*{  
  font-family: Lora;
  line-height: 1.65;
}

body{
  background-color: #FFF;
}

#mw-page-base{
  background: none;
}

#mw-panel > div:not(#p-lang){
  display: none;
}

#mw-head > div:not(#right-navigation), #p-views{
  display: none;
}

.mw-body{
  border: none;
  max-width: 35em;
  margin: 0em auto;
  font-size: 1.7em;
}

div#mw-panel{
  position: fixed;
  top: 5em;
  padding-top: 0px;
  left: 0em;
  margin: 0px;
  text-align: left !important;
}

div#mw-panel div.portal div.body{
  margin: 0px;
}

#p-lang-label, .after-portlet-lang, .uls-settings-trigger{
  display: none;
}

div#mw-panel div.portal{
  background: none;
}

a{
  color: #000 !important;
  text-decoration: none !important;
  font-style: italic;
  transition: color 0.15s;
}

a:hover{
  color: #FF6200 !important;
}

p{
  text-align: justify;
  -moz-hyphens: auto;
  hyphens: auto;
}

h1, h2, h3, h4, h5, h6{
  margin-top: 3em !important;
  color: #aaa;
  border: none;
  box-shadow: 0px 6px 6px -7px rgba(0,0,0,0.75);
  text-align: left;
}

h1{
  margin-top: -10px !important;
}

#toctitle{
  text-align: left;
  text-align: left !important;
}
  
#toctitle > h2{
  color: #000 !important;
  box-shadow: none;
  font-family: Lora !important;
}
  
.mw-editsection *{
  color: #aaa !important;
}

#p-lang{
  font-size: 2em;
}

.interlanguage-link > a{  
  color: #ddd !important;
}

.interlanguage-link > a:hover{  
  color: #000 !important;
}

.interlanguage-link:not(.interwiki-pl):not(.interwiki-en){
  display: none;
}

#right-navigation{
  float: none;
  margin-left: 1.5em;
}

div#simpleSearch{
  background: #fff;
  height: auto;
}

div#simpleSearch #searchInput{
  padding: 0.2em 0.8em;
  font-size: inherit;
}

.infobox, div.thumbinner{
  font-size: 75%;
  background: #fff;
  border: none;
  box-shadow: 0px 0px  5px -0px rgba(0,0,0,0.2);
  margin: 0.5em 0px 0.5em 1.5em;
  border-collapse: collapse;
}

.infobox td, .infobox th{
  padding: 0.3em 0.5em !important;
}

[style*="#c9ffd9"]{
  background: #efefef !important;
  text-align: left !important;
}

div.hatnote {
    padding-left: 0.5em;
    margin: 1.5em 0em 1em;
    background: #f3f3f3;
    border-left: 5px solid #ddd;
  font-style: normal;
}

  div.tright{
    margin: 0px;
  }
}
