css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('hackforums.net') {
/*BG*/
  body {
    background: none;
      background-image:  url('data:image/jpg;base64,') !important;

    color: #0a0;/*nihil*/
  }
/*Header*/
  .menu ul {
    background: rgba(10,10,10,0.7) !important;
    color: #0a0;/*99f*/ /*nihil*/
    border-radius: 5px 5px 0px 0px;
    height: 20px;
    font-weight: bold;
    padding: 0px;
    text-align: center;
    border: 1px solid rgba(120,120,120,0.2) !important; /*-bottom could be removed*/
  }
  #panel { /*affects the entire profile page*/
    background: none repeat scroll 0% 0% rgba(0,0,0,0.7)!important;
    border-radius: 0px 0px 5px 5px;
    color: #999 !important;
    font-size: 13px;
    border: 1px solid rgba(120,120,120,0.2) !important;
    padding: 6px;
  }
  td.thead  a:hover {
    text-shadow: 0px 0px 8px #f00; /*weak*/ /*nihil*/
  }
 
/*Main canvas/bg*/
  .tborder {
    background: rgba(20,20,20,0.8) !important;/*the grid*/
    border: 1px solid rgba(120,120,120,0.4) !important; /*global borders*/    
}
  .thead { /*threadheader*/
    background-color: rgba(20,20,20,0.8) !important; /*fail*/
    color: #a3a3a3 !important; /* ? */
  }
      .thead a:link {
      text-decoration: none;
        color: #0f0;
      }
  .tcat {
    background: linear-gradient(#343434, #121212) !important; /**/
    color: #0a0 !important;
}
.trow1 {
    background-color: rgba(12,12,12,0.65) !important; /*first child*/
    color: #a1a1a1 !important;
}
.trow2 {
    background-color: rgba(0,0,0,0.65) !important; /*second child*/
  color: #a2a2a2 !important; /*yes*/
  }
     /*quotation box*/
    blockquote {
    border: 1px dashed rgb(90, 100, 90);
    color: rgb(200, 220, 200);
    margin: 0px;
    background: none repeat scroll 0% 0% rgba(12, 12, 12, 0.6);
    padding: 4px;
    }
    blockquote cite {
    font-weight: bold;
    border-bottom: 1px dashed #999;
    font-style: normal;
    display: block;
    margin: 4px 0px;
    }
  /*Quote reply(container}*/
  .messageEditor {
    background: none repeat scroll 0% 0% #000;
    border: 1px solid rgba(120,120,120,0.2) !important;
    padding: 3px;
}
   /*drop down menu (font)*/
  .messageEditor .toolbar_dropdown .editor_dropdown_label {
    min-width: 120px;
    background: none repeat scroll 0% 0% #000;
    padding: 3px 4px;
    margin-right: 14px;
    border-width: 1px 0px 1px 1px;
    border-style: solid none solid solid;
    border-color: rgba(120,120,120,0.2) -moz-use-text-color #121212 #232323;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    height: 14px;
}/***/
  .messageEditor .editor_dropdown_menu_title { /*header of dd menu*/
    background: none repeat scroll 0% 0% rgba(120,120,120,0.4) !important;
    padding-left: 3px;
    border-bottom: 1px solid rgba(120,120,120,0.7) !important;
    color: #c7c7c7 !important;
    font-size: 10px;
    font-weight: bold;
}
  .messageEditor .editor_dropdown_menu {
    margin: -3px 0px 0px;
    padding: 0px 0px 1px;
    top: 24px;
    left: 0px;
    border: 1px solid rgba(120,120,120,0.4) !important;
    position: absolute;
    width: 160px;
    background: none repeat scroll 0% 0% #000;
    z-index: 10000;
    max-height: 200px;
    overflow: auto;
}
  .messageEditor .editor_dropdown_menu
  li.editor_dropdown_menu_item_over {
    background-color: #010;
    text-shadow: 1px 1px 8px #0f0;
    color: #0f0;
  }
  /*color palette*/
  .messageEditor .toolbar_button_color .editor_dropdown_menu {
    width: 153px;
    height: 98px;
    padding: 1px;
    background: none repeat scroll 0% 0% #000;
    margin-left: -1px;
  }
  /*hover*/
  .messageEditor .toolbar_button_color
  li.editor_dropdown_menu_item_over {
    border: 1px solid #0f0 !important;
  }
  /*buttons*/
   .bitButton { /*most buttons*/
    color: #a2a2a2 !important;
    background: linear-gradient(#343434,#000) !important;
    border: 1px solid #444 !important;
    box-shadow: 0px 1px 0px 0px #a1a1a1 inset !important;
    padding: 3px 6px;
    border-radius: 3px;
    text-shadow: none;
    text-decoration: none;
    font-family: arial;
    font-size: 14px;
    font-weight: bold;
    }
    a.bitButton > a:hover {
      text-shadow: 1px 1px 8px #0f0;
    }
     
/*footer*/
  .bottommenu {
    background: none repeat scroll 0% 0% rgba(20,20,20,0.8) !important;
    color: #aaa;/*dividers*/ /*nihil*/
    border: 1px solid rgba(120,120,120,0.2) !important;
    width: 610px !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }
  .bottommenu a:link {
    color: #a2a2a2 !important;
    font-weight: bold !important;
  }
  .bottommenu a:visited {
    color: #a0f; /*purple sprite*/
  }
  .bottommenu a:hover {
    color: #0f0;
    text-shadow: 0px 0px 5px #0f0;
  }
  td.tfoot {
    background: linear-gradient(#121212,#343434) !important;
  }
  .tfoot a:link {
    color: #0a0;
    text-decoration: none;
  }
  .tfoot a:visited {
    color: #a0f;
  }
  .tfoot a:hover {
    color: #0f0 !important;
    text-shadow: 0px 0px 5px #0f0;
  }
  /*board statistics*/
   tbody#boardstats_e {
     background-color: #000;
   }
  .smalltext {/*profile info stats in posts*/
    color: #a1a1a1 !important;
  }
  .navigation .active {
    color: #0a0;
    font-size: 14px;
    font-weight: bold;
    display:  none;
  }
  /*.largetext {
    display: none;
  }*/
  span.smalltext.float_right { /*date/time*/
    color: #959595 !important;/*
    background: rgba(20,20,20,0.8) !important;*/
  }
  /*post(message) box*/
    textarea {
    background: none repeat scroll 0% 0% rgb(0, 0, 0) !important;
    color: rgb(180, 180, 180) !important;
    border: 1px solid rgb(0, 0, 0);
    padding: 2px;
    font-family: Verdana,Arial,Sans-Serif;
    line-height: 1.4;
    font-size: 14px;
    }
  /*Forum search*/
    input.textbox {
    background-color: #000;
     border-radius: 12px !important;
    color: #a4a4a4 !important;
    border: 1px solid #333;
     padding-left: 7px !important;
   }
    input.button {
    color: #a2a2a2 !important;
    background: linear-gradient(#343434,#000) !important;
    border: 1px solid #444 !important;
   }
  /*post counter*/
  .tcat a:link {
    color: #cacaca !important;
  }
  /*quote&report tabs*/
  /*topic tabs*/
  .shadetabs li a.selected {
    position: relative;
    top: 1px;
    background: none repeat scroll 0% 0% #000;
    color: #000;
    text-shadow: 0px 0px 5px #0f0;
   }
    .shadetabs li a {
    background-color: #000;
    box-shadow: 1px 1px 1px 1px #a1a1a1 inset !important;
    border-radius: 6px 6px 0px 0px !important;
    color: #070;
    font-family: arial;
    font-size: 15px;
    font-weight: bold;
    padding: 3px 6px;
    text-decoration: none;
    text-shadow: 1px 1px 0px #000;
   }
  .shadetabs li a:hover {
    color: #0f0;
    background-color: #010;
  }
  #panel a:link {
    color: #0c0;
  }
  #panel a:hover {
    color: #0f0;
    text-shadow: 0px 0px 5px #0f0;
  }
  td.trow1 > strong a:link {
    color: #0f0;
  }
  td.trow1 > strong a:hover {
    color: #0f0;
    text-shadow: 0px 0px 9px #0f0;
  }
  td.trow2 > strong a:link {
    color: #0f0;
  }
  td.trow2 > strong a:hover {
    color: #0f0;
    text-shadow: 0px 0px 9px #0f0;
  }
  td.trow1 > span.smalltext > a:link {
    color: #0f0;
  }
  td.trow1 > span.smalltext > a:hover {
    text-shadow: 0px 0px 9px #0f0;
  }  
  td.trow2 > span.smalltext > a:link {
    color:  #0f0;
  }
  ul > li > a:link {
    color: #0c0;
  }
  ul > li > a:hover {
    color: #0f0;
    text-shadow: 0px 0px 5px #0f0;
  }
  /*member list*/
   select {
    background: none repeat scroll 0% 0% #000;
    border: 1px solid rgba(120,120,120,0.2) !important;
     color: #a1a1a1 !important;
  }
  /*pagination*/
  .pagination .pagination_current {
    color: #a2a2a2 !important;
    background: linear-gradient(#343434,#000) !important;
    border: 1px solid #444 !important;
    font-weight: bold;
    box-shadow: 0px 0px 4px #fff !important;
  }
  .pagination a {
    color: #a2a2a2 !important;
    background: linear-gradient(#343434,#000) !important;
    border: 1px solid #444 !important;
  }
  .pagination a:hover {
    background-color: #000;
    text-shadow: 0px 0px 8px #0f0;
    color: #0f0;
  }
  /*buttons*/
  .button {
    color: #0c0;
    background-color: #000 !important;
    border: 1px solid #0a0 !important;
    box-shadow: 0px 0px 0px 0px #000 inset !important;
    border-radius: 3px;
    padding: 3px 6px;
    text-shadow: 1px 1px 0px #000;
    text-decoration: none;
    font-family: arial;
    font-size: 14px;
    font-weight: bold;
  }
 /*menu header*/ 
   a.navButton {
    color: #acacac !important;
    text-decoration: none;
   }
  .menu ul a:hover { /*header menu*/
    color: #d2d2d2 !important;
    text-shadow: 0px 0px 8px #fff !important;
    background-color: rgba(120,120,120,0.1) !important;
    border-top: 1px solid #a1a1a1 !important;
  }
  /*username and CP*/ /*under construction*/
  span.pages {
    color: #a1a1a1 !important;
  }
  /*user stars*/
  span.userstars {
    background: none;
    background-color: #000 !important;
    display: block;
    width: 122px;
    height: 14px;
  }
  /*Nav */
  div.navigation {
    color: #a2a2a2 !important;
    background: rgba(20,20,20,0.8) !important; 
    border-radius: 6px !important;
  }
  .navigation a:link {
    color: #999 !important;
  }
  .navigation a:hover {
    color: #0f0;
    text-shadow: 1px 1px 8px #0f0;
  }
  #content {
    color: #0a0;
  }
  /*welcome*/
  .largetext {
    color: #9a9a9a !important;
    background: rgba(20,20,20,0.7) !important; /**/
  }
  td.trow2 > div.messageEditor {
    width: 850px !important;
  }
  td.trow2 > div.messageEditor > div > textarea#message_new {
    width: 845px !important;
  }
  td.trow1 > div > textarea#message {
    width: 830px !important;
  }
  /*fixed invis members*/
  .group7 {
    color: #040;/*yes*/
    text-decoration: none;
  }
  /*custom Logo*/  
  div.logo > a > img {
    display: inline-block;
    width: 0px;
    height: 0px;
    padding: 40px 268px;
    background: url(http://s1.bild.me/bilder/120914/6543692logo_pl2.gif) !important; /*yes*/
  }
  /*replaced section images*/
  tbody#cat_1_e > tr > td.trow1 > img { /*rules*/
    display: inline-block;
    width: 0;
    height: 0;
    padding: 20px;
    background: url(http://s1.bild.me/bilder/120914/5120984rules-news-announcements1.png) !important;
  }
  /*CP sidebar*/
  .usercp_nav_profile {
    color: #0f0 !important;
  }
  .usercp_nav_item >a:link {
    color: #0f0;
  }
  a:link {
    color: #cccccc !important; /**/
  }
  a:hover {
    color: #fff !important;
    text-shadow: 1px 1px 8px #0f0 !important;
  }
  a:visited {
    color: #777 !important;
  }
  tbody{
    background: transparent !important;
  }
  #content {
    background: transparent !important;
  }
  td.trow1.post_buttons{
    background: linear-gradient(#121212, #343434) !important; /**/
  }
  
  /*legend*/
  .float_left {
    background: rgba(20,20,20,0.6) !important;
    border-top-right-radius: 7px !important;
    border-bottom-right-radius: 7px !important;
    padding-right: 5px !important;
  }

  strong{
    background: rgba(20,20,20,0.5) !important;
    color: #a1a1a1 !important;
  }
  
}
