css:
@-moz-document domain(play.pokemonshowdown.com){ .chat-log-add {
border-top:1px solid #151515;
background:#151515;
}

.userlist {
background:#080808 url(http://107.161.19.92:8000/CustomCss/Halloween/Userlist.png) no-repeat;
border-right:1px solid #101010;
}

.ps-room{ border-left: 1px solid #545454; }

.userlist li {
border:none;
color:#6bf8c5;
}

.pm-log-add.focused {
background-color:#00e1ff;
border-color:#00e1ff;
}

.message-announce {
background:#C4501A;
border-color:#40FF00;
}

.broadcast-blue {
background-color:#D27422;
padding:2px 4px;
}

.pm-window.focused h3:hover,.pm-window.focused h3 {
background-color:#D0721F;
border-color:#000;
}

#header {
background:url(http://107.161.19.92:8000/CustomCss/Halloween/TopHalloween.jpg);
}

body {
background-image:url(http://107.161.19.92:8000/CustomCss/Halloween/Halloween.jpg);
background-repeat:no-repeat;
background-position:center;
}

 .menugroup .button {
 background:#000;
 }
 
 .menugroup .button:hover {
 background:#FF4000;
 }

.chat-log,.ps-room.ps-room-light {
background:#151515;
color:#77D6B8;
}

.pm-window h3,.pm-log-add {
background-color:#151515;
border-color:#151515;
}

.highlighted {
background-color:#58009C;
}




.utilichart .pokemonnamecol {
padding-top: 5px;
height: 23px;
width: 115px;
color: #77d6b8;
font-size: 10pt;
}
 
.utilichart .typecol img {
opacity: 0.9;
margin-right: 1px;
}
 
.utilichart .col {
float: left;
padding-top: 6px;
height: 22px;
font-size: 9pt;
color: #77d6b8;
text-shadow: 1px 1px black;
}
 
.utilichart .twoabilitycol {
padding-top: 0;
height: 28px;
font-size: 8pt;
width: 90px;
}
 
.utilichart .abilitycol {
padding-top: 7px;
height: 21px;
font-size: 8pt;
width: 90px;
}
.utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em {
color: #77d6b8;
font-size: 7pt;
font-style: normal;
}
}
