css:
@-moz-document url("https://www.tumblr.com/dashboard") {
/* GETS RID OF THAT UGLY RADAR BACKGROUND IN RIGHT COLUMN */

div#right_column, div#right_column:after {background:none!important;}
#right_column {top:8px!important; background-color:transparent!important;}

/* CHANGES FONT FAMILY AND BACKGROUND GRADIANT */

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #main_doc_page, #dashboard_settings, #dashboard_create_tumblelog, #dashboard_members, #dashboard_tumblelog_settings, #dashboard_messages_inbox, #notifications_index, #tagged_display, #dashboard_useraction_followers, #dashboard_posts_likes, #dashboard_useraction_following, #dashboard_lookup_lookup_contacts, #dashboard_promoted_directory, #dashboard_group_members, #main_help, #dashboard_messages_submissions, #dashboard_posts_editing, #dashboard_upload_static_file, #dashboard_account_create_tumblelog, #tagged_actions_display, .plexi, #settings_actions_tumblelog, #settings_actions_account, #settings_actions_dashboard, #settings_actions_apps, #settings_actions_notifications, #activity_actions_index {background:linear-gradient(#ee897e, #a873bc, #d278b0); background-attachment:fixed!important;}

/* REMOVES BACKGROUND ON LEFT COLUMN */

#left_column {background:transparent!important;}

#settings_actions_tumblelog #left_column, #settings_actions_account #left_column, #settings_actions_dashboard #left_column, #settings_actions_apps #left_column, #settings_actions_notifications #left_column, #dashboard_upload_static_file #left_column {background-color:#fff!important;}

/* HEADER STUFF */

	.l-header-container {background:rgba(210,120,176,1)!important; background-image:none!important; padding:5px 0px; max-width:3980px!important;}
 	.l-content {background:transparent!important; border-radius:0px !important; text-shadow:none !important;}

#content_top{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}
#content_bottom{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}
.content_cap{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}

.compose-button {border-color:#ee897e!important; background-color:#ee897e!important; color:#fff!important;}

/* THE LITTLE NUMBER BUBBLE NEXT TO THE DASHBOARD AND MESSAGE ICONS */

	.tab_notice {top:-8px!important; background-image:none!important; margin-left:2px !important; text-align:left!important; border-radius: 15px 15px 15px 0px!important; vertical-align:middle !important; background-color:#a0a0a0!important; border:4px solid #a0a0a0!important;height:23px!important;}
	*.tab_notice_value {text-shadow:none!important; color:#fff!important;}
 	#header #inbox_button .tab_notice {margin-left:1px!important;}

#right_column .tab_notice {top:4px!important; right:7px!important;}

/* RIGHT COLUMN (SIDEBAR) STUFF */

	.popover_menu_item * {}
	.controls_section {margin-top:0px!important; padding:10px!important;}
	.controls_section, .controls_section .selected, #right_column a#dashboard_switch_blog_menu_current {background-color:#cc90cd!important;}
	.controls_section *, .controls_section .selected * {background-color:#cc90cd!important; color:#fff!important; box-shadow: 0px 10px 18px -12px #cc90cd !important;}
	#dashboard_controls_open_blog * {background-color:#cc90cd!important; color:#fff!important; box-shadow: 0px 0px 0px 0px #000 !important;}
	#popover_blogs {border:0px!important; margin-left: 0px!important; width:219px!important; position:absolute!important; margin-top:4px!important; border:5px #fff solid!important; border-radius:0px!important; border-bottom:0px!important;}
	#popover_blogs * {border:0px!important; background-color:#fff!important; box-shadow: 0px 0px 0px 0px #000 !important; color:#b997d1!important; border-radius:0px!important;}
	#popover_blogs .blog_url * {color:#333!important;}

.blog_icon_image {width:40px!important; height:40px!important; opacity:1!important; display:inline!important;}
#open_blog_link {color:#fff!important; opacity:0.8!important;}

.controls_section, .controls_section a {box-shadow:none!important; border: one!important; font-weight:normal!important;}

#dashboard_controls_open_blog {box-shadow:none!important; border:none!important;}

.small_links {padding-bottom:0px!important; opacity:0.7!important;}
.small_links * {color:#fff!important;}

.controls_section_account {margin:10px 0px -20px 0px!important;}
 
/* GETS RID OF ALL THAT JUNK ON YOUR SIDEBAR */

#recommended_tumblelogs, .add_and_remove, #tumblr_radar {display:none!important; opacity:0!important; background:none!important;}
.spotlight {display:none!important; background-image:none!important;}
 
/* SQUARES OFF THE CORNERS OF POSTS (NO ROUNDED CORNERS) */
 
.post img, .reply_text, .text_field, #tag_editor, .source_url_questionmark_gradient, #post_one {border-radius:0px!important; box-shadow:none!important;}
   
/* CHANGES COLOR OF SOURCE URL IN TOP-RIGHT OF POSTS */
 
#content_top, #content_bottom, .inline_external_image, .source_url {border-radius: 0px !important; box-shadow: none !important; background-color:#ffb0b0;padding-top:0px!important;}

/* EDITS POST INFO (URLS AT THE TOP OF POSTS, ETC.) */
 
.post_info a, .post_info, .post_controls a, .footer_links.with_tags span a {font-weight:400!important; letter-spacing:1px;}
 
.post_source_name_prefix, .post_source a {color:#a0a0a0 !important; letter-spacing:1px;}

.post_tags {margin-top:10px!important; margin-bottom:0px!important;}
.post_tags_inner a, .post_tag, .post_tag a {color:#AAA!important;}
 
.post_notes_label {color:#999!important; font-weight:normal!important;}
 
.notification a {border-bottom: 0px solid #3366FF !important;}
.notification blockquote {border-left: 3px solid #3366FF !important;}
 
/* NOTIFICATION STUFF??? SURE I GUESS THAT'S WHAT IT IS */
 
.notification, .answer_container, .answer_input, .dashboard_options_form,
.flat{border: 0px solid #FF00FF !important;}

ol#posts li.notification {line-height:21px!important;}
ol#posts li.notification * {font-weight:normal!important; color:#fff!important;}
ol#posts li.notification * a {color:#fff!important; opacity:1!important;}
 
ol#posts li.notification.alt {background-color:rgba(204,144,205, 1)!important;}
 
ol#posts li.notification {background-color:rgba(204,144,205, .3)!important;}
 
/* POST BODY STUFF??? SURE I GUESS THAT'S WHAT IT IS */
 
.post_full .post_tags {white-space: normal !important;}
.post_full .post_tags .post_tag {display: inline !important;}
.post_full .post_tags:after {width: 0px !important;}
 
.post p, .post blockquote, .post_info, .caption, .conversation_lines, .quote_source, #left_column #posts .post .permalink{background-color:transparent!important;}
 
#content_bottom {border-radius:0px !important;}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {background:none!important; width:0px!important; height:0px!important; margin-top:0px!important; border-bottom:solid 4px #000!important; border-right:11px solid transparent!important; border-left:11px solid transparent!important;}
 
.selection_nipple:not([class="selection_nipple white"]) {border-bottom:solid 0px #000!important;}
 
.arrow {background:none!important; width:0px!important; height:0px!important; border-right:solid 9px #000 !important; border-bottom:9px solid transparent!important; border-left:2px solid transparent!important; border-top:9px solid transparent!important; margin-top:0px!important;}
.nipple {background:none!important; width:0px!important; height:0px!important; border-right:solid 6px #000!important; border-bottom:6px solid transparent!important; border-left:0px solid transparent!important; border-top:6px solid transparent!important; margin-top: 0px!important;}
.nipple.border {background:none!important; width:0px!important; height:0px!important; border-right:solid 6px #000!important; border-bottom:6px solid transparent!important; border-left:1px solid transparent!important; border-top:6px solid transparent!important; margin-top:1px!important;}
 
#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current{background-image:#C0C0C0!important}

.post_avatar {-webkit-border-radius: 50px!important; border-radius: 50px!important;}
 
.post {-webkit-border-radius: 0px!important; border-radius: 0px!important;}
.permalink {opacity:0.5!important; -webkit-filter: grayscale(1);}
 
#left_column #posts .post {border-bottom:0px #000 solid !important; box-shadow: 0px 0px 0px #fff;}
#left_column #posts .post {margin-bottom:20px!important;}

/* I'M NOT ENTIRELY SURE WHAT THE REST OF THIS STUFF DOES TBH */

div.post_content blockquote {margin-left:0 !important; padding-left:10px !important;}
 
ol#posts li.post.new_post {background:#ffffff top center no-repeat!important; box-shadow: none !important; border: 0px solid #E7EAEC !important; border-radius:0px !important; padding:0px!important; margin:0px!important;}
 
.posts a, .followers a, .drafts a, .queue a {border-bottom:0px #fff solid!important;}
 
.post_permalink {opacity:0!important;}

#related_tags a {background-color:transparent!important; color:#A4DED5!important;}

li.selected {box-shadow:none!important; border-bottom:0px solid #fff!important; border-radius:0px!important;}
 
#nav .nav_item.active {background-color:#A4DED5}

.count {text-shadow:none!important;}
 
#post_two {border-radius:0px 0px 8px 8px !important;box-shadow: none !important;}

.photo {box-shadow:none!important; border:1px solid #A4DED5!important; border-radius:0px!important;}
 
.avatar_frame {border:0px solid #fff!important;}

.token {border: 0px solid #444 !important;background-color:none!important;}
 
img[src*="http://assets.tumblr.com/images/content_bottom_edit_form.png"],
img[src*="http://assets.tumblr.com/images/content_top_edit_form.png"]{height: 0px !important;background-color:#FFF!important;
box-shadow: none !important;
border: 0px solid #E7EAEC !important;
border-radius:0px !important;
padding:0px!important; margin:0px!important;}
 
#posts LI[class*=" post"] {box-shadow: none !important; border: 1px solid #E7EAEC !important;border-radius:0px !important;}
 
#posts LI[class*=" post"]:hover {box-shadow: none !important; border: 1px solid #BCBCBC !important;border-radius:0px !important;}
 
.camera {background: transparent !important;
border-radius: 0px !important;
opacity: 0.7 !important;
padding-bottom: 4px;}
 
.image { display: block !important;
margin-left: auto !important;
margin-right: auto !important;}

#unread_tag_notice {display:none!important; opacity:0!important;}

.activity .count {opacity:1!important;}
 
.open_popover {width:568px!important; margin-bottom:15px!important;}
 
#following, #lookup, #staff_picks {background-color:#fff!important; margin-top:-30px!important; margin-left:0px!important; margin-right:0px!important; padding:30px!important; border:3px #eee solid; border-top:none; box-shadow: inset 0 0 30px #ddd; border-radius: 0px 0px 15px 15px !important;}
#staff_picks {padding:29px 0px!important;}

.note_item {background-color:#f3f3f3!important;}
 
#dashboard_upload_static_file #content {background-color:#fff!important; padding:0px 20px 20px 20px; border-radius:0px 0px 20px 20px!important; margin:10px 20px 0px 20px!important;}
#dashboard_tumblelog_settings #content {background-color:#fff!important; margin:10px 0px!important;}
 
.post_footer, .post_notes, .post_notes_label {background-color:#fff!important;}
 
.search_query_btn, .recommended_tumblelogs, .radar_header {display:none!important;}
 
.dashboard_options_form {background-color:#2C4762!important;}
 
#main_help #container {background-color:#eee!important; padding:20px 0px 0px 0px!important;}
 
data-tumblelog-name {display:none!important; opacity:0!important;}

.account_header {display:none!important;}

#user_tools {margin-top:0px!important;}

img {background-color:#fff!important;}

.post_avatar_link {background-color:#b9e9e2!important;}

.new_post_label i {color:#cc90cd!important; padding-top:2px!important; margin-left:0px!important;}

#new_post_label_text > I, #new_post_label_quote > I, #new_post_label_chat > I, #new_post_label_video > I {color:#c69ed5!important;}
}
