css:
@-moz-document url-prefix("http://www.animetake.com/") {
/**
 *  Author: Seikyo
 *  Date: 23/12/13
 *  Description: Simplified and large version of AnimeTake
 **/

/*************
* Hidden DIV *
*************/
#mainContent #about,
#header, 
#rightsidebar.hp_sidebar h2:first-child,
#rightsidebar.hp_sidebar h2:nth-of-type(2),
#categories > h3.navheader,
#wrapper .textwidget, #wrapper .catg-advet,
#categories iframe, #cont_btm,
#ddmcc_container > p:first-child,
#wrapper > div:nth-child(1)
{
  display: none;
}
/*********
* GLOBAL *
*********/

#categories {
  clear: both;
}

#mainContent{
  margin: 0;
  padding: 0;
}

#outer{ 
  background: #d8d6d3 url('http://files.gamebanana.com/img/ico/sprays/yoko_cs.png') no-repeat 23% -35px;
  height: auto;
}
#wrapper{
  margin: 0 auto;
  margin-top: -5px;
  padding: 0;
  border: 0;
  width: 1200px;
}

#wrapper h3.navheader, #wrapper h2{
  background: #7F2123 !important;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  color: white !important;
  text-align: center !important;
}
/*********
* TOPBAR *
*********/
#topbar-outer{
  margin: 0;
  padding-top: 120px;
  border-bottom: 0px solid #6f0104;
}

#topbar{
  width: 1200px;
  margin: 0 auto;
  padding: 0;
  border: 0;
  border-radius: 4px;
  background: #6f0104;
  opacity: 0.85;
}

#topbar ul{
  margin-left: 40px;
}

#topbar li{
  margin: 0;
}

#topbar li a:hover{
  background: #d12926;
}
/***************
* Right Column *
***************/
#rightsidebar{
  float: right;
  margin-top: 40px;
  width: 300px; 
}

#wp-calendar{
  margin: 0;
  border-collapse: collapse;
  background: #EDEBE8;
  margin-bottom: 5px;
}
#wp-calendar caption{
  background: #7F2123;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  border: 0;
  font: bold 13px/17px tahoma;
  padding: 3px 0 2px 0;
}
#wp-calendar thead th
{
  background: #AC4357;
  height: 20px;
}
#wp-calendar tfoot td { background: #AC4357 }
#wp-calendar tfoot a { color: white; }
#wp-calendar tbody td,
#wp-calendar tbody th
{
  margin: 0;
  border: 1px solid #d8d6d3;
  height: 20px;
  background: none;
}
#wp-calendar tr:last-child td:first-child{
  border-bottom-left-radius: 4px;
}
#wp-calendar tr:last-child td:last-child{
  border-bottom-right-radius: 4px;
}
#wp-calendar tbody tr:first-child td{
  border-top: 0;
}
#wp-calendar tbody tr:last-child td{
  border-bottom: 0;
}

#rightsidebar ul {
  background: #EDEBE8;
  border-bottom-left-radius: 4px;
  border-bottom-right-radius: 4px;
  padding-bottom: 2px;
}

#rightsidebar ul li{
  background: none;
  min-height: 18px;
  padding: 2px 5px;
  margin: 0;
  border-bottom: 1px solid #d8d6d3;
}
#rightsidebar ul li:last-child{
  border-bottom: 0;
}

#rightsidebar div.affiliates{
  background: #EDEBE8;
  border-bottom-left-radius: 4px;
  border-bottom-right-radius: 4px;
}
  
#rightsidebar div.affiliates a{
  background: none;
}
/**************
* Left Column *
**************/
#categories{
  width: 200px;
  margin-top: 40px;
}

#categories div.login{
  background: #EDEBE8;
  border-radius: 4px;
  margin-bottom: 0;
}

#categories ul.ddrc{
  margin: 0;
  padding: 0;
  background: #EDEBE8;
  padding: 4px 0;
  border-bottom-left-radius: 4px;
  border-bottom-right-radius: 4px;
}

#categories ul.ddrc li{
  padding: 2px 5px;
  margin: 0;
  color: black;
  background: #EDEBE8;
}


/****************
* Center Column *
****************/
#mainContent{
  float: left !important;
  background: none !important;
  width: 700px !important;
  box-sizing: border-box !important;
  padding: 0 15px 0 15px !important; /* Left margin */
}

#updates {
  background: none !important;
}

#mainContent h3, #mainContent h1{
  margin-top: 5px;
  background: none;
  padding-left: 70px;
  font-size: 1.4em;
  color: #AC4357;
  text-shadow: 2px 2px 3px #bbb;
}

#updates ul{
  width: 100% !important;
}

#updates li{
  width: 100%;
}

#updates .listupdate{
  float: right;
  width: 100%;
  padding: 0;
  margin: 0;
}

#updates .listupdate img{
  position: absolute;
  border: 0;
  border-top-left-radius: 4px;
  border-bottom-left-radius: 4px;
  height: 55px;
  width: 55px;
}

#updates h4{
  margin: 0;
  padding: 0;
}

#updates h4 a{
  font-size: 1em;
  margin: 0;
  padding: 0;
}

#updates .updateinfo{
  box-sizing: border-box;
  border-radius: 4px;
  width: 100%;
  padding: 0;
  margin: 0;
  padding: 3px 0 3px 65px;
  margin-bottom: 5px;
  background: #EDEBE8 !important;
  min-height: 55px;
  height: auto;
  box-shadow: 1px 2px 2px #bbb;
}

#updates p.center{
  clear: both;
  text-align: right;
  font-size: 1.1em;
  font-weight: bold;
}

#updates div.post{
  background: none;
}

#updates div.post table{
  width: 100%;
}

#updates div.post a{
  font-size: 1.1em;
  padding: 3px 15px ;
}

#updates div.post li{
  height:18px;
}

/** Anime Download */
#updates > ul{
  margin: 0;
  padding: 0;
  width: 100%;
  background: #EDEBE8;
  border-radius: 4px;
  box-shadow: 1px 2px 2px #bbb;
}
#updates ul li{
  background: none;
}

#mainContent.page {
  float: left;
  box-sizing: border-box;
  width: 700px;
}

#mainContent.page .upaddc {
  margin-top: 40px;
}

#mainContent.page .ddmcc{
  padding: 0;
  margin: 0;
  width: 50%;
  margin-top: 10px;
  padding: 5px 15px;
  box-sizing: border-box;
}
#mainContent.page .ddmcc ul,
#mainContent.page .ddmcc li,
#mainContent.page .ddmcc p.sep
{
  width: 100%;
}
/** Anime Detail */
#content{
  margin-top: 40px;
  background: #EDEBE8 !important;
  width: 100% !important;
  box-sizing: border-box !important;
  padding: 10px 15px 10px 15px !important;
  border-radius: 4px;
}
#content div
{
  width: 100% !important;
  margin: 0 !important;
  padding: 0 !important;
  border: none !important;
  box-sizing: border-box !important;
  background: none;
  color: black !important;
}

#content p#path{
  position: absolute;
  margin-top: -45px;
  margin-left: 35px;
  font-size: 1.1em;
  color: black;
}

#content > div[class^=cat] span{
  color: black;
}

#content > div[class^=cat] a{
  padding: 2px 3px;
}

#content div.catg_desc:nth-of-type(1){
  background: #d8d6d3 !important;
  border-radius: 4px !important;
  margin-bottom: 5px !important;
  padding: 3px 10px !important;
}

#content #newe p:first-child{
  margin: 0;
  padding: 0;
}

#content ul.catg_list{
  padding: 0;
  margin: 0;
}

#content ul.catg_list li{
  background: #d8d6d3;
  padding: 1px 0 1px 30px;
}

#content ul.catg_list li.main{
  background: none;
  background: #AC4356;
  padding: 1px 10px;
}

#content #down ul{
  margin-bottom: 10px;
  
}

#content .post-info{
  background: #d8d6d3 !important;
  border-radius: 4px !important;
  margin-bottom: 5px !important;
  padding: 0 10px 0 0 !important;
  min-height: 55px;
}

#content .post-info div{
  width: auto !important;
}

#content .post-info-thumb img{
  float: left;
  border-top-left-radius: 4px;
  border-bottom-left-radius: 4px;
  width: 55px;
  height: 55px;
  margin-right: 10px;
}
#content .post-info .post-info-info{
  width: 500px !important;
}

#content .post-info-info h1{
  padding-left: 0;
  text-shadow: none;
}

#content #down {
  box-sizing: content-box !important;
  margin-bottom: 20px !important;
}

#content #down p{
  text-align: justify !important;
  font-size: 1.1em;
  clear: both;
}

#content #down h2{
  clear: both;
}

#content #description{
  padding: 3px 15px !important;
  margin-top: 5px !important;
}

#content #down p strong{
  display: inline-block;
  margin: 5px;
}
#content #down p img{
  float: left;
  margin-right: 15px;
  margin-bottom: 15px;
  border-radius: 4px;
  box-shadow: 1px 2px 2px #bbb;
}

#content .cat_list_title{
  background: #6f0104;
  margin: 15px 0 10px 0;
  padding: 2px 15px;
  box-sizing: content-box;
  border-radius: 4px;
  text-shadow: none;
  color: white;
}

#content div.coment_box {
  box-sizing: content-box !important;
  background: #d8d6d3;
  margin-bottom: 5px !important;
  border-radius: 4px;
}

#content div.coment_cont{
  box-sizing: border-box !important;
  padding: 2px 10px !important;
}
/*************
* Footer DIV *
*************/
#footer {
  width: 100%;
  background: #7F2123 !important;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  margin-top: 10px;
  box-sizing: border-box;
  text-align: center;
}
}
