css:
.header-part1,.header-part2,.header-part3,.headerValue,#mailContent,.tabpanelcontainer, .inline-toolbar,.headerName, #threadTree tree, #tabmail #threadCols,#tabmail #threadPaneBox,#tabmail .tree-stack,#tabmail .tree-rows,#tabmail .tree-bodybox *,#tabmail treechildren{
   background:#111 !important;
   color:#CCC !important;
}
#tabpanelcontainer, #messagepanebox,.main-header-area,#expandedHeaderView{
background:#111 !important;
   color:#CCC;
}
body, body body{
background:#333 !important;
color:#DDD;
}

#the-canvas{
   background:#DDD;
   box-shadow:#000 0px 2px 5px;
}

#conversationHeader, 
#collapsedfromBox,
#expandedHeaderView,
#collapsedHeaderView,
#expandedfromBox,
#msgHeaderViewDeck,
#compactHeader{
background:#000;
color:#EEE;
border:none;
}
#conversationHeader button{
background:#000;
color:#EEE;
border:none;
box-shadow:none;
}


.message{
background:#333;
color:#EEE;
box-shadow:none;
border:none;
border-radius:0;
}

.messageHeader{
background:#000;
}
.messageHeader a{
color:#EEE;
}
.messageHeader li{
background:#000 !important;
}

.attachments{
color:#999;
}
.attachmentThumb{
background:#333;
border:none;
}

.message .messageFooter{
background:#333;
border-top:none;
}
.message .messageFooter button{
background:#333;
color:#999;
border:none;
box-shadow:none;
}

hr{
border:none;
border-top:#999 1px solid;
background:#333;
}

a:link, a:visited{
color:#6AF;
}
a:hover{
color:#9CF;
}

span[_moz_quote="true"]{
  color:#99B;
}

/*
* Tooltip 
*/
.tooltip{
background:#333;
border-color:#444;
border-radius:0;
}
.tipFooter{
background:#222;
}
.tipFooter button{
background:#222;
}
