css:
@-moz-document domain("weibo.com") {
.WB_text {
  color: #333;
  font-family: Arial,Helvetica,sans-serif;
  font-size: 14px;
  font-size-adjust: none;
  font-stretch: normal;
  font-style: normal;
  font-variant: normal;
  font-weight: 400;
  line-height: 23px;
  padding-bottom: 4px;
  padding-left: 0px;
  padding-right: 0px;
  padding-top: 4px;
  -moz-font-feature-settings: normal;
  -moz-font-language-override: normal;
}
.PCD_header .cover_wrap {
  height: 5px;
}
.WB_notes {
  margin: 0px;
}
.shadow {
  display: none;
}
.PCD_header {
  display: none;
  height: 150px;
}
.PCD_header .pf_wrap {
  height: 50px;
}        
.webim_list {
  display: none;
}
.WB_main_r {
  display: none;
}
.W_main_c {
  width: 800px;
}
.W_main_r {
  display: none;
}
.WB_feed {
  width: 800px;
}
.hot_feed_top_tit {
  width: 800px;
}
.B_discover .WB_frame_c, .WB_main_c {
  width: 800px;
}
.WB_frame_b {
  display: none;
}
.S_page .WB_frame_c, .S_page .WB_feed, .S_page #Pl_Official_TimeBase__21 {
  width: 920px;
}
#Pl_Official_ProfileFeedNav__25 {
  width: 800px;
}
.WB_tab_a {
  background-color:#FFFFFF;
}
div[feedtype=ad] {
  display: none;
}
div[node-type="feed_spread"] {
  display: none;
}
.M_sc_tips {
  display: none;
}
.M_sc_foot {
  display: none;
}
.PCD_mplayer{
  display: none;
}
.PCD_W_wrap_d{
  display: none;
}  
.PCD_W_wrap_e{
  float: left;
  width: 960px;
} 
.W_fold {
  display: none;
}
div[node-type="onlineBarFriend"] {
  display: none;
}
.gn_nav_list li:nth-of-type(2), .gn_nav_list li:nth-of-type(3) {
  display: none;
}
.title_area .key {
  display: none;
}
.WB_footer {
  display: none;
}
.WB_miniblog {
  background: none;
}
.PCD_tab .tb_tab td:nth-of-type(3) {
  display: none;
}
#v6_pl_content_hometip{
  display: none;	
}
@media screen and (max-width: 1009px) {
.S_page .WB_frame_c, .S_page .WB_feed, .S_page #Pl_Official_TimeBase__21, .WB_feed {
  width: 600px;
}
.B_discover .WB_frame_c, .WB_main_c, .WB_frame_a {
  width: 600px;
}
.WB_timeline {
margin-left: 305px;
}
}
}
