css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {
#mainBar, #feedlyPage {width:100% !important;}
  .u5Entry {background:#232323 url('data:image/jpeg;base64,') 0 0 no-repeat;height:169px !important;}
.u5Entry.visual {height: 169px !important;}
.u5Entry > div > div:nth-child(2) {background: none repeat scroll 0 0 rgba(0, 0, 0, 0.7);margin-top: -42px !important;padding: 5px 0 !important;box-shadow:0px 0px 3px rgba(0,0,0,0.5);}
.u5Entry > div > div:nth-child(3) {display: block;padding:0 !important;margin-top:-2px;}
.u5Entry .summary {display:none;}
.u5Entry .info {height:32px !important;}
.u5Entry .metadata {overflow:visible !important;font-size:0 !important;color:transparent !important;}
.u5Entry .wikiBar {display:none;margin:-160px 0 0 0 !important;float:right;padding:5px 7px 5px 5px !important;background: none repeat scroll 0 0 rgba(0, 0, 0, 0.7);box-shadow:0px 0px 3px rgba(0,0,0,0.5);border:0 !important;}
.u5Entry:hover .wikiBar {display:block;}
.u5Entry .wikiBar span:nth-of-type(1) {width:20px;height:16px;float:left;margin:0 7px 0 0;display:block;font-size:0 !important;color:transparent !important;background:url('data:image/png;base64,') 50% 50% no-repeat;z-index:1000 !important;}
.u5Entry .wikiBar span:nth-of-type(2) {display:none !important;}
.u5Entry .wikiBar span:nth-of-type(3) {width:10px;height:16px;float:left;margin:0;display:block;font-size:0 !important;color:transparent !important;background:url('data:image/png;base64,') 50% 50% no-repeat;z-index:1000 !important;}
.u5Entry .metadata .meta {display:none !important;}
.u5Entry .title {display: inline-block !important;overflow: hidden;white-space: wrap;width: 252px;font-weight:normal !important;padding:0 5px 0 5px !important;}
.u5Entry .title.read {color: #aaaaaa !important;text-shadow:1px 1px 2px rgba(0,0,0,0.5);}
.u5Entry .title.unread {color: #ffffff !important;text-shadow:1px 1px 2px rgba(0,0,0,0.5);}
.u5Entry .u5EntryAnnotationHolder, .u5Entry .u5EntryAnnotationHolder > div {display:block !important;z-index:50 !important;opacity:1 !important;}
.u5Entry.textOnly .info {height:32px !important;}
.u5Entry.textOnly .title {height:42px !important;margin:0 0 16px 0 !important;padding:0 5px 0 5px !important;line-height:16px !important;}
}
