css:
@-moz-document domain("lady.mail.ru") {
#All.bgbody_1 {background-image:none!important; background:white!important;}
.article__item_share, div[class^="pc-bracing"], .pc-bracing, div[data-module="Shares.Compact"], 
.sticky-springs, .p-top-banners, .rb_banner, .block_bg_secondary, .daynews__banner, .nat_lady, .cols__column_sidebar 
{display:none!important;}
}

@-moz-document domain("games.mail.ru") {
body {background-image: none!important; background: white!important;}
.mid {background: none!important;}
.b-main__wrapper > .b-container, .b-pc__wrapper .b-container {margin-top: 0!important;}
.b-pc__wrapper_news {cursor: auto!important;}
.b-header__menu__holder > .b-header__menu  {position: relative!important;}

#simplemodal-container, #simplemodal-overlay, body > div.main-col > div.content > div.mid > div.col-rght, .b-header__stripe, 
.b-likes__wrapper, .news-body-endless-full > .sep, .b-content__banner, .b-bg
{display:none!important; opacity: 0!important; pointer-events:none!important;}
}

@-moz-document domain("hi-tech.mail.ru") {
div.pm-menu__center__inner.pm-menu__center__inner_submenu {margin-left:10%!important;}
body > div.layout > div > div.block.js-springs__group > div.wrapper > div.cols, 
body > div.layout > div > div.sticky.js-module > div > div > div.wrapper > div.cols, 
body > div.layout > div > div.block > div.wrapper > div.cols {margin-left:16%!important;}
div[class="pc-branding js-branding-wrapper"], .cols__column_small_14, 
div.article__item.article__item_share, .block_separated_top, .footer, div[class^="cols cols_margin cols_percent"], 
.article__item {padding-left: 0px!important;}
.sticky__inner_top {position: relative!important;}

body > div.line, .footer-banner, div[class*="rb_"], .js-module.article__text > .article__item_share, .pm-toolbar__banner,
div[data-module="TrackBlocks"] + div[class="block"], .pc-branding, .footer__portal, .footer__project, div[id^="rb_vid_"],
.cols__inner > div[data-module="TrackBlocks"], .cols__inner > .newsitem.margin_bottom_20, .cols__inner > .list_type_circle, 
.cols__inner > .margin_bottom_30.margin_top_20.box_collapse.box, .block_separated_top, .sharelist.margin_top_20.js-module, 
div[class="sticky__inner"] > div[style^="height: 54px;"], .cols__column_sidebar
{display: none!important; opacity: 0!important; pointer-events:none!important;}
}

@-moz-document domain("news.mail.ru") {
/*мусор на главной*/
.daynews__spring, .block_separated_top:first-of-type  {display: none!important;}
/*правая колонка*/
.cols__column_sidebar {display: none!important;}
/*статичное верхнее меню*/
.portal-menu__submenu {transform: none!important; position: relative!important}
/*центровка верхнего меню*/
.portal-menu_width {width: 780px!important;}
/*центровка статей*/
.block_separated_bottom > .wrapper {width: 780px!important;}
.wrapper[data-module="TrackBlocks"] {width: 780px!important;}
div[data-module^="FixedPanelView.Rubric"] > .block > .wrapper {width: 780px!important;}
.wrapper > .cols > div[class^="cols__column"] {width: 780px!important; margin-left: 0px!important;}
div[data-view="GalleryPreviews.Article.Gallery,GalleryShot"] {width: 780px!important;}
div[data-module="FixedPanelView.Rubric,MediaEmbeds"] .article__text > .article__item {padding-left: 0!important;}
div[data-module="StickyPanel.Card.AnchorMenu"] {height: auto!important; position: relative!important;}
/*мусор под статьёй*/
.cols__inner > div[class^="rb_"], .js-module > div[class^="rb_"],
div[class*="p-directhack"], div[data-view="RelapView"] {display: none!important;}
/*социалка*/
.article__item_share, .cols__inner > .sharelist_justify, button[data-module="Subscription"] {display: none!important;}
/*подвал*/
.footer, body > .sticky, body > .line, .block.js-module[data-module="TrackBlocks"] > .wrapper, 
.layout > .grid + .block > .wrapper > .cols{display: none!important;}
}

@-moz-document url("https://news.mail.ru/") {
.b-index-spring {display: none!important;}
}

@-moz-document url-prefix("https://news.mail.ru/card") {
#totop-linker > div.p-main.js-main > div > div > div > div.p-body.c-clearin.js-fxd_cntnt__tbl.js-fxd_rght__prnt.js-ovrflw_fix.p-body_sub.p-body_subwhite.p-body_leftshort {left: 3.5%!important;}
}

@-moz-document domain("sport.mail.ru") {
.layout-scroll__inner { top: 0 !important; left: 0 !important; position: relative !important; }
div.layout__body__right__inner.springed.clearin > div.springed__left {width: auto!important; margin-left: 14%!important; margin-right: 14%!important;}

div[class="incoming incoming_footer clearin"], div[class="footer__agent-prefix"], div.foot-portal, .layout__body__left-bg-for-small-screen, 
.springed__right, .article__spring-place, .directhack, .sharelist.js-shares_all, .s-partners, .sub-news_tabled
{display: none!important; opacity: 0!important; pointer-events:none!important;}
}

@-moz-document url("https://sport.mail.ru/") {
.index__inner {display: none!important; opacity: 0!important; pointer-events:none!important;}
.index-top__left__inner {padding-right :0!important;}
}

@-moz-document url("https://mail.ru/") {
.layout__centercol__wrap > div[id^="slot_"][class="tgb"], .bgshechka, #news\:games > .news__tabpicture,
.layout__leftcol > #slidotype\:opener, .layout__leftcol > #slidotype\:switcher, a[class="set-homepage-link"] 
{display: none!important;}
}

@-moz-document domain("afisha.mail.ru") {
/*Центровка*/
.screen_small .cols__column_small_32 {margin-left: 5.1%!important;}
.screen_medium .cols__column_medium_43 {margin-left: 14.1%!important;}
.screen_large .cols__column_large_47 {margin-left: 17.2%!important;}
.article__item {padding-left: 0!important;}
/*Ширина блока статей и видео*/
div[class="cols__column cols__column_small_32 cols__column_medium_43 cols__column_large_47"], .article-photo > .article-photo__inner {width: 860px!important;}
div.article__container > iframe[src*="youtube"], div.article__container > iframe[src*="videoapi"] {width: 860px!important; height: 480px!important;}
iframe[class^="instagram-media"] {max-width: inherit!important;}
.fb_iframe_widget, div.fb-post.fb_iframe_widget > span, div.fb-post.fb_iframe_widget > span > iframe {width: 860px!important;}
/*Ширина галерей картинок*/
div.cols__inner > .gallery-grid_large_g-huge {width: auto!important}
.gallery-grid__inner > .gallery-grid__item_title_vertical {width: 338px!important; height: 440px!important;}
.gallery-grid__inner > .gallery-grid__item_square_small {width: 164px!important; height: 140px!important;}
.gallery-grid__inner > .gallery-grid__item_square_small2medium {width: 250px!important; height: 215px!important;}
.gallery-grid__inner > .gallery-grid__item_square_big2medium {width: 250px!important; height: 215px!important;}

.pc-branding, div.cols__column.cols__column_small_14.cols__column_sidebar, .article__item_share, .margin_top_20.nat_afisha.nat, 
div[class^="block block_shadow"], .block_shadow.block_bg_secondary.block > .wrapper:nth-of-type(2), .sharelist.margin_top_20.js-module, 
div[id^="rb-branding"]
{display:none!important; opacity: 0!important; pointer-events:none!important;}
}

@-moz-document domain("mail.ru") {
.logo__homepage, .layout__promotoolbar, .search, .layout__rightcol, .w-text-banner_outer, .informers__column_right, .w-mailbox__userinfo__promo, .social, .w-text-banner_outer-hiddable, .p-directhack, div[data-adq-id^="adqcontainer"], div[data-module^="Slot.Mimic"], .sharelist__items, .article__share, .article__item_share, div[class^="adv-slot-"], a[id^="tb-"][class^="tb-"][target="_blank"] {display:none!important; opacity: 0!important; pointer-events:none!important; }

.logo__link {pointer-events: none!important;}
}

@-moz-document domain("auto.mail.ru") {
/*центровка верхнего меню*/
.screen_small .portal-menu_width {width: 780px!important;}
.screen_medium .portal-menu_width {width: 780px!important;}
.screen_large .portal-menu_width {width: 780px!important;}
/*центровка статей*/
div[data-module="FixedPanelView.Rubric,MediaEmbeds"] > .block > .wrapper {width: 780px!important;}
div[data-module="FixedPanelView.Rubric,MediaEmbeds"] .article__text > .article__item {padding-left: 0!important;}
div[data-view^="GalleryPreviews"][class^="article__text"] > .article__item {padding-left: 0!important;}
div[data-view="GalleryPreviews,PopupGalleryView.Shot,ShowGalleryPopup,GalleryShot"] {width: 780px!important;}
div[data-view="GalleryPreviews.Article.Gallery,GalleryShot"] {width: 780px!important;}
.block:not(.link-hdr) > .wrapper > .cols > div[class^="cols__column"] {width: 780px!important; margin-left: 0px!important;}
.js-article_track > .block_collapse .wrapper {width: 780px!important;}
.link-hdr + .block > .wrapper {width: 780px!important;}
.link-hdr > .block > .wrapper {width: 780px!important;}
.link-hdr:not([data-counter]) > .wrapper {width: 780px!important;}
div[itemtype] > .block > .wrapper {width: 780px!important;}
.shadow > .block > .wrapper {width: 780px!important;}
.block_shadow_top > .wrapper {width: 780px!important;}
.sticky-springs > .block:not(.block_special):not(.block_collapse_hor) > .wrapper {width: 780px!important;}
.dropdown_calendar  {max-width: 580px!important;}
/*удаление мусора*/
div.p-horo-banner.margin_bottom_30, div.js-springs__top.springs__top, .spring_top-250, .grid__item_banner, .p-content-banner,
.grid__banner, .shadow:after, .spring_top, div.box.box_block.margin_top_20, div.article__item.article__item_share, 
body > div.footer, div.grid.js-track_visibility ~ div.block > div.wrapper, .sticky-springs__top, .pc-branding, .pm-toolbar__banner,
div.block.block_shadow_top.block_center.link-hdr.js-track_visibility, .daynews__banner, .cols__inner > .box,
div.sharelist.sharelist_justify.js-module.margin_top_20, .block_separated_top, .margin_bottom_30 .nat, 
.p-tgb, .block.block_bg_primary.block_collapse_bottom, .mailru-visibility-check
{display: none!important; opacity: 0!important; pointer-events:none!important; }
}

@-moz-document domain("pogoda.mail.ru") {
div.rb-direct-side, .block_advert, .block_footer-index, .cols__column_absolute .cols__column__inner, .cols__column__inner_border, 
.block_footer, .branding, #doubleformat_top, html > body > header > a, .side-informer 
{ display:none!important; opacity: 0!important; pointer-events:none!important;}
}

@-moz-document domain("otvet.mail.ru") {
.fixed-supported .fixedsticky-on, .fixedsticky-withoutfixedfixed .fixedsticky-on {position: relative!important;}
.fixedsticky-dummy {display: none!important; opacity: 0!important; pointer-events:none!important;}
}

@-moz-document domain("e.mail.ru") {
#leftcol-banners {opacity: 0!important; pointer-events:none!important;}
.b-datalist__head__item_rb > div[class^="b-rb"], .b-letter__head__rb > div[class^="b-rb"] {margin-top: -33px!important;}
/*Директ в отчёте об отправке*/
.message-sent__hr.message-sent__hr_big, message-sent__hr.message-sent__hr_small, 
#preload_banner_1, .message-sent__ads, .message-sent__wrap:nth-of-type(4), .js-helper.direct-state 
{display: none!important; opacity: 0!important; pointer-events:none!important; height: 0!important;}
}

@-moz-document domain("go.mail.ru") {
.responses__wrapper > div[id^="js-"][id$="Block"] > div[class$="-block"], div[class="rb_banner"]
{display: none!important; opacity: 0!important; pointer-events:none!important; height: 0!important;}
}
