   -webkit-animation-fill-mode: both;
   animation-fill-mode: both;
    animation-name: scale;
    animation-duration: .3s;
  animation-delay: 0s;
  }
@keyframes scale {  from {   
  -webkit-transform: scale(1.0);   
  transform: scale(1.0);  
  visibility: visible;
  box-shadow:0px 00px 10px 0px rgba(00, 0, 00, 0.1)}
  to {
    -webkit-transform: scale(1.05);
    transform: scale(1.05);
    visibility: visible;
    box-shadow:0px 00px 30px 2px rgba(00, 0, 0, 0.5) }
  }
  
  @keyframes scaleout {  from {   
  -webkit-transform: scale(1.05);   
  transform: scale(1.05);  
  visibility: visible;  box-shadow:0px 00px 30px 2px rgba(00, 0, 0, 0.5)}
  to {
    -webkit-transform: scale(1.0);
    transform: scale(1.0);
    visibility: visible;box-shadow:0px 00px 10px 0px rgba(00, 00, 0, 0.1)
    }
  }
  
