css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
#watch7-main
{
margin: 600px !important;
}
  #watch7-sidebar
{
  position: relative !important;
  top: 0px !important;
margin-top: auto !important;
}
#player-api
{
margin-left: auto !important;
  margin-right: auto !important;
}
.watch-stage-mode .player-width {
    width: 854px;
    left: auto;
}
.player-width
  {width: 90% !important;}
    .player-height
  {height:  90vh !important;}

  
  
.video-stream, .html5-video-content, .html5-main-video, .html5-video-player,
.el-detailpage, .ps-null, .hide-info-bar, .autohide-controls-aspect, .autohide-controls-fullscreen, .autominimize-progress-bar-non-aspect, .ad-created, .iv-module-created, .iv-module-loaded, .endscreen-created, .ytp-block-autohide, .paused-mode
  {width: 100% !important; height:  100%!important; margin: auto !important; 	  }

.video-stream, .html5-main-video
{
 	position: relative !important;
	top: 50% !important; 
}
.html5-progress-bar 
  {margin: auto !important;width: 90vw !important;}
  .ytp-force-transform red
  {margin: auto !important;width: 90vw !important;}
.ytp-progress-bar-container
    {margin: auto !important;
      width: 90vw !important;}

 .html5-video-player 
  {margin: auto !important; text-align:center;}
.ytp-ad-progress
  {transform-origin: none !important;}
.ytp-load-progress
   {transform-origin: none !important;}
  .ytp-play-progress
   {transform-origin: none !important;}
}

