@-moz-document regexp("https?://twitter.com/.*") {
/*
Design inspired by https://dribbble.com/gregoirevella/projects/191186-Twitter-concept
Made by shosher8a and theotix, updated by mkody
*/
body {
font:14px/20px Roboto,sans-serif;
color:#333;
}

.global-nav,.global-nav-inner {
border:0;
background:#fff;
height:60px;
}

.global-nav .container {
max-width:100%;
text-align:left;
padding:9px 0;
}

.pull-right {
margin-right: 20px;
}

.container, .ProfilePage .global-nav .container{
max-width:100%;
width:100% !important;
}

.bird-topbar-etched,.pushstate-spinner {
font-size:30px;
margin-top:6px;
margin-left:20px;
}

.logged-in .topbar {
width:auto;
padding-left:165px;
}

.logged-in .topbar .global-nav [role=navigation] {
display:block!important;
position:fixed;
top:0;
left:0;
width:165px;
height:100%;
background:#213246;
}

.nav {
margin:0;
}

#global-actions>li {
height:50px;
overflow:hidden;
width:100%;
}

.nav>li>a,.nav>li>button {
height:50px;
}

#global-actions>li:hover>a,#global-actions>li.active>a {
height:46px;
}

.logged-in .wrapper,.logged-in .wrapper-narrow,.logged-in .wrapper-permalink .permalink-container {
width:100%!important;
min-height: 100%;
float:right;
box-sizing:border-box;
min-width:855px;
background:#F5F6F8!important;
padding:59px 0 0 155px;
}

.logged-in .wrapper-permalink .permalink-container {
padding:0 0 10px;
}

.permalink-in-reply-tos+.permalink-inner .permalink-tweet,
.permalink .inline-reply-tweetbox {
border-radius:0;
}

.dashboard {
float:right;
width:24.5%;
margin-right:.5%;
}

.content-main {
float:left;
width:73%;
border-right:none;
box-sizing:border-box;
}

.content-header,.content-no-header {
border:0;
border-radius:0;
}

.content-header .header-inner,.content-no-header .no-header-inner {
min-height:60px;
border-radius:0;
box-sizing:border-box;
border:none;
padding:18px 24px;
}

.stream .stream-items {
width:94%;
padding:3%;
}

.stream-items> li.stream-item {
display: inline-block;
box-sizing:border-box;
width:100%;
margin-bottom:3%;
}

.stream-container,.stream-items {
margin-top:-2px;
}

#stream-items-id > li.stream-media-items.stream-item.separation-module > div,.media-grid,.media-row-content.photo-list {
display:flex;
flex-wrap:wrap;
}

.content-main .stream-container .media-thumbnail.grid-tweet,.media-row-content.photo-list .media-thumbnail {
clear:none;
flex:1 1 auto;
border:none!important;
margin:4px;
}

.wrapper-search .Grid-cell .u-size2of3,
.AdaptiveSearchPage.three-col .content-main {
width: 100%!important;
}

.search-stream .stream-user-item .social-proof {
margin-left: 70px;
}

.search-stream .stream-item .content {
margin-left: 70px;
}

.stream-item .account .content {
margin-left: 75px;
}

.app {
padding-left: 70px;
}

.cards-base img {
margin: 0 auto;
}

.media-box img {
height:auto!important;
width:100%!important;
}

.content-main .stream-container .media-thumbnail.grid-tweet:last-child {
flex:0 1 auto;
}

.content-main .grid-tweet[scaled-width="574"] {
width:100%!important;
}

.js-stream-item.stream-item,.unfocusable-stream-item {
background:none;
border:none;
}

.conversation-module.stream-items {
padding:0;
width: 100%;
}

.tweet {
margin-bottom:5px;
background:#fff;
border:1px solid #EAECEB;
border-radius:6px;
color:#AAA;
padding:18px 20px;
}

.tweet>.context {
margin-left: 85px;
}

.stream-item-activity-line-me {
padding:10px 20px 5px 27px;
}

.stream-item-activity-me .activity-type {
left:7px;
}

.activity-truncated-tweet .tweet {
display:block;
padding:15px 0;
}

.activity-truncated-tweet .tweet-content {
margin-left:15px!important;
}

.stream-item-activity-me .activity-supplement {
padding:5px 5px 0 27px;
}

div.stream-item-header {
border-bottom:1px solid #EAECEB;
margin-bottom:15px;
}

.tweet .time {
float:right;
position:absolute;
right:20px;
top:18px;
}

.stream-item-header .avatar,.permalink-header .avatar {
float:none;
margin-top:3px;
margin-left:0;
position:absolute;
left:20px;
top:18px;
}

.permalink-header .account-group {
padding-left: 70px;
}

.conversation-module .conversation-tweet-item .stream-item-header .avatar {
margin-left:0;
z-index:300;
}

.with-media-forward .cards-base .media-forward {
border-radius:0;
max-height:300px;
}

.content > .cards-media-container {
margin:0 -20px;
}

.tweet div.content > .stream-item-header > a {
overflow:auto;
display:block;
height:60px;
}

.content-main .tweet div.content > .stream-item-header > a {
padding-left:60px;
}

.tweet div.content > .stream-item-header > a * {
float:left;
}

.tweet div.content > .stream-item-header > a .username {
clear:left;
}

.recent-tweets .tweet .avatar {
margin-left:0;
margin-top:-7px!important;
width:20px;
height:20px;
}

.recent-tweets .tweet .stream-item-header {
margin-bottom:0;
border-bottom:0;
height:30px;
}

.recent-tweets .js-action-profile-name {
line-height:1.1em;
}

.recent-tweets .tweet .stream-item-header a {
padding-left:35px!important;
}

.avatar {
border-radius:3px;
}

.module .flex-module {
background-color:inherit;
border:1px solid #E2E3E5;
border-radius:0!important;
border-width:1px 0 0;
}

.flex-module-header {
margin-bottom:8%;
padding-bottom:8%;
border-bottom:1px solid #E2E3E5;
}

.Icon--mediaplay:before {
height:60px;
line-height:80px;
}

.content-main .stream-container .media-thumbnail.grid-tweet {
height:15em!important;
}

.content-main .stream-container .media-thumbnail.grid-tweet img {
min-height:100%;
position:relative;
height:auto;
width:100%;
min-width:100%;
display:block;
margin:0!important;
}

.profile-card.profile-header .profile-picture {
border:0;
box-shadow:none;
}

.profile-card.profile-header .profile-picture,.profile-card.profile-header .profile-picture .avatar {
width:80px;
height:80px;
border-radius:100%;
}

#suggested-users {
position:absolute;
}

.route-profile .content-main {
margin-top:295px;
}

.ProfilePage .AppContent {
margin-left: 165px;
}

.route-profile .profile-card.profile-header {
width:65.2%;
margin-left:165px;
position:absolute;
left:0;
}
.profile-header .profile-banner-footer {
padding:0;
}

.u-textUserColor {
color:#6CAEE0!important;
}

.u-borderUserColor,.u-borderUserColorHover:hover,.u-borderUserColorHover:focus {
border-color:#6CAEE0!important;
}

.u-bgUserColor,.u-bgUserColorHover:hover,.u-bgUserColorHover:focus {
background-color:#6CAEE0!important;
}

.u-boxShadowInsetUserColorHover:hover,.u-boxShadowInsetUserColorHover:focus {
box-shadow:inset 0 0 0 5px #6CAEE0!important;
}

.u-textUserColorLight {
color:#C4DEF2!important;
}

.u-borderUserColorLight,.u-borderUserColorLightFocus:focus,.u-borderUserColorLightHover:hover,.u-borderUserColorLightHover:focus {
border-color:#C4DEF2!important;
}

.u-bgUserColorLight {
background-color:#C4DEF2!important;
}

.u-boxShadowUserColorLighterFocus:focus {
box-shadow:0 0 8px rgba(0,0,0,0.05), inset 0 1px 2px rgba(108,174,224,0.25)!important;
}

.u-textUserColorLightest {
color:#F0F6FB!important;
}

.u-borderUserColorLightest {
border-color:#F0F6FB!important;
}

.u-bgUserColorLightest {
background-color:#F0F6FB!important;
}

.u-bgUserColorDarkHover:hover {
background-color:#568BB3!important;
}

.u-bgUserColorDarkerActive:active {
background-color:#406886!important;
}

.content-main .expansion-container .conversation-root .tweet {
border-radius: 6px 6px 0 0;
}

.content-main .conversation-ancestor .tweet div.content > .stream-item-header > a,
.content-main .first-navigable-stream-item .tweet div.content > .stream-item-header > a,
.content-main .extra-ancestor-after-root .tweet div.content > .stream-item-header > a {
padding-left: inherit;
margin-top: 14px;
}

.content-main .last-navigable-stream-item .tweet div.content > .stream-item-header > a {
margin-top: 14px;
}

.content-main .expansion-container .tweet.descendant {
min-height:32px;
padding-left:20px;
}

.content-main .expansion-container .tweet.descendant .stream-item-header .avatar {
margin-left:0;
width:48px;
height:48px;
border-radius:4px;
}

#global-actions > li > a {
border-bottom-color:#6CAEE0;
}

.photo-selector:not(.disabled):hover .btn,.icon-btn:hover,.icon-btn:active,.icon-btn.active,.icon-btn.enabled {
color:#6CAEE0;
border-color:rgba(108,174,224,.5);
}

.photo-selector:not(.disabled):hover .btn,.icon-btn:hover {
background-image:linear-gradient(rgba(255,255,255,0), rgba(108,174,224,.1));
}

.tweet-btn,.tweet-btn:focus {
background-color:#6CAEE0;
background:rgba(108,174,224,.8);
}

.btn:focus,.btn.focus,.Button:focus {
box-shadow:0 0 0 1px #fff, 0 0 0 3px rgba(108,174,224,0.5);
}

.selected-stream-item:focus {
box-shadow:0 0 0 3px rgba(108,174,224,0.5);
}

.ProfileTweet.is-actionable {
box-shadow:0 0 0 2px rgba(108,174,224,0.5);
}

.rosetta .dm-tweetbox,.rosetta .WebToast-buffer--altColor,.content-main .conversations-enabled .expansion-container .inline-reply-tweetbox {
margin-bottom:5px;
background-color:#F0F6FB;
border:1px solid #e1e8ed!important;
}

.home-tweet-box {
background-color: #F0F6FB;
border-radius: 0 !important;
}

.conversations-enabled .inline-reply-caret .caret-inner {
border-bottom-color:#F0F6FB;
}

.tweet-box[contenteditable=true] {
border-color:rgba(108,174,224,0.25);
}

input:focus,textarea:focus,div[contenteditable=true]:focus,div[contenteditable=true].fake-focus {
box-shadow:inset 0 0 0 1px rgba(108,174,224,0.7);
border-color:#A6CEEC;
}

.currently-dragging.modal-enabled .modal .tweet-box,.currently-dragging:not(.modal-enabled) .tweet-content .tweet-box,body.supports-drag-and-drop .tweet-form.upload-photo-hover.drag-and-drop .tweet-box,.tweet-box[contenteditable=true]:focus {
