}

.DashboardProfileCard,.module ul.js-nav-links {
margin-top:10px;
}

.stream-items> li.stream-item.open,.route-profile .profile-page-header .profile-header-inner-overlay {
width:100%;
}

.stream-items> li.separation-module,.stream-items> li.semi-expanded {
box-sizing:border-box;
width:100%;
}

.tweet-actions .with-icn b,#doc.route-home .module .tweet-box {
display:none;
}

.stream-item .content,.permalink-tweet .content,.recent-tweets .tweet .content {
margin-left:0;
}

