css:
@-moz-document domain(www.sciencedaily.com) {

@namespace url(http://www.w3.org/1999/xhtml);
object {display:none !important;}
#seealso {display:none !important;}
#footer {display:none !important;}
#side {display:none !important;}
#bottomsearch {display:none !important;}
#googlebottom {display:none !important;}
#googlerelated {display:none !important;}
embed {display:none !important;}
#headercontent {
	width: 2000px !important;
}
#title {
	top: auto !important;
}

html, body {
	font-family: Georgia, serif !important;
	font-size: 15.5px !important;
}
iframe {
	display:none !important;
}

#wrapper {
	width: 1000px !important;
	margin: 30px auto !important;
	background-color: #fff !important;
}
#content { 
	width: 1000px !important;
	margin: 30px auto !important;
	background-color:#fff !important;
}
#main { 
	width: 1000px !important;
	margin: 30px auto !important;
	background-color:#fff !important;
}
#story {
	float: left !important;
	width: 780px !important;
	text-align: justify !important;
}
#related {
	float: right !important;
	width: 200px !important;
}
#sections {
	top: 15px !important;
	left: 100px !important;
	width: 1000px;
	text-align: left;
	margin: 0 auto;
}
#first {
	font-family: Georgia, serif !important;
	font-size: 15.5px !important;
	line-height: 120% !important;
} 
p {
	font-family: Georgia, serif !important;
	font-size: 14px !important;
	line-height: 120% !important;
}

#topstories div.storyphoto {
	width: auto !important;
	max-height: 500px !important;
}

#topstories div.storyphoto img {
	width: auto !important;
	height: auto !important;
}

}
