css:
@-moz-document domain("neopets.com") {
/********************************************************************/
/* HTML Tag Styles **************************************************/
/********************************************************************/
	
	body {
		background-color: #ACADAE !important;
	}

	A:Link, A:Visited {
		color: #505F80 !important;
	}

	A:Hover {
		color: #CACACA !important;
	}

/********************************************************************/
/* Foundation IDs ***************************************************/
/********************************************************************/

	#main {
		border-left: 2px solid #536265 !important;
		border-right: 2px solid #536265 !important;
	}
	#header, #ban {
		background-image: url("http://i56.tinypic.com/2vab5fa.jpg")!important;		
		border: 0px !important;
	}
	
	#ban {
		background-color: #536265 !important;
		background-position: left top !important;
	}

	#header {
		background-color: #536265 !important;
		background-position: left bottom !important;
	}

	#footer {
		background-color: #536265 !important;
		background-image: url("http://i54.tinypic.com/2ue4emd.jpg") !important;
	}




/********************************************************************/
/* Header Stuff *****************************************************/
/********************************************************************/

/* Navigation.  Duh.  */
	ul.dropdown {
		border: 1px solid #2A3E46 !important; 
		background-color: #ACADAE !important; 
	}

	LI:hover UL.dropdown A, LI.over UL.dropdown A {
		color: #000000 !important; 
	}

	LI:hover UL.dropdown A:Hover, LI.over UL.dropdown A:hover {
		color: #DEDCDC !important; 
	}

/* Event Icons */
	.eventIcon, .user {
		color: #DEDCDC !important; 
	}

	.eventIcon A:Link, .eventIcon A:Hover, .eventIcon A:Visited,
	.user A:Link, .user A:Hover, .user A:Visited {
		color: #DEDCDC !important; 
		font-weight: bold !important;
	}

/* Clock */
	#nst {
		color: #DEDCDC !important; 
	}


/********************************************************************/
/* Sidebar Classes **************************************************/
/********************************************************************/

	.sidebarModule {
		border: 1px solid #000000 !important;
	}

	.sidebarTable, .sidebarHeader {
		border: 1px solid #FFFFFF !important; 
	}

	.sidebarHeader {
		background-color: #505F80 !important; 
	}

	.sidebarHeader A:Link, .sidebarHeader A:Hover, .sidebarHeader A:Visited {
		color: #000000 !important; 
	}


/********************************************************************/
/* Content Classes **************************************************/
/********************************************************************/

	.contentModuleHeader {
		background-color: #505F80 !important; 
		color: #FFFFFF !important; 
	}

	.contentModuleHeader A:Link, .contentModuleHeader A:Hover, .contentModuleHeader A:Visited {
		color: #000000 !important; 
	}

	.contentModuleContent img {
		border: px solid #000000 !important; 
	}

	.moreLink {
		border: 1px solid #8F8F8F !important; 
	}

	.contentModuleHeaderAlt {
		background-color: #505F80 !important; 
		color: #000000 !important; 
	}

	.contentModuleHeaderAlt A:Link, .contentModuleHeaderAlt A:Hover, .contentModuleHeaderAlt A:Visited {
		color: #CACACA !important; 
	}

	.moreLinkAlt {
		border: 1px solid #674736 !important; 
	}


/********************************************************************/
/* Footer Classes ***************************************************/
/********************************************************************/

	.footerForm {
		color: #CACACA !important; 
	}

	.copyright, .copyright A:Link, .copyright A:Hover, .copyright A:Visited {
		color: #CACACA !important; 
	}


/********************************************************************/
/* Misc Classes of Doom *********************************************/
/********************************************************************/

	.marqueeSlot, .marqueeSlot A:Link, .marqueeSlot A:Hover, .marqueeSlot A:Visited {
		background-color: #b9c96d !important; 
		color: #FFFFFF !important; 
	}

	.marqueeSlotOn, .marqueeSlotOn A:Link, .marqueeSlotOn A:Hover, .marqueeSlotOn A:Visited {
		background-color: #899551 !important; 
		color: #FFFFFF !important; 
	}

/********************************************************************/
/* Random Footer Images *********************************************/
/********************************************************************/

	.footerNifty {
		display: none;
	}
	
}
