css:
@-moz-document url-prefix("http://d2n.duskdawn.net/map") {
.nvt {
    background-image: none;
    background-color: #32411C;
}
  
.unknown { 
    background-image: url("https://sparstrumpf.omameier.net/nvtnew.png");
}

#map li {
  width: 28px !important;
  height: 28px !important;
}

#map {
  width: 890px !important; /* Before: 640px */
}

div.app {
  width: 1220px !important; /* Before: 920 px */
}

img.depleted_building_marker, img.blueprint_available_marker {
  margin-left: 5px !important;
  margin-top: 5px !important;
}

img.depleted_marker {
  margin-left: 15px !important;
  margin-top: 15px !important;
  height: 13px;
  width: 13px;
}

img.citizen_marker {
  margin-left: 19px !important;
  margin-top: 0px !important;
}

#map .headerx, #map .headery {
  line-height: 28px !important;
  font-size: 12px !important;
}

#map li.corner {
  background-size: 100% auto !important;
}

#map li.zone.building {
  background-size: 100% auto !important;
}

#map li.zone.nvt.building {
  background-image: url(http://d2n.duskdawn.net/images/building-0.png);
}

img.visited_marker {
  width: 28px;
  height: 28px;
}

p.zombie_marker, p.scout_sense_marker {
  font-size: 12px !important;
  margin-top: 6px;
  width: 100% !important;
  text-align: center;
  padding-left: 0px !important;
  padding-top: 0px !important;
}
}
