@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(livejournal.com) {
	.b-tree.b-tree-root, .ljcmt_full, .comments-body>div, .entry-comments-text>.comment-wrap, .entry-comments-text>.comments-links:nth-child(2), div.b-xylem:nth-child(1), H2#comments, .Comments, td.body > div, div.comments-pages, .b-pager, .b-massaction, .comments-footer {display:none;}
	td.body > .entry, .comments-nav,.comments-links {display:inherit;}
}
