css:
@-moz-document domain("twitter.com") {
/* ==== twitter  -  minimal
		(re)design by daylo
		cc-by
		
		version 0.6
		= small fixes + removed moments
   ==== */

/* ====
		General
   ==== 
*/
* {
  text-decoration: none!important;
}
div.module {
  border-radius: 3px;
}
div.BannersContainer, .footer.inline-list, .Trends {
  display: none!important;
}
div.front-card {
  margin-top: 0;
}
img.ProfileAvatar-image, .ProfileTweet-avatar, .avatar {
  border-radius: 1000px!important;
  background: transparent;
}
div.ProfileAvatar, .nav .session .dropdown-toggle:before {
  border: 0;
  box-shadow: 0 0 0;
  background: transparent;
}
/* ====
		Banner
   ==== 
*/

/* =semi-opaque= */
div.global-nav-inner {
  background: rgba(255,255,255,0.8);
}
div.global-nav, div.topbar {
  border: 0;
}
#search-query {
  background: rgba(255,255,255,0.5);
  border: 0;
}
#search-query:focus {
  background: rgba(255,255,255,0.7);
}
#search-query:active {
  background: rgba(255,255,255,1);
}
/* =remove discover= */
a[href="/i/discover"], li[data-global-action="moments"] {
  display: none !important;
}
span.text, div[data-promoted="true"] {
  display: none !important;
}
/* =search bar fixes= */
input {
  border-radius: 3px !important;
}
div.pull-right {
  display: block;
  float: left;
  width: 86%;
}
div[role="search"] {
  width: 83%;
}
form.form-search {
  width: 98%!important;
}
ul.right-actions {
  margin: 0 0 0 7%!important;
}
/* ====
		left-col
   ==== 
*/

body.three-col .wrapper {
  width: 100%;
}
div.DashboardProfileCard, .dashboard {
  position: fixed;
  z-index: 1000;
  width: 23%;
}
div.trends { padding-top: 70%; }
div.trends-inner {
  opacity: 0.4;
  transition: opacity 0.2s ease-in-out;
}
div.trends-inner:hover {
  opacity: 1;
}
/* ====
		centre
   ====
*/

div.content-main {
  width: 75% !important;
  float: right!important;
  border-radius: 2px!important;
}
#page-container {
  padding-right: 0;
  margin-right: 0;
  padding-left: 0;
  margin-left: 0;
}
div.home-tweet-box, .top-timeline-tweetbox, .tweet, .new-tweets-bar, .home-tweet-box, .top-timeline-tweetbox .timeline-tweet-box, .tweet-box, .content-header .header-inner, .content-no-header .no-header-inner, .stream-item, .unfocusable-stream-item, .stream-item-activity-me, .module li:first-child .list-link, .primary-btn, .following.first-hover .follow-btn:hover, .following .follow-btn, .following .follow-button.cancel-hover-style:hover, .following .follow-button, .notifying .device-follow-button, .button-group-item .content.selected {
  border: 0!important;
  border-radius: 2px!important;
}
#tweet-box-home-timeline > div {
  font-size: 20px;
}
#typeahead-dropdown-6 {
  width: 85%;
}
#daylo-placehold-dgbfkn, .home-tweet-box, .dm-tweetbox, .RetweetDialog-commentBox, .WebToast-box--altColor, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox, .ProfileTweet {
  background: #fff;
  border: 0;
}
div.stream-item-header > a > span, .username s, .account-group-inner s, a.tweet-timestamp {
  color: #e6e6e6 !important;
}
div.u-lg-size2of3, .u-lg-size4of6, .u-lg-size8of12 {
  width: 100% !important;
}
div.container {
  max-width: 100%!important;
}
/* ====
		right-col
   ==== 
*/


div.dashboard.dashboard-right, .ProfileWTFAndTrends {
  display: none;
  width: 0;
}
/* =====
		footer
   =====
*/

div.Footer {
  display: none;
}
/* ====
		/status/
   ====
*/
div.permalink-container {
  width: 95%;
  margin-left: auto;
  margin-right: auto;
}
/* ====
		direct messages
   ====
*/

#dm_dialog-dialog {
  position: initial!important;
  padding: 2% 5% 0 5%;
  width: 90%;
  
  text-align: left;
}
div.dm-dialog .twttr-dialog-header {
  width: 95%;
}
p.dm-thread-snippet {
  float: initial;
}
}
