css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dailyff14.com") {

  * {
    text-shadow: none !important;
  }
  
  #container > .rtcom,
  .left-container,
  .rss-blogroll,
  .blogroll-channel,
  .main-container > .hdpr,
  #more > b,
  #more > h2,
  div[id*=feed],
  div[id*=imobile],
  #ff14ga,
  a[href*=ffantena] {
    display: none !important;
  }
  
  .main-container {
    width: 850px;
    
  }
  
  .main-container,
  .article {
    background-size: 850px;
  }
  
  .article-header-inner {
    width: 580px;
  }
  
  .articles-header-inner {
    margin-left: 1em;
  }
  
  #more > span {
    font-size: 16px !important;
  }
  
  #more > div {
    margin-bottom: 2em;
    line-height: 1.8em;
  }
  
  hr { 
    height: 0px !important;
    border: none !important;
  }
  
  br {
    display: block !important;
    line-height: 0px !important;
  }
  
}
