css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("http://theoldreader.com.*"),
               regexp("https://theoldreader.com.*"){
/*
LilyB's - The Old Reader
A middle ground color scheme for The Old Reader. Not to dark and not to bright.
*/

.navbar .navbar-inner{
	background-color:#777777}

body,
.landing .left .screenshot,
.reader .floating{
	background-color:#444444;
	color:#EDEDED}

a{text-shadow:1px 1px rgba(0,0,0,0.4)}

.landing{
	color:#EDEDED}
	
.alert.alert-info {
    background-color:#DEDFDE}

.well{
	background-color:#777777;
	color:#111111;
	border:1px solid #808080}

.home-page dl dd p a {
    color:#FFFFFF}

.hero-unit,
.hero-unit.posts-filler span{
	background-color:#777777;
	color:#111111}

/*sidebar*/
.nav-list li.nav-header.active a:not(.i):before{
	border-left:3px solid #DA3F2D}

.nav-list li.nav-header a,
.nav-list li.nav-header a:hover{
	text-shadow:1px 1px rgba(0,0,0,0.3);
	color:#DA3F2D!important}

.badge{
	background-color:#DA3F2D}

/*feed*/
.post h3 a{
	text-shadow:none}

.post h3 a:hover{
	color:#30A67C}

.post.unread {
    border-left:8px solid #30A67C;
    padding-left:6px}

.post blockquote{
	background-color:#BBBBBB}

/*buttons*/
.btn-small{
background:#DDDDDD linear-gradient(#DDDDDD,#CCCCCC)}
/*end feed*/
}/*end style*/
