css:
@-moz-document domain("scratch.mit.edu") {
html{height:100%;}#topnav form.search{position:absolute;display:inline-block;bottom:3px}#topnav form.search input{box-shadow:none;border-radius:0;width:45px;-webkit-transition:250ms all;transition:250ms all}#topnav form.search #search-input:focus{box-shadow:none;width:170px}#topnav{background-color:#02b0ff;box-shadow:0 0 20px rgba(0,0,0,.5)}#topnav ul.site-nav li.last{width:0}#topnav ul.site-nav li.last a{font-size:0;cursor:default}#topnav ul.site-nav li.last:after{content:'';display:block;position:fixed;opacity:1;top:0;width:calc(50% - 100px);right:0;border-bottom:35px solid #fff;border-left:35px solid transparent;cursor:default}#topnav ul.account-nav>li>span,#topnav ul.site-nav li:first-child a{color:#02b0ff}.dropdown span.dropdown-toggle span.caret{border-top:4px solid #02b0ff;opacity:1}#topnav ul.account-nav.logged-in>li,#topnav ul.account-nav>li,#topnav ul.site-nav li,#topnav ul.site-nav li.last{z-index:10;border-left:none;border-right:none}#topnav ul.site-nav li:hover{background-color:#008ece}
#topnav ul.site-nav li:nth-child(2) a{font-size:0}#topnav ul.site-nav li:nth-child(2) a:before{content:'Explore';font-size:15px}#topnav ul.site-nav li:nth-child(3){position:fixed;right:calc(50% - 65px);height:35px}#topnav ul.site-nav li:first-child{position:fixed;right:calc(50% - 175px);height:35px}#topnav ul.account-nav .my-stuff .mystuff-icon{background:url(http://i.imgur.com/rqrJnyw.png) no-repeat center center}#topnav ul.account-nav .messages .messages-icon{background:url(http://i.imgur.com/AbjUMwL.png) no-repeat center center}#topnav ul.account-nav .logged-in-user .user-icon{border:none;border-radius:2px;position:relative;bottom:1px}#topnav ul.account-nav .logged-in-user.dropdown.open,#topnav ul.account-nav>li.sign-in.open{background-color:#ddd!important}#topnav ul.account-nav .logged-in-user .dropdown-menu{background-color:#fff}#topnav ul.account-nav li:hover,#topnav ul.account-nav.logged-in li:hover,#topnav ul.account-nav>li.join-scratch:hover,#topnav ul.account-nav>li.sign-in:hover,#topnav ul.site-nav li:first-child:hover{background-color:#e5e5e5}#topnav ul.account-nav ul.user-nav li.logout.divider{border-top-color:#eee}#topnav ul.account-nav ul.user-nav li a{color:#02b0ff}#topnav ul.account-nav .sign-in .popover{background-color:#ccc;border-color:#dadada}#topnav ul.account-nav .sign-in .popover .arrow{border-bottom-color:#dadada}#topnav ul.account-nav .sign-in .popover .arrow:after{border-bottom-color:#ccc}#topnav ul.account-nav .sign-in form .forgot-password a,#topnav ul.account-nav .sign-in form label{color:#008ece}#footer{background-color:#008ece;color:#ddd;-webkit-box-shadow:inset 0 0 10px #02b0ff;box-shadow:inset 0 0 10px #02b0ff}#footer ul.footer-col li h4{color:#fff!important}#footer a{color:#9ce0ff}#footer a:hover{color:#50c8ff}
        #explore-bar{visibility: hidden;}
        #tip-bar{visibility: hidden;}
}

@-moz-document url-prefix("http://scratch.mit.edu/discuss/") {
.linksb,.linkst{color:#fff;text-shadow:0 1px #000}.linksb a,.linkst a{color:#9ce0ff;text-shadow:0 1px #000}.linksb a:hover,.linkst a:hover{color:#50c8ff;text-shadow:0 1px #000}
}

@-moz-document domain("cdn.scratch.mit.edu") {
body,html{background:0 0}
}
