css:
@-moz-document domain(iphones.ru){

/*** iPhones.ru - Light v2.23 ***/
/*** релиз от 15.07.2016 ***/

/** .contentShifter, **/
/** Hide block / Убираем всё лишнее **/

.header__menu-project-cont,
.externalofferlist__cont,
.postlist-incut-dealslist,
.push-notifier-block,
.externalpostlist__cont,
.content_sticky-wrap,
.postlist_sticky-wrap,
.sidebar,
.contestlist,
.footer__menu,
.footer__menu_social,
.social-share,
.content-wrap_related,
.header__sub-wrap,
.postlist-banner,
.advert-header_branding,
.postitem_line-banner,
.bnr,
div.content-wrap_misc.content-wrap_line
 { display: none !important; }


body{ background:#211818 !important; }

/** Хидер **/
.header__main-wrap{
	border-top: 0px !important;
}

.header__menu > li > a{
	padding: 25px 10px 27px  !important;
}

.header__main-wrap{
	background-color: initial;
	border-bottom: 0px solid white !important;
}

.header__main-wrap .container{
	background-color:  #ffffff;
	border-bottom: 1px dotted #0055cc !important;
}

.header__cont{
	padding: 0 20px 0 20px;
}

.header__search{
	display: none !important
}

/** Новости **/

div#contentShifter{
height: 125px !important;
}

.postlist_sticky__col1-in,
.postlist_sticky__col2
{
	border-top: 0px dotted #0055cc !important;
	border-bottom: 1px dotted #0055cc !important;
}

.postlist_sticky__col1-in,
.postlist_sticky__col1 .post_sticky
{
	width: 590px !important;
}
.postlist_sticky__col2-in{
	border: 0px  !important;
}


.postlist_sticky__col1 .post_sticky{
	border-left: 0px !important;
	border-right: 0px !important;
}

.container{
	width: 960px !important;
	width: 960px !important;
}

.content-wrap{
	border-left: 0px solid white !important;
	border-right:  0px solid white !important;
}

.postlist-page .content-wrap{
	margin-top: 0px !important;
}

.postlist-page .content{
	margin-left: 20px;
	width: 920px;
}

.content-wrap_post{
	margin-top: 0px !important; 
}

.content-wrap_post .content{
	margin-top: 0px !important; 
}

.postitem{
	border-bottom: 1px dotted #e3e3e3 !important;
	padding: 30px 0 30px !important;
}
.postitem__image{
	float: right !important;
	width: 450px !important;
	margin-left: 10px !important;
}

.postitem_small .postitem__image{
	border-radius: 0% !important;
}

.postitem__content{
	float: left !important;
	width: 450px !important;
	margin-right: 10px !important;
}

.postitem_large .postitem__content-p{
	font-size: 18px !important;
}

.postitem_small.postitem_image .postitem__content{
	margin-right: 10px !important;
	margin-left: 0px !important;
}

.postlist__navigation{
	margin: 30px 30px 30px;
}


/** Страница поста **/
.post-page .content{
	width: 850px !important;
	margin-top: 0px !important; 
}

/** Футер **/
footer{
	background-color: initial;
}
.footer__cont{
	background-color: #ffffff;
	padding: 30px 30px 30px !important;
	border-top: 1px dotted #0055cc !important;
}

.footer__copyright{
	float: left !important;
}

}
