css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pandora.com") {
    
/* Colors */
#playerBar, .contentnav {
    background-color: #202020;
}
    
.stationContent {
    background-color: #101010 !important;
}

.stationListItem .options, .btn_bg_sort.unselectable {
    background: #360F52;
}

.stationName.stationName_hover, .stationListItem.selected, .btn_bg_sort.selected {
    background: #492366 !important;
}

.slidesBackground {
    background-color: #bababa;
}
    
#body {
    background-color: #ffffff;
}
    
    
/* Splash */
#splash {
    background-color: #360F52;
    background-image: url('https://31.media.tumblr.com/090eb2dad5dced7771278b1a70c62bf1/tumblr_inline_n4iii0VX851qmpver.png');
    background-repeat: repeat-x;
    background-attachment: fixed;
    background-position: top center;
}
  
  
/* Top Bar */
#brandingBar .middlecolumn {
display:none !Important;}
  
#brandingBar {
background-image:none !important;
background-color:#3a4a6a !important;
color:#FFF !important;
position:absolute !important;
right:0px !important;
height:25px !important;
top:70px !important;
z-index:2 !important;
min-width:0px !important;
max-width:0px !Important;}
  
#topnav { 
top:-1px !important;
max-height:10px !important;
min-height:10px !important;}


  
  
/* PlayBar */
.top{
    display: none !important;
}
  
#playerBar {
    border-bottom: none !important;
    height: 68px !important; 
}
  
.contentnav {
    border: none !important;
}
  
.middlecolumn{
margin-left: 19.5% !important;
}
  
#playerBar .middlecolumn {
margin-left: 21.7% !important;
margin-top: 2px !important;
padding-top: 15px;
}
  
.searchBox {
width: 270px !important;
height: 38px !important;
background: url('http://imageshack.com/a/img842/9341/gwye.png') no-repeat;
background-position: left center!important; 
background-size: 30px 38px;
border-radius: 6px !important;
}
  
#playerBar .leftcolumn {
width: 300px !important;
margin: 3px 0 0 7px !important;    
}
  
#playerBar .searchInput {
top: 9px !important;
left: 2px !important;
font-size: 15px !important; 
color: #A8ABB3 !important;
}
  
#playerBar .searchSubText {
top: 21px !important;
left: 40px !important;
font-size: 15px !important;
color: #A8ABB3 !important;
}
  
.rightcolumn {
width: 269px !important;
}
  
#playerBar .rightcolumn .info {
margin: 7px 9px 0 0;
width: 208px !important;
height: 62px !important;
} 
  
#playerBar .rightcolumn .info a {
font-size: 12px !important; 
}

#playerBar .rightcolumn .cd_icon {
margin: 6px 5px 0 0 !important; 
}
  
.user_activator {
background:none !important;
border:0px !important;
margin: -5px -10px 0 0 !important;
}
    
#brandingBar .rightcolumn {
margin-top: -10px;
}
  
  
/* Stretching */
.contentnav {
position:fixed !important;
top:70px !important;
left:0px !important;
width:100% !Important;
z-index:1 !important;}

.adSupported-layout #adLayout, .adSupported-layout .contentContainer,
div#mainContentContainer.contentContainer div.content div#mainContent div.home div.homeRightSide div.slidesBackground div img {
width: 100% !important;}

.contentContainer .top, .view-2, .home .homeRightSide, .stationContent, #mainContent, .home.fxCol-cont-1.view-2, 
#mainContentContainer {
border-radius:0px !important;}

#mainContainer {
background-color: #FFF !important;
height:100% !important;
}
  
.homeRightSide, .slideDragLimit {
height: 100% !important;
}
      
.fxCol-cont-1 {
    background: none;
}

.col-prop-1 {
    min-height: 500px;
}
  
#mainContainer {
background-image: none !important; 
}
  
  
  
/* Main Container Positioning*/
.stationContent {
z-index: 0;
} 
  
.contentnav {
    top: 63px !important;
}
  
#main {
top: 91px !important;
}
  

/* Sidebar */
#detailContainer {
margin-left: 2px !important;
}
  
.stationContent{
    border: none !important;
    border-radius: 0px;
}
  
.stationName{    
    color: #ddd !important;
    text-shadow: none !important;
}
  
#shuffleIcon{
    margin-top: 8px;
}
    
.stationListItem .stationName .stationNameText.promotedStation{
    display: none !important;
}
    
.stationListItem.selected{
    border: none;
}
    
.stationListItem.selected .stationName {
    padding: 3px 0px 5px;
    margin-top:5px;
}
    
.stationListItem .stationName{
    padding-top: 3px;
}
    
.stationListItem{
    margin-top: 5px;
}
    
.stationListItem .options{
    border: none;
}
    
.option.addVariety, .option.optionsItem{
    border: none !important;
    color: #ddd !important;
}
    
.stationListHolder {
    margin-top: 30px;
}
   
#stationSortButtons.clearfix{
    margin-top: -580px;
}
    
#stationSortDate, #stationSortAlpha {
    border: none;
    color: #ddd !important;
    text-shadow: none !important;
    width: 95px;
}
    
.btn_bg_sort.selected, .btn_bg_sort {
    background-image: none;
}
    
.stationContent{
    height: 100%;
    position: fixed;
}
    
.jspContainer, .stationListHolder {
    height: 550px !important;
}
  

  
  
/* Slider > Background */
.slide{
margin-left: 8px !important;
}  
  
/* Slides > Background */
.slidesBackground > div > img, .slidesBackground > img {
    display: none !important;
}
    
.slidesBackground {
    padding-top: 23px !important;
    width: 100% !important;
}
  
.bottom{
width: 100% !important;
}
  
  
/* Slides > Thumbs */
.thumbContainer{
    display: block !important;
    background: none !important;
    /*background: rgba(0,0,0,0.6) !important;*/
    margin-top: -70px !important;
    height: 60px !important;
    width: 100% !important;
    padding-top: 15px;
}
    
.thumbDown{
    margin-left: 52px !important;
}
    
.thumbUp, .thumbDown{
    background: none !important;
}
    
.slide .thumbContainer .thumbUp a:link, .slide .thumbContainer .thumbUp a:visited { 
    background: url("http://pandora.com/img/player-controls/btn_up.png")no-repeat scroll 0% 0% transparent;
}
    
.slide .thumbContainer .thumbUp.indicator a:link, .slide .thumbContainer .thumbUp.indicator a:visited {
    background: url("http://pandora.com/img/player-controls/btn_up_indicator.png")no-repeat scroll 0% 0% transparent;
}
    
.slide .thumbContainer .thumbDown a:link, .slide .thumbContainer .thumbDown a:visited {
    background: url("http://pandora.com/img/player-controls/btn_down.png")no-repeat scroll 0% 0% transparent;
}
    
.slide .thumbContainer .thumbDown.indicator a:link, .slide .thumbContainer .thumbDown.indicator a:visited {
    background: url("http://pandora.com/img/player-controls/btn_down_indicator.png")no-repeat scroll 0% 0% transparent;
}  
    
/*.slides_left_scroll.unselectable, .slides_right_scroll.unselectable {
    background: none;
    display: block !important;
    background-color: #360F52;
    height: 50px;
}
    
.slides_left_scroll.unselectable {    
    border-radius: 0 5px 5px 0;
    background-image: url('https://cdn4.iconfinder.com/data/icons/arrows-9/100/arrow-9-512.png');
    background-size: 23px 40px;
    background-repeat: no-repeat;
}
    
.slides_right_scroll.unselectable{
    border-radius: 5px 0 0 5px;
}*/
  
  
/* Lyrics, Bio, etc */
.lyricsText.unselectable { 
font-size: 16px !important; 
line-height: 22px !important;
}
  
#trackInfo{
    border: none !important;
}
    
#trackDetail{
    /* display: none !important; */
    border: none !important;
    background-color: #fff !important; 
}
   
#trackInfoButtons{
    background-color: transparent;
    border: none;
   /* width: 330px;
    margin: -55px auto 20px auto; */
}
  
#trackDetail > div.item.relatedArtists {
margin-bottom: 12px !important;  
}
  
.searchPopupContainer {
position: fixed;
top: 55px;
left: 14px;
width: 296px;    
}
  

  
  
  
/* Ads, Logo, Footer, etc. */
#ad_container, #accountMessageRibbon, #footer, .track_detail_close, .divider.last,
#videoPageInfo, .stationChangeSelectorNoMenu, .logo, .upgradeLink, .user_menu + SPAN, #upgradeOffer {
display:none !important;
height:0px !important;}
  
#promobox, .platformPromo.view-3.clearfix {
display:none !important;
}
  


}
