css:
/* 2015-03-11 */

@-moz-document url-prefix("http://sc.ivt.su"), url-prefix("https://sc.ivt.su") {

.post-like, .user-photo {
    visibility: collapse;
    display: none
}
    
.x-toolbar {
    background-image: none;
    background-color: #485d72;
    border-color: #405272;
    color: #ffffff !important;
}
ul.x-tab-strip-top {
    background-image: none;
    background-color: #485d72;
    border-bottom-color: #405272;
}
.x-tab-strip-top .x-tab-right, .x-tab-strip-top .x-tab-left, .x-tab-strip-top .x-tab-strip-inner {
    background-image: none;
    background-color: #7591ad;
}
.x-tab-strip-inner span.x-tab-strip-text {
    color: #ffffff;
}
.x-btn button {
    color: #ffffff;
    font-size: 9px;
    background-color: #485d72;

}
.x-toolbar .x-btn-over .x-btn-mc em.x-btn-split,
.x-btn-tl, .x-btn-tr, .x-btn-tc, .x-btn-ml, .x-btn-mr, .x-btn-mc, .x-btn-bl, .x-btn-br, .x-btn-bc {
    background-image: none;
}
.x-layout-collapsed, .x-layout-split {
    background-color: #547494;
}
.x-accordion-hd {
    background-image: none;
}
.x-panel-bwrap {
    background-color: #485d72;
}
.x-panel-header {
    color: #ffffff;
    border-color: #5c76a5;
    background-color: #485d72;
    background-image: none;
}
.x-tree-node .x-tree-selected {
    background-color: #b8c7e4;
}
.unread-action {
    background-color: #d0d9ff;
}
.task-action-block {
    padding: 5px;
    border-bottom-color: #95add6;
}
.x-form-text {
    background-image: none;
    background-color: #485d72;
    color: #ffffff;
    border-color: #95add6;
}

td.x-grid3-hd-over .x-grid3-hd-inner, td.sort-desc .x-grid3-hd-inner, td.sort-asc .x-grid3-hd-inner, td.x-grid3-hd-menu-open .x-grid3-hd-inner {
    background-color: #d2d9e4;
    background-image: none;
}

}
