css:
@charset "utf-8";
/*Autor: skorki */
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("imdb.com") {

/*smieci*/
#bottom_ad_wrapper {
display: none !important;
}
/*tÅ‚o*/
#styleguide-v2 {
background-image: none !important;
background: #1C1B1B !important;
background-color: #1C1B1B !important;
}
.reset, th, tr, thead, tfoot, tbody, caption, table, legend, label, form, fieldset, li, td, ul, ol, dd, dt,
 dl, i, u, b, var, tt, sup, sub, strong, strike, small, samp, s, q, kbd, ins, font, em, dfn, del, 
 code, cite, big, address, acronym, abbr, pre, blockquote, p, h6, h5, h4, h3, h2, h1, iframe, object, applet, span, div, body, html{
color: #b0b0b0 !important;
text-shadow : none !important;
}
div{
border-color: #000000 !important;
}
a {
color: #d2d2d2 !important;
text-shadow : none !important;
font-weight: bold !important;
}
.redesign .ghost {
color: #000000 !important;
}
img, #home_img, #nb_search .primary.btn  {
opacity: 0.7 !important;
}
#navbar .alt_logo a {
opacity: 0.1 !important;
}
#root, div#footer, #sidebar, #main, #pagecontent, .article, .even, #filmography .head,
.aux-content-widget-2, .aux-content-widget-3, .article.on-tv, .aux-content-widget-4,
.lister-item.mode-advanced:nth-child(2n), .lister-item.mode-advanced.nth-child-even, 
.lister-item.mode-detail:nth-child(2n), .lister-item.mode-detail.nth-child-even, 
.lister-item.mode-simple:nth-child(2n), .lister-item.mode-simple.nth-child-even,
.lister > .header, .faceter > .faceter-content,
.faceter > .faceter-header,
div.sub_nav, #navbar, #latestHeadlines, ul#navWatchlist li, .listo .header .nav, .bannertop, div.media_single,
#navbar #nb_search #navbar-suggestionsearch,.rating-list, div.findMoreMatches  {
background: #3d3b3b !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border-color: #000000 !important;
}
.odd,
.lister-item.mode-advanced:nth-child(2n+1), .lister-item.mode-advanced.nth-child-odd, 
.lister-item.mode-detail:nth-child(2n+1), .lister-item.mode-detail.nth-child-odd,
 .lister-item.mode-simple:nth-child(2n+1), .lister-item.mode-simple.nth-child-odd,
#prometer{
background: #1C1B1B !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border-color: #000000 !important;
}
.faceter > .faceter-content > .faceter-facets,
.rec_details .rec-jaw-upper, div#canvas, div#meta, div#photo-container, div#photo-credit {
background: #3d3b3b !important;
border-color: #000000 !important;
}
.rec_details .rec-jaw-lower > div.rec-jaw-teeth {
display: none !important;
}
.pro-list-title, #prometer #meterRank, .rec_details .rec-jaw-lower, div#photo-caption {
background: rgba(0,0,0,0.1) !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border: none !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
color: #B1B1B1 !important;
}
td#img_primary img {
border-color: #000000 !important;
}
span.in_production, a.in_production, a.in_production:link, a.in_production:visited, a.in_production:active, a.in_production:hover { 
color: #FF0000 !important;
}
/*przyciski*/
.faceter-category.active,
.btn2, .listo ul.list_tabs a, .listo ul.list_tabs li.active a, ul.quicklinks li.subnav_selected, .btn {
background: #3d3b3b !important;
background-color: #3d3b3b !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
text-shadow: none !important;
border: none !important;
color: #d2d2d2 !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
}
.btn2:hover {
background: #F3CE00 !important;
color: #000000 !important;
}
.faceter > .faceter-content > .faceter-categories > ul > li:hover {
background: #1C1B1B !important;
background-color: #1C1B1B !important;
text-shadow: none !important;
border: none !important;
color: #d2d2d2 !important;
}
/*wyszukiwarka*/
#navbar input, #navbar select, form {
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border: none !important;
color: #ffffff !important;
background: #3d3b3b !important;
background-color: #3d3b3b !important;
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}
#navbar #nb_search #navbar-suggestionsearch a {
background: #3d3b3b !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border-color: #000000 !important;
}
#navbar select option { 
color: #ffffff !important;
}

}
