css:
@-moz-document domain("nyaa.si") {
 
  body {
    background-color: #23696C;
  }

  .torrent-list > tbody > tr > td {
    background: #D9EDF7;
  }

  .table-bordered {
    border: 0px solid #ddd;
  }

  .table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td {
    border: 0px solid #ddd;
  }

  .container {
    width: 1170px;
    background: #D9EDF7;
    border-radius: 5px;
  }

  .alert {
    border: 0px solid transparent;
  }

  .navbar-inverse {
    background-color: #173738;
    border-color: #173738;
  }

  .navbar-inverse .navbar-collapse, .navbar-inverse .navbar-form {
    background: #173738;
  }

  .navbar-inverse .navbar-brand, .navbar-inverse .navbar-brand:focus {
    color: #ebebeb;
    background: #173738;
  }

  .navbar-inverse .navbar-nav > li > a, .navbar-inverse .navbar-nav > li > a:focus {
    color: #ebebeb;
    background: #173738;
  }

  .navbar-inverse .navbar-brand:hover, .navbar-inverse .navbar-brand:focus {
    color: #ebebeb;
    background: #173738;
  }

  .navbar-inverse .navbar-nav > li > a:hover, .navbar-inverse .navbar-nav > li > a:focus {
    color: #ebebeb;
    background: #173738;
  }

  .btn-primary, .btn-primary:hover {
    color: #fff;
    background-color: #23696C;
    border-color: #23696C;
  }

  .pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
    color: #23527c;
    background-color: #63ABAE;
  }

  .pagination > li > a, .pagination > li > span, .pagination > li > a:focus, .pagination > li > span:focus {
    background-color: #D9EDF7;
    border: 0px;
  }

  .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    background-color: #D9EDF7;
  }

  .pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
    background-color: #D9EDF7;
  }

  .navbar-inverse .navbar-nav > .open > a, .navbar-inverse .navbar-nav > .open > a:hover, .navbar-inverse .navbar-nav > .open > a:focus {
    background-color: #173738;
    color: #ebebeb;
  }

  .navbar-inverse .navbar-nav > .active > a, .navbar-inverse .navbar-nav > .active > a:hover, .navbar-inverse .navbar-nav > .active > a:focus {
    color: #fff;
    background-color: #173738;
  }

  .navbar-nav > li > .dropdown-menu {
    background: #D9EDF7;
  }

  .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    background-color: #D9EDF7;
  }

  kbd {
    background-color: #23696C;
    box-shadow: none;
  }
    
    
}
