css:
@-moz-document url-prefix("http://www.nyaa.se/"), url-prefix("http://sukebei.nyaa.se/") {

a{
color:#0F3C3E!important;
text-decoration:none;
}

a:hover{
color:#44A0A0;
text-decoration:underline;
}

body{
background:url(http://i.imgur.com/Yx7aMYq.jpg);
background-color:#1D7174!important;
font-family:'segoe ui'!important;
margin:0;
min-width:1000px;
padding:0;
}

form{
margin:0;
padding:0;
}

img{
border:0;
}

input, select, textarea{
font-family:'segoe ui'!important;
}

label{
cursor:pointer;
}

#main{
margin-left:auto;
margin-right:auto;
padding-bottom:4px;
padding-top:45px;
width:1000px;
}

#navbar{
float:left;
text-align:left;
width:45%;
}

#searchcontainer{
float:right;
padding:7px 0px 7px 0px;
text-align:left;
width:55%;
}

#tabnav{
float:right;
font-size:0.8em;
height:40px;
margin:0px 12px 0px 0px;
padding:0;
}

#tabnav a{
/* top part text color */
color:#fff!important;
float:left;
line-height:19px;
padding:12px 9px 8px 9px;
}

#tabnav a:hover{
text-decoration:underline;
}

#tabnav li{
display:inline;
list-style-type:none;
margin:0;
padding:0;
}

#topbar{
background:url(http://i.imgur.com/Yx7aMYq.jpg)!important;
background-color:#0F3C3E!important;
height:40px;
position:fixed;
width:100%;
}

.content{
background:url(http://i.imgur.com/Yx7aMYq.jpg);
background-color:#A1DADF!important;
border:2px solid #0F3C3E!important;
border-radius:5px;
float:left;
font-family:'Segoe UI'!important;
font-size:0.8em;
margin-bottom:6px;
padding-bottom:4px;
padding-top:2px;
width:87%;
}

.aplus{
background:url(http://i.imgur.com/Yx7aMYq.jpg);
background-color:#7E79C9!important;
}

.hidden{
background-color:#C0C0C0;
}

.remake{
background-color:#CF5B5B!important;
}

.str{
display:none;
}

.trusted{
background:url(http://i.imgur.com/Yx7aMYq.jpg);
background-color:#79C998!important;
}

.page{
border:1px solid #0F3C3E;
padding:0.25em;
}

.pagelink:hover{
background-color:#339999;
border:1px solid #333333;
color:white;
text-decoration:none;
}

.leftpages{
text-align:right;
width:5.6%;
}

.pages{
text-align:center;
width:88.8%;
}

.rightpages{
text-align:left;
width:2%;
}

.leftpages, .pages, .rightpages{
float:left;
margin-bottom:8px;
margin-top:4px;
}

.refinesearch{
line-height:2em;
margin-left:8px;
margin-top:4px;
}

.torrentcats{
padding-left:6px;
width:70%;
}

.torrentsubcatlist{
padding-left:18px;
padding-top:4px;
}

.notice{
font-weight:bold;
padding:11px;
}

.eyecatch{
color:red;
font-weight:bold;
}

.tlist{
border:0;
border-collapse:collapse;
margin-bottom:4px;
margin-left:auto;
margin-right:auto;
table-layout:fixed;
width:100%;
}

.tlistsortorder{
padding:8px;
}

.tlisthead{
font-weight:bold;
text-align:left;
vertical-align:top;
}

.tlistthone{
width:10%;
}

.tlistthtwo{
width:61%;
}

.tlistththree{
width:20px;
}

.tlistsizehead{
text-align:center;
width:8%;
}

.tlistthfive{
width:5%;
}

.tlistthsix{
width:5%;
}

.tlistthseven{
width:5%;
}

.tlisttheight{
width:4%;
}

.tlistrow{
border-top:1px solid #0F3C3E!important;
}

.tlisticon{
padding:5px 2px 5px 2px;
}

.tlisticon img{
display:block;
}

.tlistname{
font-weight:normal!important;
overflow:hidden;
padding-bottom:4px;
padding-top:4px;
word-wrap:break-word;
}

.tlistdownload{
padding-top:4px;
}

.tlistsize{
border-left:1px solid #0F3C3E!important;
border-right:1px solid #0F3C3E!important;
text-align:center;
}

.tlistfailed{
font-weight:600!important;
text-align:center;
}

.tlistsn{
color:green;
font-weight:600!important;
padding-left:4px;
}

.tlistln{
color:#E90605!important;
font-weight:600!important;
}

.tlistdn{
font-weight:600!important;
}

.tlistmn{
color:navy;
font-weight:600!important;
text-align:center;
}

.chatbox{
height:400px;
overflow:auto;
}

.chatboxinner{
padding:4px;
}

.deletebutton{
background-color:white;
border:0;
}

.manageheader{
font-size:1.2em;
font-weight:bold;
text-align:center;
}

.manage{
margin:0px 20px 0px 20px;
}

.container{
padding:10px;
}

.viewtop{
border:0;
border-collapse:collapse;
width:100%;
}

.viewtable{
border:0;
border-collapse:collapse;
table-layout:fixed;
width:100%;
}

.viewcategory{
font-weight:bold;
text-align:left;
}

.viewflagtorrent{
font-weight:bold;
text-align:right;
}

.viewtorrentname{
overflow:hidden;
word-wrap:break-word;
}

.viewmisc{
float:left;
width:70%;
}

.viewdownloadbutton{
float:right;
margin-top:2px;
padding-bottom:5px;
padding-top:10px;
text-align:right;
width:20%;
}

.viewdownloadtxtbutton{
float:right;
margin-top:2px;
padding-bottom:5px;
padding-top:10px;
text-align:right;
width:7%;
}

.viewshowhidefiles{
font-style:italic;
font-weight:bold;
}

.thead{
font-weight:bold;
padding-left:2px;
text-align:left;
vertical-align:top;
width:10%;
}

.viewthtwo{
width:60%;
}

.viewthfour{
width:20%;
}

.viewsn{
color:green;
font-weight:600!important;
}

.viewln{
color:red;
font-weight:600!important;
}

.viewdn{
font-weight:600!important;
}

.vtop{
vertical-align:top;
}

.viewdescription{
background-color:white;
border:2px solid #0F3C3E!important;
border-radius:5px;
padding:10px;
word-wrap:break-word;
}

.viewfiletable{
background-color:white;
border:2px solid #0F3C3E!important;
border-radius:5px;
width:100%;
}
.fileentryname{
border-bottom:2px dotted #0F3C3E!important;
float:left;
min-height:1.3em;
overflow:hidden;
padding-left:10px;
width:747px;
word-wrap:break-word;
}

.fileentrysize{
border-bottom:2px dotted #0F3C3E!important;
min-height:1.3em;
width:12%;
}

.comment{
background-color:white;
border:2px solid #0F3C3E!important;
border-radius:5px;
margin-top:4px;
padding:4px;
width:auto;
}

.commentoutline{
background-color:white;
border:3px solid #339999;
border-radius:5px;
margin-top:4px;
padding:4px;
width:auto;
}

.chead{
float:left;
width:25%;
}

.cnumber{
float:right;
font-weight:bold;
text-align:right;
width:3%;
}

.cmain{
clear:both;
padding:4px;
word-wrap:break-word;
}

.avatar{
float:left;
width:7%;
}

.avatarstaff{
float:left;
width:37%;
}

.avatarnyaa{
float:left;
width:37%;
}

.commentform{
height:100px;
width:600px;
}

.commentformdiv{
text-align:center;
}

.spoiler, .quote{
background-color:white;
border:2px solid #0F3C3E!important;
border-radius:5px;
padding:10px;
}

.quoteheader{
font-style:italic;
font-weight:bold;
}

.plink{
padding-left:6px;
}

.pedit{
padding-left:6px;
}

.error{
color:red;
font-weight:bold;
padding:8px;
}

.center{
margin:auto;
text-align:left;
width:50%;
}

.flagcontent{
padding-left:6px;
padding-top:4px;
}

.ulform{
padding:4px 0px 6px 6px;
}

.ulformtable{
border:0;
border-collapse:collapse;
padding:0;
}

.manageformtable{
border:0;
border-collapse:collapse;
padding:0;
}

.inputmanagename{
font-weight:bold;
vertical-align:top;
width:140px;
}

.inputmanagetext{
width:600px;
}

.inputmanagetextarea{
height:200px;
width:600px;
}

.inputmanagesubmitter{
width:60px;
}

.inputmanagereason{
width:150px;
}

.inputulname{
font-weight:bold;
vertical-align:top;
width:125px;
}

.inputultext{
width:400px;
}

.inputulselect{
width:404px;
}

.inputultextarea{
height:200px;
width:400px;
}

.inputulrules{
padding-bottom:4px;
width:400px;
}

.inputsearchterm{
width:140px;
}

.inputsearchcategory{
width:140px;
}

.inputsearchsubmit{
margin-left:3px;
width:67px;
}

.inputptext{
width:500px;
}

.inputchatboxmsg{
width:600px;
}

.inputflagreason{
height:150px;
width:300px;
}

}
