css:
@-moz-document url(https://animeforums.net/){
li.node.forum{width: 135px;display:table-cell;}
.nodeInfo.unread .nodeTitle:after{margin-left:0px;left:0px;top:20px;position:absolute;}
blockquote{display:none;}
.nodeText{position:absolute;}
.node .subForumList{display:none;position: absolute; top: 72px; width: 232px; height: auto; left:10px;z-index:9999;border:none;background: rgba(68, 50, 102,.95) !important;}
.node .subForumList li{width: 100px !important;}
.node .nodeLastPost{left: 10px;display:none;z-index:9998;}
.node .nodeText{margin-right: 12px;margin-left:12px;}
.node .nodeInfo{overflow:visible;}
.nodeText .nodeTitle a{text-shadow: .5px .5px #000000;}
.node .nodeText{width: 135px;}

li.node.forum:hover .nodeLastPost{display: inline; top: 25px;}
li.node.forum:hover .subForumList{display: inline;}
li.node.forum:hover .nodeIcon{opacity: .4;}
li.node.forum:hover .nodeText{opacity: .7;}

.xbnodeStatsContainer{display:none;}
.node .nodeLastPost {
  background-color: rgb(58, 58, 58);
  padding: 10px;
  border-bottom: 1px solid rgb(35, 35, 35);
  font-size: 12px;
  padding: 3px 10px;
  margin: 10px;
  border: 1px solid rgb(35, 35, 35);
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  line-height: 14px;
  white-space: nowrap;
  word-wrap: normal;
  overflow: hidden;
  width: 210px;
  height: 38px;
}
.node_67 > div:nth-child(1) > div:nth-child(2) > h3:nth-child(1) > a:nth-child(1),
li.level_2:nth-child(6) > div:nth-child(1) > div:nth-child(2) > h3:nth-child(1) > a:nth-child(1),
.node_25 > div:nth-child(1) > div:nth-child(2) > h3:nth-child(1) > a:nth-child(1) {visibility: hidden;}
.node_67 > div:nth-child(1) > div:nth-child(2) > h3:nth-child(1) > a:nth-child(1):after{content: "Digimon";visibility: visible;position:absolute;top:0px;left:0px;}
li.level_2:nth-child(6) > div:nth-child(1) > div:nth-child(2) > h3:nth-child(1) > a:nth-child(1):after{content: "Japanese";visibility: visible;position:absolute;top:0px;left:0px;}
.node_25 > div:nth-child(1) > div:nth-child(2) > h3:nth-child(1) > a:nth-child(1):after{content: "Feedback";visibility: visible;position:absolute;top:0px;left:0px;}

.node .subForumList{left: -65px !important;}
.node .nodeLastPost{height: 28px !important;}

.FloatingContainer .floatingItem .DismissCtrl{display:none;}
/*[[width]]*/
/*[[suffix]]*/
.triangle-border.left:before{top: 12px; left: -27px; border-width: 13px 27px 13px 0;}
.triangle-border{border: 3px solid #cccccc;}

#content > div > div > div.mainContainer > div > div.FloatingContainer.Notices > div > div {
    left: 9999px;
}
}
/*[[smilies]]*/
