css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.fastmail.com") {
  
  .app-toolbar > .v-Toolbar-section > .v-Button {
    background-color: #272822;
    border-color: #fff;
    color: #fff;
    text-shadow: 0 -1px 0 #222;
  }
  
  .app-toolbar > .v-Toolbar-section > .v-Button:hover {
    background: none repeat scroll 0 0 #3C3D38;
    border-color: #fff;
  }
  
  .app-sidebar { background-color: #272822; }
  
  .app-toolbar .ButtonView { background-color: #272822; border-color: #CCC; }
  .v-FolderSource-badge { background-color: #C1DB3F; color: black; }
  
  .ListKBFocusView { background: none repeat scroll 0 0 rgba(249, 38, 102, 0.5); }
  
  .MainNavToolbar:hover { background-color: #3C3D38; }
  .app-belowToolbar { background-color: #272822; }
  
  .app-listItem { background-color: #272822; color: #F8F8F2; }
  .app-main .app-below-toolbar { background: none repeat scroll 0 0 #393A31; }
  .app-viewBar { color: #fff; } 
  .mailbox { background-color: #393A31; }
  
  .app-toolbar { background-color: #1D1D1E; }
  .app-source { color: white; }
  .app-source:hover { background-color: #3C3D38; }
  .app-source > .icon { color: #A6A6A6; }
  .app-source.is-selected { background-color: #3C3D38; border-color: #d0d0d0; }
  
  h1.v-Thread-title { color: #EFEFEF; }
  .app-view-bar { color: #EFEFEF; }
  .v-Sidebar-editLink { color: #EFEFEF; }
  
  .v-UsageInfo { color: #272822; }
  .app-listItem.is-selected { background-color: #494F5B; }
}
