/* AGENT_SHEET */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.bidari.org/menu.htm) {
@media (max-width: 1300px) {    
html, body, p, span {
    font-size: 75% !important;
    }
}    
}

@-moz-document url(http://www.bidari.org/contactmain.htm) {
html, body, p, span, i {
    background: #FFFFFF !important;

    font-family: "Arial" !important;
    font-weight: normal !important;
    font-size: 70% !important;
    color: #000000 !important;
    line-height: 40% !important;
    margin-top:20px !important;
    margin-left:10px !important;
    direction: ltr !important;
    text-align: left !important;

    }

p span b i {
    font-size: 100% !important;
    position:absolute !important;
    margin-top:-30px !important;
    margin-left:0px !important;
    }
}


@-moz-document url(http://www.bidari.org/credits.htm) {
html p {
    font-family: "Arial" !important;
    font-weight: normal !important;
    font-size: 100% !important;
    color: #000000 !important;
    line-height: 40% !important;
    margin-left:40px !important;
    direction: ltr !important;
    text-align: left !important;
    }
}



@-moz-document domain("www.bidari.org") {


font, body, p, #de72970, a, h4 {
    background: none !important;
    font-family: "IRLotus", "B Lotus", "Far.Lotus", "Traditional Arabic" !important;
    font-weight: normal !important;
    font-size: 115% !important;
    color: #000000 !important;
    direction: rtl !important;
    text-align: right !important;
}

.MsoNormal {
    font-size: 180% !important;
}

    
a:visited {
    color:purple !important;
}
a:hover {
    color:red !important;
}    
a:active, a:link {
    color: blue !important;
}


}
