css:
@-moz-document url-prefix("https://keep.google.com") {

/* Background color of page and main body (top section) */
html, body /*, .RfDI4d-bN97Pc*/
{
    background-color: #111111 !important;
}

/* java script to remove placeholder text in search box. Can't do with user style alone (greasemonkey needed) /*
/* document.getElementById("gbqfq").placeholder = ""; */

/* Replace lightbulb with yellow bulb */
.neVct-Ne3sFf-Bz112c
{ background-image: url('data:image/svg+xml;base64,') !important;
}

/* color text below lightbulb */
.neVct-Ne3sFf-fmcmS
{
  color: #fb0 !important;
}

/* Lighten shadow on hover of any note */
.ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-n0tgWb:hover .IZ65Hb-TBnied
{
    box-shadow: 0px 2px 15px #999999 !important;
}

/* remove slideout menu dividers */
.gb_oc
{
    border: none !important;
}

/* slideout menu icons */
.gb_pc .gb_uc
{
   color: white !important;
}

/* slideout menu section labels */
.gb_ic, .Q0hgme-fmcmS-LgbsSe
{
    color: white !important;
}
}
