css:
@-moz-document url-prefix("http://tjournal.ru/") {
@media screen and (min-width: 1200px){.container { max-width: 1800px; width: auto; } }
  
@-webkit-keyframes rainbow {
  0%   { background: #ff5b5b; } 
  14%  { background: #ff7f00; }
  29%  { background: #d0d000; }
  43%  { background: #009000; }
  57%  { background: #309ace; }
  71% { background: #0000ff; }
  85% { background: #8b00ff; }
  100% { background: #ff5b5b; }
}
  
@-moz-keyframes rainbow {
  0%   { background: #ff5b5b; } 
  14%  { background: #ff7f00; }
  29%  { background: #d0d000; }
  43%  { background: #009000; }
  57%  { background: #309ace; }
  71% { background: #0000ff; }
  85% { background: #8b00ff; }
  100% { background: #ff5b5b; }
}
  
body {-webkit-animation: /*[[rainbow]]*/ 60s infinite linear; animation: /*[[rainbow]]*/ 60s infinite linear; background: /*[[color]]*/ !important;}
.b-block a:hover {color: #33b0ff;}
.up {width: 30px;}
.up i{opacity: 0.8;}
.b-articles__b_t8 {display: inline-table; width: 498px;height: 125px;border: 1px solid #edeff1 !important; -webkit-border-radius: 6px 6px 0 0; -moz-border-radius: 6px 6px 0 0; border-radius: 6px; margin: 5px 2px; padding: 21px 27px;}
.b-articles__b_t8 .b-articles__b__title__img{box-shadow: none; border-radius: 8px; height: 70px; width: 70px; left: 0;}
.b-articles__b_t7, .b-articles__b_t5, .b-articles__b_t1 {text-align: center; width: 1055px; border: 1px solid #edeff1;-webkit-border-radius: 6px 6px 0 0; -moz-border-radius: 6px 6px 0 0; border-radius: 6px; margin: 10px auto;}
.b-articles__b__image img {-webkit-border-radius: 6px 6px 0 0;-moz-border-radius: 6px 6px 0 0;border-radius: 6px;}
.b-menu__li:hover a {color: #FFF !important;}
.b-block-main-articles>.b-articles>.b-articles__b:first-child{border-top: 1px solid #edeff1;}
}
