css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("next-episode.net") {

    body {
        background:hsl(0,0%,0%)!important;
    }
    .main_table {
        border-color:hsl(0,0%,100%)!important;
        box-shadow: 0 0 15px 5px hsl(0,0%,0%);
        background:black!important;
    }

    .pickAShow a {
        border: 1px solid hsl(0,0%,0%)!important;
        background: hsl(0,0%,0%)!important;
    }

    .td4 {
        background:hsl(0,0%,0%)!important;
    }
    #header .td4 a[href="http://next-episode.net"] img,
    #header td[rowspan="2"] a[href="http://next-episode.net"] img {
        display:none!important;
    }
    #header .td4 a[href="http://next-episode.net"]:after {
        content:"next-episode.net";
        color:#000!important;
        font: bold 2em Courier New!important;
        margin-right:2em;
    }
    #header .td4 a[href="http://next-episode.net"] {
        text-shadow:
            -3px -3px 1px hsl(0,0%,0%), /*up left*/
            0px -3px 1px hsl(0,0%,0%), /*up*/
            3px -3px 1px hsl(0,0%,0%), /*up right*/
            3px 0px 1px hsl(0,0%,0%), /*right*/
            3px 3px 1px hsl(0,0%,0%), /*down right*/
            0px 3px 1px hsl(0,0%,0%), /*down*/
            -3px 3px 1px hsl(0,0%,0%), /*down left*/
            -3px 0px 1px hsl(0,0%,0%); /*left*/
            text-decoration:none!important;
    }
    #header .td4 a[href="http://next-episode.net"]:hover {
        text-shadow:
            -3px -3px 1px hsl(0,0%,0%), /*up left*/
            0px -3px 1px hsl(0,0%,0%), /*up*/
            3px -3px 1px hsl(0,0%,0%), /*up right*/
            3px 0px 1px hsl(0,0%,0%), /*right*/
            3px 3px 1px hsl(0,0%,0%), /*down right*/
            0px 3px 1px hsl(0,0%,0%), /*down*/
            -3px 3px 1px hsl(0,0%,0%), /*down left*/
            -3px 0px 1px hsl(0,0%,0%); /*left*/
    }

    .footer a,
    .tdc1 a,
    a.remove,
    .footer3 a,
    .footer9 a,
    a[style="color:#5f91c4"],
    .input2 {
        color: hsl(0,0%,0%)!important;
    }

    a.showName,
    a.episodeinfo,
    p.legend,
    .footer3,
    .footer,
    .nextEpInfo {
        color: hsl(0,0%,0%)!important;
    }

    *[style="color: #5F91C4"] {
        color:hsl(0,0%,0%)!important;
    }

    .footer_real {
        background-color: hsl(0,0%,0%)!important;
        border-top: 1px solid hsl(0,0%,0%)!important;
    }

    tr[style="font-weight: bold; background-color: #5f91c4; color: #ffffff;"] * {
        background:hsl(0,0%,0%)!important;
        color:#fff!important;
    }

html > div {display:none!important;}

    table#settings td.leftCol,
    table#settings td.leftCol ul li a,
    table#settings td.leftCol ul li a:hover,
    table#settings td.leftCol ul li a.active,
    table#settings td.leftCol h5 {
        background:black!important;
        font-weight:bold!important;
    }

    table#settings td.leftCol ul li {
        border-bottom:none!important;
    }

    table#settings td.leftCol h5 {
        font-size: 16px!important;
        color: hsl(0,0%,0%)!important;
    }

    table#settings td.leftCol ul li a:hover {
        text-decoration:underline!important;
    }
    
    table#settings td.leftCol ul li a.active {
        font-style:italic;
    }
   

}
