css:
@-moz-document url-prefix("http://tjournal.ru/") {

body {
background:#eaeaea;
}
.b-menu {
border: 1px solid rgba(0, 0, 0, 0.2);
}
.b-menu__li {
border-top: 1px solid rgba(0, 0, 0, 0.2);
}
.b-menu__li a {
color:#616161;
}
.b-userblock__wrapper {
background: rgba(0, 0, 0, 0.03);
}
.b-userblock__name {
text-shadow: 0px 0px 0 rgba(0, 0, 0, 0.0);
}
.b-userblock__name a {
color: #565656;
}
.b-userblock__exit a {
color:#56566;
opacity:1;
}
.b-userblock__exit a u {
border:0;
color:#56566;
}
.up {
background: rgba(0, 0, 0, 0.03);
}
.b-search {
background: none repeat scroll 0 0 rgba(0, 0, 0, 0.03);
}
.b-search__icon {
opacity:1;
}
.up i {
opacity:1;
}
.b-search__form input::-webkit-input-placeholder {color:#fff;}
.b-search__form input::-moz-placeholder          {color:#fff;}/* Firefox 19+ */
.b-search__form input:-moz-placeholder           {color:#fff;}/* Firefox 18- */
.b-search__form input:-ms-input-placeholder      {color:#fff;}
.b-adv-300x500__wrapper {
background: rgba(0, 0, 0, 0.03);
}
.icon-logo:before {
background: #FFCC00;
border-radius: 13%;
border: 3px solid #FFCC00
}
.b-head__actions a {
background: rgba(0, 0, 0, 0.03);
}

}
