	/* Fira Sans font
	************************************************/
	/* cyrillic */
	@font-face {
		font-family: 'Fira Sans';
		font-style: normal;
		font-weight: 500;
		src: local('Fira Sans Medium'), local('FiraSans-Medium'), url(https://fonts.gstatic.com/s/firasans/v5/zM2u8V3CuPVwAAXFQcDi4JX5f-9o1vgP2EXwfjgl7AY.woff2) format('woff2');
		unicode-range: U+0400-045F, U+0490-0491, U+04B0-04B1, U+2116;
	}
	/* latin */
	@font-face {
		font-family: 'Fira Sans';
		font-style: normal;
		font-weight: 500;
		src: local('Fira Sans Medium'), local('FiraSans-Medium'), url(https://fonts.gstatic.com/s/firasans/v5/zM2u8V3CuPVwAAXFQcDi4Ogdm0LZdjqr5-oayXSOefg.woff2) format('woff2');
		unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
	}
	/* cyrillic */
	@font-face {
		font-family: 'Fira Sans';
		font-style: normal;
		font-weight: 700;
		src: local('Fira Sans Bold'), local('FiraSans-Bold'), url(https://fonts.gstatic.com/s/firasans/v5/DugPdSljmOTocZOR2CItOpX5f-9o1vgP2EXwfjgl7AY.woff2) format('woff2');
		unicode-range: U+0400-045F, U+0490-0491, U+04B0-04B1, U+2116;
	}
	/* latin */
	@font-face {
		font-family: 'Fira Sans';
		font-style: normal;
		font-weight: 700;
		src: local('Fira Sans Bold'), local('FiraSans-Bold'), url(https://fonts.gstatic.com/s/firasans/v5/DugPdSljmOTocZOR2CItOugdm0LZdjqr5-oayXSOefg.woff2) format('woff2');
		unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
	}
	
	
	/* Variables
	************************************************/
	body {
		--contentWidth: 700px;
		--contentFont: 'PT Sans', sans-serif;
		--titleFont: 'Fira Sans', sans-serif;
	}
	/***********************************************/
			
	body:not(.slided) {overflow-y: scroll !important}
	
	.proShowcase,
	#realtimeSign,
	#tipsModule_part,
	#discoverModule_part,
	
	.headerInfo-links.right,
	.metadata > span.meta,
	
	.u0Entry .recommendationInfo,
	.u0Entry > .condensedTools > a,
	
	aside,
	.content img + br + br,
	.content img + br + br + br
	{
		display: none !important;
	}
	
	
	/* Menu */
	#feedlyTabsHolder,
	#feedlyTabs {
		max-height: 100% !important;
	}
	#feedlyTabsHolder {
		box-sizing: padding-box !important;
		padding-bottom: 51px !important;
	}
	#feedlyTabsHolder:hover,
	div.simpleUnreadCount:hover {
		background-color: transparent !important;
	}
	div.simpleUnreadCount {
		cursor: default !important;
	}
	#feedlyTabs > div:last-child,
	#feedlyTabs > div:last-child > div[style*='margin-bottom: 80px'] {
		margin: 30px 0 10px !important;
	}
	.feedIndex:hover .feedTitle {
		color: #dc3c00;
	}
	#feedlyTabs .selected,
	#feedlyTabs .selected .feedTitle {
		color: #000 !important;
		font-weight: bold;
	}

	
	/* Titles */
	#feedlyTitleBar, h2,
	.entryHeader .title {
		font-family: 'Fira Sans', sans-serif !important;
		font-family: var(--titleFont) !important;
		font-weight: 700 !important;
		margin-left: -0.04em !important;
		line-height: 1 !important;
	}
	#feedlyTitleBar {
		color: #111;
		font-size: 2.2em;
		font-weight: 400 !important;
	}
	h2 {
		font-weight: 400 !important;
	}
	#feedlyTitleBar .hhint {
		font-size: 0;
	}
	#feedlyTitleBar .hhint > span {
		font-size: 13px;
	}
	.hhint > .categoryUnreadCountHint {
		margin-right: 10px;
		font-style: italic;
		color: rgba(0,0,0,.8);
	}
	.read {color: #000}
	.unread {color: #753}
	
	
	/* Popup */
	#popupPart > .popup {
		border: 1px solid rgba(0,0,0,.3);
		box-shadow: 0 20px 200px white,
			          0 3px 10px rgba(0,10,5,.2)
	}
	#popupPart > .popup .selectedItem {
		background-color: rgba(0,50,30,.07);
	}
	
	
	/* Action button */
	.websiteCallForAction {
		border: 1px solid rgba(0,0,0,.2);
		border-radius: 3px !important;
	}
	.websiteCallForAction:hover,
	.secondaryCallForAction:hover {
		background-color: transparent;
		border: 1px solid rgba(0,0,0,.3);
		color: rgba(0,0,0,.7);
	}
	.websiteCallForAction:active,
	.secondaryCallForAction:active {
		color: rgba(0,0,0,.8);
		background-color: rgba(0,30,20,.08);
		box-shadow: inset 0 0 3px rgba(0,0,0,.1);
		transition: none !important;
	}
	
	
	/* Homepage */
	#feedlyPageHeader {min-width: 500px !important}
	#feedlyPart0 {box-sizing: border-box}
			
			
	/* Feed */
	#timeline > .section:not(:first-child) {
/* 		border-top: 2px dashed #ddd !important; */
	}
	
	
	/* List
	-------------------------------------*/
	.u0Entry {
		padding-left: 0;
		border: 0 solid rgba(0,0,0,.1);
		border-bottom-width: 1px;
	}
	.u0Entry:first-child {
		border-top-width: 1px;
	}
	.u0Entry:hover {
		background-color: rgba(0,50,30,.07) !important;
	}
	.u0Entry.quicklisted {
		background-color: rgba(160,120,0,.07);
	}
	.u0Entry .title:not(.unread) {
		color: rgba(0,0,0,.7);
	}
	.u0Entry:not(:hover) .u0Summary {
		opacity: .7;
	}
	.u0Entry .quicklistHandle {
		background-size: 29px;
	}
	.u0Entry > .condensedTools {
		width: auto;
	}
	.u0Entry > .condensedTools > img {
		width: 28px; height: 28px;
		margin-top: 3px;
	}
	
	
	/* Cards
	-------------------------------------*/
	.u5Entry {
		position: relative !important;
		background: transparent;
		border: none !important;
		border-radius: 4px !important;
		box-shadow: inset 0 0 0 1px rgba(0,0,0,.16);
		overflow: hidden;
	}
	.u5Entry:hover {
		box-shadow: 0 0 0 4px rgba(0,30,20,.1),
			    inset 0 0 0 1px rgba(0,0,0,.24),
			          0 1px 1px rgba(0,10,5,.1);
	}
	.u5Entry:active {
		box-shadow: 0 0 0 1px rgba(0,0,0,.24),
			          0 1px 2px rgba(0,10,5,.2),
			          0 0 0 5px #fb0;
	}
	@-moz-keyframes return-back-highlight {
		0%   {background: rgba(250,230,190,.7)}
		100% {background: rgba(255,255,255,0)}
	}
	.u5Entry[style*='background-color'] {
		animation: return-back-highlight 1.5s;
	}
	.u5EntryAnnotationHolder {
		background: #eee !important;
		position: relative;
		z-index: -1;
	}
	.u5Entry .visual {
		border-radius: 0 !important;
	}
	.u5Entry .visual.playable {
		background-size: 120px, 120%;
		background-position: center 24px, center;
	}
	.u5Entry .title {
		font: 500 17px 'Fira Sans', sans-serif !important;
		font: 500 17px var(--titleFont) !important;
		line-height: 1 !important;
		height: auto !important;
		max-height: 5em !important;
		margin-bottom: 13px !important;
	}
	.u5Entry .title:after {
		content: '';
		display: block;
		position: absolute;
		top: 0; left: 0;
		width: 100%; height: 100%;
	}
	.u5Entry .wikiBar {
		position: relative !important;
	}
	.u5Entry span.action,
	.u5Entry span.action + span {
		visibility: visible;
		opacity: .5;
		transition: opacity .5s;
	}
	.u5Entry:hover span.action,
	.u5Entry:hover span.action + span {
		opacity: 1;
		transition: none;
	}
	.u5Entry span.action + span {font-size: 0}
	.u5Entry span.action + span:after {
		content: '|';
		font-size: 1rem;
	}
	
	
	/* Articles
	-------------------------------------*/
	.u100Frame {
		padding: 0 0 20px 0 !important;
		margin-bottom: 50px !important;
		border: none !important;
		border-bottom: 2px dashed #ddd !important;
		cursor: default !important;
	}
	.u100Entry {
		max-width: 700px !important;
		max-width: var(--contentWidth) !important;
		margin-bottom: 0 !important;
	}
	
	
	/* entryHeader
	-------------------------------------*/
	.entryHeader .shareBarHolder {
		position: fixed;
		top: 0;
		right: 16px;
		margin: 0;
	}
	.entryHeader .shareBarHolder > .headerInfo {
		height: 0 !important;
		padding: 0 !important;
		margin: 0 !important;
		border: 0 !important;
	}
	.entryHeader .shareBarHolder .headerInfo-links {
		margin: -1px 0 0 -10px !important;
	}
	.entryHeader .shareBarHolder > 
		.headerInfo img:not(:first-child) {
			display: none;
	}
	.u100Frame .headerInfo.headerInfo-article {
		float: left !important;
		border: none !important;
		margin: 5px 15px 0 0 !important;
	}
	.u100Frame .websiteCallForAction {
		float: left !important;
		clear: right !important;
		margin-top: 13px !important;
		padding: 8px 12px 7px !important;
	}
	.entryHeader .title {
		font-size: 3em !important;
	}
	.entryHeader .title:hover {
		text-decoration: underline !important;
	}
	.entryHeader .title.read {color: black}
	
	/* metadata */
	.entryHeader .metadata {
		display: inline-block;
		font-size: 0;
		color: #777;
	}
	.entryHeader .metadata > *:not(:empty) {
		font-size: 12px;
	}
	.entryHeader .metadata .sourceTitle {
		font-weight: bold;
		color: #095;
	}
	.entryHeader .metadata >
		span[title^='published'] {
			margin-left: 5px;
			color: black;
	}
	.entryHeader .metadata > .action:hover {
		color: #dc3c00;
		text-decoration: none;
	}
	.entryHeader .metadata > .action:before {
		content: '|';
		padding: 0 7px;
		color: rgba(0,0,0,.2);
	}

	
	/* entryBody
	-------------------------------------*/
	.entryBody {
		max-width: 700px !important;
		max-width: var(--contentWidth) !important;
		margin-top: 0;
		clear: both;
		color: #222;
		font-size: 18px !important;
		font-family: 'PT Sans', sans-serif !important;
		font-family: var(--contentFont) !important;
	}
	.entryBody img {
		border: none !important;
	}
	.entryBody > iframe,
	.entryBody > .content:not(:empty) {
		margin-top: 2em !important;
	}
	.entryBody > .content > *:first-child {
		margin-top: 0 !important;
	}
	.entryBody > .content img,
	.entryBody > .content table {
		width: auto !important;
		max-width: 100% !important;
		height: auto !important;
		margin-top: 0 !important;
	}
	.entryBody > .content > ul > li {
		line-height: 1.3;
		margin-bottom: .7em;
	}
	.entryBody > .content > ul:only-child {
		padding-left: 0;
	}
	.entryBody > .content > ul:only-child > li {
		line-height: inherit;
		margin-bottom: 1em;
	}
	.entryBody a {
		border-bottom: none !important;
	}
	.entryBody a:hover {
		color: #dc3c00 !important;
		text-decoration: underline;
	}
	.entryBody a:active {
		color: #c01 !important;
	}
	.entryBody a:link {color: #079}
	.entryBody a:visited {color: #039}
	
	.entryBody b {
		font-size: inherit !important;
		color: inherit !important;
	}
	
	
	/* floatingEntry
	-------------------------------------*/
	#floatingEntry .floatingEntryScroller {
		overflow-y: scroll !important;
	}
	#floatingEntry .floatingEntryContentSlide {
		width: 100%;
	}
	#floatingEntry .floatingEntryContentSlide > div {
		width: 700px;
		width: var(--contentWidth);
		margin: auto !important;
	}
	#floatingEntry .entryholder {
		min-height: 100vh !important;
		padding-bottom: 0 !important;
	}
	#floatingEntry .u100FeedHeader,
	#floatingEntry .u100FeedSummary-add {
		width: 700px;
		width: var(--contentWidth);
	}
	#floatingEntry .entryTitle {
		margin-top: -33px !important;
	}
	#floatingEntry .frameActionsTop {
		margin-top: 0 !important;
	}
	#floatingEntry .u100Entry {
		padding-bottom: 150px;
	}
	.u100Header-banner-info {margin-left: -35px}
	
	.shareBarFixedHolder .headerInfo {
		display: none !important;
		height: 50px !important;
		border-bottom: none;
		box-shadow: 0 1px 0 rgba(0,0,0,.15);
	}
	.shareBarFixedHolder .headerInfo .sliderWidth {
		width: 700px !important;
		width: var(--contentWidth) !important;
	}
	.shareBarFixedHolder .headerInfo-title {
		margin-left: -8px;
	}
	.shareBarFixedHolder .headerInfo-links > img:not(:first-child) {
		display: none !important;
	}
	.shareBarFixedHolder .headerInfo-primary {
		width: 580px !important;
		max-width: 580px !important;
	}
			
	#floatingEntry .websiteCallForAction {
		position: absolute !important;
		bottom: 25px !important;
		padding: 10px 16px 9px !important;
	}
	
	/* slider arrows */
	.bumperHolder {
		position: sticky;
		top: 50px !important;
		width: 100% !important;
		min-width: 100px !important;
		padding: 0 !important;
	}
	.slideBumper {
		width: calc((100% - 700px) / 2);
		width: calc((100% - var(--contentWidth)) / 2);
		min-width: 40px;
		margin: 0 !important;
		height: calc(100vh - 100px);
	}
	.slideBumper-left {
		top: 0;
		left: 0;
	}
	.slideBumper-right {
		left: auto;
		right: 1px;
	}
	.slideBumper div {
		box-sizing: border-box;
		margin: 0 !important;
		padding: 0 !important;
		background-color: transparent !important;
		background-position: 50% !important;
		border-radius: 0 !important;
		transition: none;
	}
	.slideBumper-left div:active {
		margin-left: -1px !important;
	}
	.slideBumper-right div:active {
		margin-left: 1px !important;
	}
			
	@media (max-width: 800px) {
		#floatingBar {display: none !important}
		.floatingEntryContent {width: 100% !important}
		.shareBarFixedHolder {display: none !important}
	}
	
}
