css:
@-moz-document url("http://www.z8games.com"), url-prefix("http://www.z8games.com"), domain("www.z8games.com") {
/* Z8 Games Modern Design */
/* By Drydareelin */
/*
/*
/*


/* Background */
body {
background: url(http://i.imgur.com/uzgDct1.jpg) top center no-repeat;
background-color: #000000;
font-family: "Arial Black", Gadget, sans-serif;
}

/* Top Bar */
.z8games-gnb ul {
background: url('http://i.imgur.com/Je4pmZM.png') no-repeat !important;
width: 1012px;
height: 26px;
}

/* Login boxes can't be changed */


/* Image(s) box */
.z8games .slides .header-slider div a img {
height: 250px;
width: 715px !important;
z-index: 500;
}

.z8games .slides .header-slider div {
position: absolute;
top: 0;
left: 0;
overflow: hidden;
height: 250px;
width: 715px !important;
border: 0px #313131 solid !important;
}

/* Remove Facebook box */
body.plugin {
display: none;
}

.z8games-gnb ul {
font: 11px "Arial Black", Gadget, sans-serif;
line-height: 30px;
}

body {
margin-left: 10px;
margin-top: 0px;
margin-right: 0px;
font-family: "Arial Black", Gadget, sans-serif;
font-size: 11px;
color: #cccccc;
padding-right: 20px;
padding-left: 20px;
}

}

@-moz-document url("http://bill.z8games.com"), url-prefix("http://bill.z8games.com"), domain("bill.z8games.com") {
/* Background */
body {
background: url(http://i.imgur.com/J66PjOh.png) top center no-repeat;
}

/* Top Bar */
.z8games-gnb ul {
background: url('http://i.imgur.com/Je4pmZM.png') no-repeat !important;
width: 1012px;
height: 26px !important;
}

/* My Z8 Points */
#col-right .z8-points .top {
width: 300px;
height: 43px;
background: transparent url('http://i.imgur.com/pnDLdEp.png') no-repeat 0 0;
text-align: left;
}
#col-right .z8-points .bottom {
width: 300px;
height: 6px;
background: transparent url('http://i.imgur.com/GFLYvMj.png') no-repeat 0 0;
}
/* Earn Free ZP box */

#col-right .zp-promo a.btn-banner {
background: transparent url('http://i.imgur.com/pPydiKm.png') no-repeat 0 -76px !important;
font-weight: normal;
}

#col-right .zp-promo a.btn-banner:hover {
background: transparent url('http://i.imgur.com/luCXlgQ.png') no-repeat 0 -76px !important;
background-color: rgba(0,0,0,0.1);
color: #000000;
font-weight: normal;
}

/* ZP Info */
#col-right .zp-info .top {
height: 6px;
background: transparent url('http://i.imgur.com/s52Rx9N.png') no-repeat 0 0;
}
#col-right .zp-info .bottom {
width: 300px;
height: 6px;
background: url('http://i.imgur.com/Y5Wfa0b.png') no-repeat 0 0;
}

/* Disposit Box */
#col-left .top {
height: 68px;
padding: 0 30px;
background: transparent url('http://i.imgur.com/IgGjAMa.png') no-repeat 0 0;
position: relative;
}
#col-left .bottom {
width: 715px;
height: 6px;
background: transparent url('http://i.imgur.com/BWV2mCF.png') no-repeat 0 0;
}
#payment-methods div.pg-section ul li a.payment-button {
display: block;
width: 168px;
height: 35px;
background: url('http://i.imgur.com/jbRnTIj.png') no-repeat 0 0;
position: relative;
z-index: 1;
}
#payment-methods div.pg-section ul li a.payment-info span {
display: inline-block;
width: 22px;
background: url('http://i.imgur.com/2nQb5G9.png') no-repeat 0 0;
}

/* Animation */
#zp-box-info div#zp-info-content {
background: rgba(70,70,70,0.8);
}
#zp-box-info div#zp-info-content:hover {
background: rgba(70,70,70,1);
}
}

