css:
@-moz-document domain("feedly.com") {
.headerInfo {
	background-color: #3a3a3a !important;
}

#feedlyPage div:nth-child(2) {
    background-color: #3a3a3a !important;
}

#feedlyPageHeader {
    padding-top: 0 !important;
}

#feedlyTabsHolder, #floatingBar, #storeHolder {
    -webkit-transform: translateZ(0) !important;
}

#floatingBar {
    background-attachment: scroll !important;
    background-color: #3a3a3a !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
}

#floatingBar h1 {
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
}

#searchBox, #proBanner, #feedlyMessageBar, #hercule {
    display: none !important;
}

#searchBoxPlaceholder {
    width: auto !important;
}

#sideArea a {
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
}

.area {
    background-attachment: scroll !important;
    background-color: #3a3a3a !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
    padding-top: 1em !important;
}

.condensedTools {
    -webkit-filter: invert() !important;
}

.entriesLoadingMessage {
    -webkit-filter: invert() !important;
}

.entryBody a:link, .entryBody a:hover, .entryBody A:visited {
    border-bottom-color: #ffff66 !important;
    color: #ffff66 !important;
}

.entryholder, .frameActionsTop {
    background-attachment: scroll !important;
    background-color: #414141 !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
}

.highlightableIcon:not(.toBeTagged) {
    -webkit-filter: invert() !important;
}

.inlineFrame {
  	padding-top: 33px !important;
  	padding-left: 0px !important;
  	padding-right: 0px !important;
    background-attachment: scroll !important;
    background-color: #414141 !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
}

.inlineFrame .u100Entry {
    background-attachment: scroll !important;
    background-color: #414141 !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
    padding: 15px !important;
}

.pageActionBar {
    background-color: #414141 !important;
}

.pageActionBar img {
    -webkit-filter: invert() !important;
}

.selectedEntry {
    background-attachment: scroll !important;
    background-color: #3a3a3a !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
    box-shadow: none !important;
}

.subscriptionContentsHolder {
    background-color: #3a3a3a !important;
}

.subscriptionContentsHolder .empty {
    color: #999 !important;
}

.subscriptionContentsHolder .nonEmpty {
    color: #fff !important;
}

.title.read {
    color: #999999 !important;
}

.topWikiWidget, .bottomWikiWidget {
    background-attachment: scroll !important;
    background-color: #e6e6e6 !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
    padding-bottom: 0 !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
    padding-top: 0 !important;
}

.u0Entry {
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
}

.u0Entry .lastModified::before {
    content: '|' !important;
    margin-right: 5px !important;
}

.u0Entry .quicklistHandle {
    background-attachment: scroll !important;
    background-color: transparent !important;
    background-image: url(http://s3.feedly.com/production/14.0/images/selector-saved-active.png ) !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-size: 30px 30px !important;
}

.u0Entry .title {
    font-weight: normal !important;
}

.u0Entry.quicklisted {
    background-color: inherit !important;
}

.u0Entry.selectedEntry {
    padding-bottom: 0 !important;
    padding-top: 0 !important;
}

.u0Entry:hover {
    background-attachment: scroll !important;
    background-color: #414141 !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
}

.u100Frame {
    box-shadow: none !important;
}

.u100entry {
    background-attachment: scroll !important;
    background-color: #3a3a3a !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
}

.u12Entry div:first-child {
    margin-bottom: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    margin-top: 0 !important;
}

.u4Entry .marginMaker {
    margin-bottom: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    margin-top: 0 !important;
}

.u4Entry .title {
    padding-bottom: 5px !important;
}

.u4entry {
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
}

.u4entry.selectedEntry {
    margin-bottom: 34px !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    margin-top: 0 !important;
    padding-bottom: 0 !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
    padding-top: 0 !important;
}

.u5entry {
    background-attachment: scroll !important;
    background-color: #414141 !important;
    background-image: none !important;
    background-position: top left !important;
    background-repeat: repeat !important;
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
    padding-bottom: 5px !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    padding-top: 5px !important;
}

.u5entry .title {
    margin-bottom: 10px !important;
}

.u5entry.selectedEntry {
    margin-bottom: 34px !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    margin-top: 0 !important;
}

.unread, #floatingBar h1, #feedlyTitleBar {
    color: #ffffff !important;
}

.viewerIcon.hi {
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: medium !important;
    border-left-color: -moz-initial !important;
    border-left-style: none !important;
    border-left-width: medium !important;
    border-right-color: -moz-initial !important;
    border-right-style: none !important;
    border-right-width: medium !important;
    border-top-color: -moz-initial !important;
    border-top-style: none !important;
    border-top-width: medium !important;
}

.websiteCallForAction {
    background-color: #333 !important;
}

.wikiWidget {
    background-color: #3a3a3a !important;
    border-bottom-color: -moz-initial !important;
    border-bottom-style: none !important;
    border-bottom-width: 0 !important;
}

body, h2, #sideArea a, #sideArea .feedIndexTitle, .entryBody h1, .entryBody h2, .entryBody h3, .entryBody h4, .entryBody h5, .entryBody h6.feedIndexTitle, .entryBody, .fusionAd div:first-of-type, .preferencesItemLabel, .preferencesItemDescription, .preferencesPanel label, .sourceTitle a:hover, .entryBody .summary b, .entryBody .content b, .entryBody td {
    color: #cccccc !important;
}

h1 .hhint {
    padding-top: 5px !important;
}
}
