@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/*--------------------THEME MADE BY larrystylnsen.tumblr.com AKA FAYE
DO NOT REDISTRIBUTE
DO NOT USE AS A BASE CODE
DO NOT TAKE PARTS OF MY THEME TO USE
FOLLOW THE RULES OR THERE WILL BE CONSEQUENCES

HTML CODES CREDIT TO hideheaders ON USERSTYLES (http://forum.userstyles.org/profile/98333/hideheader) AND FROM https://userstyles.org/styles/101559/pink-overload-tumblr-dash-theme
------------------------*/



body {

/*CREDIT TO https://www.flickr.com/photos/expeditions FOR THE PHOTO*/
  background: 
#fff url("http://41.media.tumblr.com/0f05f173bf4aa70ce7159492298e2a35/tumblr_n83nlvN4CP1s2rrw7o7_1280.jpg") top left fixed no-repeat !important; 
background-size:cover !important;
}



.login.sign_up,
A[href="/forgot_password"],
#login_form SPAN,
.footer_links {
  display: none !important; 
}


.help_text, label {
color:#c9c9c9 !important;
}
  .vertical_tab.selected {
color:#000 !important;
        text-shadow: 1px 5px 1px #000000 !important;
}


     
::-webkit-scrollbar-thumb{
background-color: #000;
border: 2px solid #fff;
height:auto;
}
 
::-webkit-scrollbar {
height:9px;
width:9px;
background-color: #000;
border: 4px solid #fff;
}

::selection {
background-color:#f9f9f9;
}








#search_query {
    background-color:  rgba(0,0,0, 0);
    color: rgb(255,255,255);
}







DIV.l-header-container { /*++*/
	background-color: rgba(0,0,0,0.05); /* or maybe pink */
	box-shadow: none; /* else there will be a faint line at the bottom */
	position: fixed; /* let the header move with the page */
}


.l-header > .compose-button {/*++*/
	background-color: #fff;
	border-color: #fff;
	opacity: .5;
}
.l-header > .compose-button:hover {/*++*/
	opacity: 1;
}


.controls_section li:hover {/*++*/
	background-color: rgba(0, 0, 0, 0.05) !important;
}








.identity, .identity-refresh , .identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column, .l-header-container--refresh, .identity-refresh .controls_section li:not(.section_header):hover {
  background: transparent;
}
    
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .right_column:after {
  border-color: transparent;
  background: transparent;
}

.identity-refresh .post_full .post_permalink {
  border-color: transparent transparent #ccc #ccc;
  background: transparent;
}



.left_column {background: none !important;}





.right_column {background: none !important;}



#right_column:after { 
display: none !important; 
}

#right_column a {
color:rgba(255,255,255,1)!important;  
}

#right_column .controls_section li a {
  color: rgba(255,255,255,1) !important; /*(0,0,0, 0.75) (255,255,255,0.75)*/
}

#right_column .controls_section li a .count {

  color: #fff !important; 
}

.controls_section_item > .posts > .hide_overflow {width:0}
.controls_section_item > .posts > .hide_overflow:before {
  content: "Posts";
  position: absolute;
}
.controls_section_item > .followers > .hide_overflow {width:0}
.controls_section_item > .followers > .hide_overflow:before {
  content: "Followers";
  position: absolute;
}
.controls_section_item > .activity > .hide_overflow {width:0}
.controls_section_item > .activity > .hide_overflow:before {
  content: "Activity";
  position: absolute;
}
.controls_section_item > .drafts > .hide_overflow {width:0}
.controls_section_item > .drafts > .hide_overflow:before {
  content: "Drafts";
  position: absolute;
}
.controls_section_item > .processing > .hide_overflow {width:0}
.controls_section_item > .processing > .hide_overflow:before {
  content: "Processing";
  position: absolute;
}
.controls_section_item > .queue > .hide_overflow {width:0}
.controls_section_item > .queue > .hide_overflow:before {
  content: "Queue";
  position: absolute;
}
.controls_section_item > .customize > .hide_overflow {width:0}
.controls_section_item > .customize > .hide_overflow:before {
  content: "Customize";
  position: absolute;
}



.new_post_label_icon > I {display: none !important}

.new_post_label { 
   padding-top: 75px !important; 
   color: rgba(255,255,255,0.75) !important; 
   /*font: italic 19px georgia, serif !important; */
}

.new_post_label:hover { 
   color: rgba(0,0,0,1) !important; 
}


#new_post:not(.editing) {
    background: none !important;
  height: short}


#new_post .arrow {
display: none
}
        
        
.l-content {
  background: transparent !important; 
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(236,191,201, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(236,191,201, 0.50)  !important;
}




.mceContentBody {
  background: url("http://static.tumblr.com/kuehu0n/T2lmudpn9/amazingphil-transparent.gif") !important; 
  background-size: 100% !important; 
  color: rgba(255,255,255, 1.0) !important; 
}


#content_top {
  width: 0px !important;
  height: 0px !important;




}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 20px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}
#footer {
  display: none !important; 
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes,
.selected .activity  {
  color: rgba(255,255,255, 0.9) !important; 
  background: transparent !important; 
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -1px !important; 
}
}
