css:
@-moz-document domain("fanfiction.net") {
body {
background-image: url(https://s6.postimg.cc/dim1a0c3l/rwby_fanfic_2.jpg) !important;
background-size: 100% 100% !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
border: transparent !important;
}

.menulink a:link { 
color: whitesmoke !important;
font-family: palatino linotype !important;
font-size: 18px !important;
font-weight: bold !important;
}

.zmenu, .tcat, .maxwidth, .table-striped tbody>tr:nth-child(odd)>td, .dropdown-menu .divider, .lc, hr, .panel_normal, .lc-left, .panel_success, .alt0, .alt1Active, .table-hover tbody tr:hover>td, .table-hover tbody tr:hover>th { 
background: transparent !important;
border: transparent !important;
}

#content_wrapper_inner {
background: transparent !important;
border-left: 1px solid transparent !important;
border-right: 1px solid transparent !important;
border-bottom: 1px solid transparent !important;
}
  
.table-bordered th, .table-bordered td, .lc-wrapper:after, .zpointer, .z-list, .modal-header, ul.topnav li a, .myborder, #content_wrapper_inner > div:nth-child(5), #content_wrapper_inner > div:nth-child(6) {
border: transparent !important;
}
 
div#content_wrapper, #p_footer { 
border: transparent !important;
background: rgba(0,0,0,0.7) !important;
}

.table-bordered, .table-striped tbody>tr:nth-child(odd)>th {
border: transparent !important;
border-radius: 10px !important;
background: rgba(0,0,0,0.6) !important;
}

.dropdown-menu, #top, .xmenu_item, .btn-group>.btn:first-child, button.btn, .t_head, ul.topnav ul li a:hover, ul.topnav ul li.active, ul.topnav li a:hover { 
color: gold !important;
border: transparent !important;
background-color: #a80c1e !important;
background-image: -webkit-linear-gradient(#000,#a80c1e) !important;
background-image: -moz-linear-gradient(#000,#a80c1e) !important;
background-image: -ms-linear-gradient(#000,#a80c1e) !important;
background-image: -o-linear-gradient(#000,#a80c1e) !important;
background-image: linear-gradient(#000,#a80c1e) !important;
}

.input-prepend.input-append input, .input-prepend.input-append select, .input-prepend.input-append .uneditable-input, select, .input-append input[class*="span"], .input-append .uneditable-input[class*="span"], .input-prepend input[class*="span"], .input-prepend .uneditable-input[class*="span"], .row-fluid input[class*="span"], .row-fluid select[class*="span"], .row-fluid textarea[class*="span"], .row-fluid .uneditable-input[class*="span"], .row-fluid .input-prepend [class*="span"], .row-fluid .input-append [class*="span"], .input-block-level, .modal-wrapper .modal, .btn:hover, .btn:focus, .btn:first-child, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
color: lightblue !important;
border: transparent !important;
background-color: #a80c1e !important;
background-image: -webkit-linear-gradient(#000,#a80c1e) !important;
background-image: -moz-linear-gradient(#000,#a80c1e) !important;
background-image: -ms-linear-gradient(#000,#a80c1e) !important;
background-image: -o-linear-gradient(#000,#a80c1e) !important;
background-image: linear-gradient(#000,#a80c1e) !important;
}
          
.modal-footer {
border: transparent !important;
background-color: #a80c1e !important;
background-image: -webkit-linear-gradient(#000,#a80c1e) !important;
background-image: -moz-linear-gradient(#000,#a80c1e) !important;
background-image: -ms-linear-gradient(#000,#a80c1e) !important;
background-image: -o-linear-gradient(#000,#a80c1e) !important;
background-image: linear-gradient(#000,#a80c1e) !important;
box-shadow: none !important;
}

.dropdown-menu>li>a, .zui a, .xgray, .gray, label.verror { 
color: lightgreen !important;
}
  
a, a:link, a:active, a:visited { 
color: gold !important;
}

div, span { 
color: whitesmoke !important;
}
}
