css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gitter.im") {
  body {
    background-color: #222 !important;
  }

  a {
    color: #dfdfdf !important;
  }

  .menu {
    background-color: #212406 !important;
  }

  body.hover .menu:hover {
    background-color: #313416 !important;
  }

  .chat-item__details {
    color: #ddd !important;
  }

  .chat-input {
    background-color: #333 !important;
  }

  .chat-input__box {
    background-color: #101010 !important;
    color: #fff !important;
  }

  .chat-input__text-area {
    color: #eee !important;
  }  

  .trpToolbar {
    color: #eee !important;
    background-color: #202220 !important;
  }

  #people-list {
    border: 2px solid #000;
    border-radius: 4px;
    border-bottom: 1px solid #000;
    border-right: 0px;
  }

  .trpTroupeRelatedContent {
    border: 2px solid #000;
    border-top: 1px solid #000;
    border-bottom: 1px solid #000;
    border-radius: 4px;
    border-right: 0px;
  }

  .search-input {
    background-color: #1F1F1F !important;
    color: #eee !important;
    border: 2px solid #000;
    border-radius: 4px;
    border-right: 0px;
  }

  .chat-header {
    background-color: #333 !important;
    color: #fff !important;
  }

  .chat-header__avatar {
    background-color: #252525 !important;
  }

  .trpContentPanel {
    background-color: #202020 !important;
    color: #eee !important;
  }

  .trpTroupeActions {
    background-color: #222 !important;
    color: #dfdfdf !important;
    border: 2px solid #000;
    border-top: 1px solid #000;
    border-radius: 4px;
  }

  .trpBtnWhite {
    background-color: #111 !important;
    color: #fff;
  }

  div.textcomplete-wrapper ul.dropdown-menu, ul.dropdown {
    background-color: #202025 !important;
    color: #fff !important;
    border-width: 2px !important;
    border-color: rgba(0,0,0,0.8);
    border-radius: 4px !important;
  }

  .popover-title {
    background-color: #2a2a2a !important;
    color: #eee;
    border-width: 1px !important;
    border-radius: 2px !important;
    border-color: #eee !important;
  }

  .popover {
    background-color: #222 !important;
    color: #eee;
    border-radius: 2px !important;
    border-color: #eee !important;
  }

  .popover-footer {
    background-color: #2a2a2a !important;
    color: #eee;
    border-radius: 2px !important;
    border-color: #eee !important;
  }

  pre code {
    border: 2px solid #000 !important;
    border-radius: 2px;
  }
}
