css:
@-moz-document domain("feedbin.com"), domain("feedbin.me") {
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

body {
    -webkit-font-smoothing: subpixel-antialiased !important;
}

/* Feed: The Coding Love */
#post1 i,
#post1 h3,
.diff-wrap,
.entry-feed-7917 .content-styles #main > div > *:not(#post1),
img[data-canonical-src*="advertisement.gif"] {
	display: none !important;
}

#post1 img,
.content-styles img {
	margin: 0 auto 20px !important;
}

/**
 * Feedbin Refurnish + Better RTL Support
 * 
 * Requires "Droid Arabic Naskh", "Iranian Sans" and "Iranian Serif" fonts.
 * Tested on bright background with Mercury as the main reading font.
 *
 * @author Sepehr Lajevardi <lajevardi@gmail.com>
 */
a[href="http://www.sourceguardian.com"] {
	display: none !important;
}

.feeds,
.feeds-inner,
.app-wrap .feeds-column {
	background: #fafafa !important;
}

.entries ul li .title,
.entries ul li .body,
.original-meta {
	line-height: 1.4em;
}

.entries ul li .body {
	font-size: 13px;
}

.entries ul li.read .favicon {
	opacity: .5;
}

.content-styles {
	line-height: 1.8 !important;
}

.entries ul li[dir="rtl"] .title,
.entries ul li[dir="rtl"] .body {
	text-align: right;
	direction: rtl;
}

.entries ul li .time,
.entries ul li .feed-title {
	text-align: left !important;
    font-size: 11px !important;
}

.entries li,
.entry-toolbar .entry-feed-title,
.next-entry-preview-inner .next-entry-feed {
	letter-spacing: 0;
    font-family: "Whitney SSm A", "Whitney SSm B", IRANSans, Vazir, Iranian Sans, serif !important;
}

.entry-inner {
	max-width: 960px !important;
	text-align: justify !important;
} 

.entry-inner li {
	text-align: left !important;
}

.entries li .title,
.next-entry-preview,
.entry-inner h1,
.entry-inner[dir="rtl"] h1, 
.entry-inner[dir="rtl"] h2, 
.entry-inner[dir="rtl"] h3, 
.entry-inner[dir="rtl"] h4, 
.entry-inner[dir="rtl"] h5, 
.entry-inner[dir="rtl"] h6 {
    font-family: Droid Arabic Naskh, Iranian Sans, Helvetica Neue, Helvetica, Arial, sans-serif !important;
	line-height: 1.4em;
}

.next-entry-preview-inner .next-entry-title {
	direction: rtl;
    text-align: left;
}

.entry-inner h1,
.entry-inner[dir="rtl"] h1 {
    font-family: Droid Arabic Naskh, "Whitney SSm A", "Whitney SSm B", -apple-system-font, ".SFNSDisplay-Regular", "Helvetica Neue", Helvetica, Arial, sans-serif !important;
}

.entry-inner[dir="rtl"] h1 {
	font-size: 28px !important;
}

[dir="rtl"] {
	text-align: justify !important;
}

[dir="rtl"] .content-styles {
	font-size: 16px;
	line-height: 2em;
	font-family: IRANSans, Vazir, Iranian Serif, Iranian Sans, Droid Arabic Naskh, Helvetica Neue, Helvetica, Arial, sans-serif !important;
	text-align: justify !important;
}

[dir="rtl"] .content-styles li {
	text-align: right !important;
}

pre, code,
.content-styles tt, 
.content-styles code, 
.content-styles kbd, 
.content-styles samp {
	font: 14px Hasklig, Monaco, Consolas, monospace !important;
    line-height: 25px !important;
    background: #f3f3f3;
    padding: 1px;
    text-align: left;
}

pre {
    padding: 5px 10px !important;
}

pre[dir="rtl"] {
	direction: rtl !important;
    text-align: right !important;
}

[dir="rtl"] br {
	line-height: 40px;
}
.content-styles li {
	margin-bottom: 1.2em !important;
}

[dir="rtl"] .content-styles {
	font-weight: 200 !important;
	color: #222;
}

[dir="rtl"] .diff-wrap,
.entry-inner .post-meta {
	direction: ltr !important;
	font-weight: 200;
	font-family: "Whitney SSm A", "Whitney SSm B", IRANSans, Vazir, Iranian Serif, "Mercury SSm A", "Mercury SSm B", Helvetica Neue, Helvetica, Arial, sans-serif !important;
}

.content-styles pre code {
	direction: ltr !important;
    text-align: left !important;
    background: transparent !important;
}

.content-styles li {
	margin-bottom: .5em;
}

.entry-inner h1,
[dir="rtl"] .diff-wrap,
.entry-inner .post-meta,
.font-serif-2 .entry-inner .post-meta {
	text-align: center;
}

.entry-inner .post-meta {
	font-size: 14px;
}

.entry-header {
	border-bottom: 1px solid #f1f1f1;
	padding-bottom: 15px;
}
                
.subscribe-promo {
	opacity: 0.4;
}

.subscribe-promo:hover {
	opacity: 1;
}

.next-entry-preview {
	font-weight: bold;
}

.content-styles a[href*="/~ff/"],
.content-styles div a[href*="feedburner.com/"],
.content-styles a[href*="feeds.wordpress.com/"] {
	display: none;
}

.feed-action-button {
	right: 30px;
	visibility: hidden;	
}

.feed-action-button:before {
	content: "â¤";
	font-size: 11px;
	visibility: visible;
}

#query,
.entries ul li .time,
.entries ul li .feed-title,
.view-toolbar .view-button {
	font-weight: 200;
}

.view-toolbar .view-button {
	letter-spacing: 1px;
	font-size: 11px;
}

.entry-feed-title {
	margin-top: 4px !important;
}

.feeds ul li .count {
	font-size: 14px;
	font-family: Georgia, "Mercury SSm A", Georgia, serif;
	top: 7px;
}

.feeds ul li .feed-link,
.feeds ul li .rename-feed-input {
	font-size: 13px;
	font-family: "Whitney SSm A", "Whitney SSm B", IRANSans, Vazir, Iranian Sans, "Mercury SSm A", Georgia, serif;
}

.toolbar-button {
	text-transform: uppercase;
	font-weight: 200;
	letter-spacing: 1px;
	font-size: 9px;
}

.content-styles table > tbody > tr > td{
	border-color: #fff !important;
}

.content-styles h2 {
	font-size: 1.2em;
    margin-top: 30px;
}

.inline-diff del, .inline-diff .diff-del {
	background: #fff !important;
}

}
