css:
@-moz-document domain("arstechnica.com") {
#qr_editor_table textarea {
    font-size: 12px;
    font-family: "Andale Mono";
}

.topic-last-post {
  overflow: hidden;
  white-space: nowrap;
}

#latest-stories ul {
  height: 470px !important;
}

.topic-title a[href^="./mcp.php"],
.mod-notice {
  background-color: transparent !important;
  display: none !important;
}

.ip-ubbcode-code-pre br { display: none; }

.post_target_unread { display: none !important; }
.unread .post_target { display: none !important; }
.unread .post_target_unread { display: inline !important; }
.post.unread .post-date { font-weight: normal !important; }

#administration-links,
#forum-links { margin-bottom: 10px !important; }

.post .post-content {
    color: black !important;
}

.post .post-edited {
    color: #555 !important;
}

.quickmod {
    font-size: 0.8125em !important;
    border: none !important;
    float: right !important;
}

#post-box dl {
    color: #535353 !important;
    font-size: 0.75em !important;
    line-height: 1.5em !important;
}

#post-box dl dt {
    font-weight: bold !important;
}

.post-author-name {
    white-space: nowrap !important;
}

.topic-list tr:nth-of-type(even) td {
    background-color: #DEDFDF !important;
}

.topic-list tr:nth-of-type(odd):not(:first-child) td {
    background-color: #F7F7F7 !important;
}

.topic-list th {
    display: none !important;
}

.topic-list td {
    border: none !important;
    font-size: 12px !important;
    line-height: 18px !important;
    padding: 4px 10px 4px 2px !important;
}

.topic-list .topic-status {
    padding: 3px 2px !important;
    width: 26px !important;
}

.topic-list .pager {
    font-size: 1em !important;
}

.post.even + noscript + #qr_editor_table,
.post.odd,
.post.odd .post-author {
    background: #DEDFDF !important;
}

.post.odd + noscript + #qr_editor_table,
.post.even,
.post.even .post-author {
    background: #F7F7F7 !important;
}

/* Classic Look */

.board-info .board-title,
#page-header a:link,
#page-body a:link {
    color: #006200 !important;
}

#page-header a:visited,
#page-body a:visited {
    color: #2F2F4F !important;
}

#page-header .breadcrumb a:visited,
#page-body .breadcrumb a:visited {
    color: #006200 !important;
}

#page-header .board-last-post a,
#page-header .board-info a,
#page-header .post-content a,
#page-header .topic-list a,
#page-body .board-last-post a,
#page-body .board-info a,
#page-body .post-content a,
#page-body .topic-list a {
    text-decoration: underline !important;
}

#page #page-body .board-info a  {
    text-decoration: none !important;
}

#page #page-body .board-info a .board-title  {
    text-decoration: underline !important;
}

.board-category,
#submit-buttons,
#submit-buttons > div,
.actionbar,
.actionbar > div {
    background: #006200 !important;
}

#submit-buttons a,
#page .actionbar a {
    color: white !important;
}

#content {
    border: none !important;
}

.board-list .status-head,
.board-list .board-head,
.board-list .topics-head,
.board-list .posts-head,
.board-list .last-post-head,
.board-list .board-topics,
.board-list .board-posts {
    background-color: #DEDFDF !important;
    color: black !important;
}

.board-list th,
.board-list td,
.topic-list th,
.topic-list td {
    border: none !important;
}

.topic-list .topic-author {
    font-size: 10px !important;
    width: 12% !important;
}

.topic-list .topic-author a {
    color: black !important;
    text-decoration: none !important;
}

.topic-list .topic-replies,
.topic-list .topic-views {
    font-size: 10px !important;
    width: 3% !important;
}

.topic-list .topic-last-post {
    color: #800080 !important;
    font-size: 10px !important;
    width: 260px !important;
}

.post .post-author {
    border-right: 1px solid white !important;
}

.post-author-title {
    margin-bottom: 0 !important;
    margin-top: 2px !important;
}

#page .post-author-title a {
    color: #800000 !important;
}

.post-author-registered,
.post-author-tribus {
    color: black !important;
    margin-top: 1.6364em !important;
}

.post-author-postcount {
    color: #006200 !important;
    margin-top: 1.6364em !important;
}

.post-author-tribus + .post-author-registered,
.post-author-registered + .post-author-postcount {
    margin-top: 2px !important;
}

.post-date-actions {
    height: 24px !important;
}

.post-date-actions-inner {
    border-bottom: 1px solid #CCCCCC !important;
    padding: 5px 0;
}

.post-date-actions-inner .post-date {
    color: #800080 !important;
}

/**/

/* Single Line Thread List */

.topic-list .topic-last-post div {
    float: left !important;
}

.topic-list .last-post-time {
    margin-right: 2px;
}

/**/

/* Fancy Quotes */

.post .ip-ubbcode-quote-title,
#preview-content .quotetitle,
.postbody .quotetitle,
.post .quotetitle {
    color: #555555 !important;
    font-weight: bold !important;
    margin-top: 12px !important;
    margin-bottom: 0 !important;
    padding: 2px 8px !important;
}

.ip-ubbcode-quote-content,
#preview-content .quotecontent,
.postbody .quotecontent,
.post .quotecontent {
    color: #555555 !important;
    margin-bottom: 12px !important;
    padding: 5px 7px !important;
}

.post .quotecontent + br {
    -display: none !important;
    line-height: 10px !important;
}

.post.odd .ip-ubbcode-quote-title,
.post.odd .quotetitle {
    background-color: #F7F7F7 !important;
    border: 1px solid #F7F7F7 !important;
}

.post.odd .ip-ubbcode-quote-content,
.post.odd .quotecontent {
    border: 1px solid #F7F7F7 !important;
}

.post.even .ip-ubbcode-quote-title,
#preview-content .quotetitle,
.postbody .quotetitle,
.post.even .quotetitle {
    background-color: #DEDFDF !important;
    border: 1px solid #DEDFDF !important;
}

.post.even .ip-ubbcode-quote-content,
#preview-content .quotecontent,
.postbody .quotecontent,
.post.even .quotecontent {
    border: 1px solid #DEDFDF !important;
}

/**/

/* Stretch page width */

#page-header,
#page-body,
#page-footer,
.post td {
    overflow: visible !important;
}

.post .post-content img {
    max-width: none !important;
}

/**/

/* Hide in-thread search box */

.actionbar .search {
    display: none !important;
}

/**/

.topic-list .topic-author a:hover {
    color: #008000 !important;
}

#page-body .post-author-inner .post-author-postcount a {
    color: #006200 !important;
}
}

@-moz-document domain("arstechnica.com") {
.ip-ubbcode-quote {
    padding-left: 8px !important;
    padding-right: 1px !important;
    padding-bottom: 1px !important;
    margin-left: 0 !important;
}
.ip-ubbcode-quote font,
.ip-ubbcode-quote-title {
    display: block !important;
/*
    margin-left: -8px !important;
    margin-right: -2px !important;
*/
    margin-bottom: 3px !important;
    padding-left: 8px !important;
    padding-bottom: 2px !important;
    font-weight: bold !important;
}
.ev_msg_rowcolor1 .ip-ubbcode-quote {
    color:           #555555 !important;
    border: 1px solid #dedfdf !important;
}
.ev_msg_rowcolor1 .ip-ubbcode-quote > font:first-child {
    background-color:           #dedfdf !important;
    border: 1px solid #dedfdf !important;
}
.ev_msg_rowcolor1 .ip-ubbcode-quote-title {
    background-color:           #dedfdf !important;
    border: 1px solid #dedfdf !important;
}
.ev_msg_rowcolor2 .ip-ubbcode-quote {
    color:           #555555 !important;
    border: 1px solid #f7f7f7 !important;
}
.ev_msg_rowcolor2 .ip-ubbcode-quote > font:first-child {
    background-color:           #f7f7f7 !important;
    border: 1px solid #f7f7f7 !important;
}
.ev_msg_rowcolor2 .ip-ubbcode-quote-title {
    background-color:           #f7f7f7 !important;
    border: 1px solid #f7f7f7 !important;
}
.ip-ubbcode-quote hr {
    display: none !important;
}
.ip-ubbcode-quote-content {
    border-width: 0px !important;
}
}

@-moz-document domain("arstechnica.com") {
#story .body,
#generic-page .body {
    /*color: black !important;*/
}

#story .body a:visited,
#generic-page .body a:visited {
    color: #7E2D00 !important;
}
}
