css:
@-moz-document domain("rpg-maker.info") {
/*
Author: DeadElf79
Version: 2.0
For: rpg-maker.info
*/

/* PROJECT OF THE MONTH
Comment this if you want to see widget */
div#maintop{
/*[[potm-show]]*/;
/*[[potm-hide]]*/;
 /* display:/*[[potm]]*/!important; */
}

/* unused toolbars and buttons */
div#toolbar, div#menubar, div#breadcrumbs, #bottom, div#itemListLeading{
  display:none !important;
}

/* logo */
div#logo, span.subtitle, div#headerbar{
  display:none!important;
}
div#header{
  height:48px;
}
div#menu{
  top:0px;
  left:0px;
}

/* may be important */
h3.header, li.level1.item5.parent, li.level1.item6.parent.last, div.wrapper-inner-b1-last {
  display: none !important;
}


/* full site width*/
.wrapper{
	width:80% !important;
}

/* chatbox improvment*/
input#chatbarText.inputbox{
  width: 70% !important;
}
div.avatar{
  display:none!important;
}
div#chatoutput{
  height:500px!important;
}

/* widgets visible */
div.wrapper-inner-b1, div.k2Pagination, div.dropdown, div.badge, div.k2FeedIcon, div.box-b1, div.box-t1, p.klatest-more{
  display:none !important;
}
div.box-3.deepest.with-header{
  padding:0px!important;
}
div.box-2{
  padding:0px!important;
  background:none!important;
}

/* online users */ 
ul.jwts_tabbernav{
  display:none!important;
}
.jwts_tabberlive .jwts_tabbertab{
 	border:none!important; 
 }

/* page borders*/
div.wrapper-t1{
  display:none!important;
}
div.wrapper-2, div.wrapper-1, div.wrapper-inner-1, div.wrapper-inner-2{
  padding:0px!important;
  background:none!important;
}

/* user right panel */
img.cHello-Avatar, .cHello-Status, .cHello-Menu{
  display:none!important;
}
.cMod-Notify {
  border:none!important; 
  display:initial!important;
 }

/* bottom side */
div.wrapper-b1{
  display:none!important;
}
}
