css:
@-moz-document domain(forums.somethingawful.com) {
#thread table.post td{
	font-size:13px;
	border-bottom-color:#AAA;
	color:#111;
	border-width:0;
}
}
@-moz-document domain(forums.somethingawful.com) {
@font-face {
  font-family: "Open Sans";
  src: local("Arial"), local('Verdana');
}
}

@-moz-document domain(forums.somethingawful.com) {
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local("Arial"), local('Verdana');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 700;
  src: local('Arial Bold'), local('Verdana Bold');
}
@font-face {
  font-family: 'Open Sans';
  font-style: italic;
  font-weight: 400;
  src: local('Arial Italic'), local('Verdana Italic');
}
@font-face {
  font-family: 'Open Sans';
  font-style: italic;
  font-weight: 700;
  src: local('Arial Bold Italic'), local('Verdana Bold Italic');
}
}
