css:
@-moz-document domain("ello.co") {
#omnibar:before {
    border-right-color: white;
}

.btn--tab {
    -webkit-transition: background-color 0.8s ease-in-out;
    background-color: #424978 !important;
    border-bottom-width: 0;
    border-left-width: 0;
    border-right-width: 0;
    border-top-width: 0;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
    color: #ffffff;
    font-family: Consolas;
    font-size: 100%;
    font-weight: bold;
    letter-spacing: 2px;
    text-transform: capitalize;
}

.btn--tab:hover {
	background-color: #51598A !important;
}

.drawer__tabs {
  background-color: #2E366B;
  margin: 0;
  padding-left: 0px;
  padding-right: 20px;
  width: 100%;
}

body:not([data-path="search-path"]) .drawer__tabs {
    border-bottom-right-radius: 15px;
}

.search__filters button:hover {
      background-color: #555681 !important;
      color: white !important;
}

.toolbar__group:before {
    content: "ello";
    font-family: Consolas;
    font-size: 160%;
    font-weight: bold;
    left: 10px;
    position: absolute;
    top: 10px;
}

div.searchbar {
    background-color: #2e366b;
}

body:not([data-path="search-path"]) div.searchbar {
 	border-bottom-left-radius: 15px; 
}

  
@media (min-width: 45em) and (max-width: 71.1875em) {
	div.invitation__icon {
      display: table-cell;
      width: 5.625em;
	}

	div.searchbar {
  		border-bottom-left-radius: 0px;
	}

	.drawer__tabs {
    	border-bottom-right-radius: 15px;
	}

	.toolbar__group:before {
  		content: "";
      	width: 0px;
    }
	.drawer {
  		padding-top: 0px;
    }
  
    .drawer__tabs {
    	padding-left: 20px;
        padding-bottom: 15px;
    }
  
  	.btn--tab {
  		text-align: center !important;
    	border-radius: 15px;
    	margin-bottom: 0px;
  	}
}

.comment-editable {
    border-radius: 15px;
}

.comment-editor--focused .comment__editor:before {
    border-right-color: #54549c;
}

.comment-editor--focused .editor__actions {
    background-color: #ebf1ff;
}

.comment__avatar .avatar-container a.avatar {
    height: 6em;
    width: 6em;
}

.comment__editor:before {
    border-right-color: #121252;
}

.drawer {
    background-color: #576099;
    padding-left: 0;
    padding-right: 0;
}

.drawer__list {
    padding-left: 30px;
    padding-right: 30px;
}

.editor__actions {
    background-color: #ebf1ff;
    border: 0;
    border-bottom-left-radius: 15px;
    border-bottom-right-radius: 15px;
    width: 100%;
}

.editor__actions .btn--ico {
    background-color: #ebf1ff;
    border: 0px;
}

.editor__actions .btn--ico:after {
    background-color: #ebf1ff;
}

.editor__actions .btn--ico:hover {
    color: #000000;
}

.editor__actions .btn--ico:hover .svgicon.icon--standard .svg__fg {
    fill: black;
}

.editor__background {
    border-radius: 15px;
}

.editor__block {
    border-style: none;
}

.editor__block [contenteditable="true"] {
    font-family: Consolas;
    font-size: 140%;
    max-width: none;
}

.form__group input[id^="user"] {
    border-radius: 15px;
}

.form__group textarea[id^="user"] {
    border-radius: 15px;
}

.friend-noise-group button {
    background-color: #2e366b;
    border-color: white;
    color: white;
}

.friend-noise-group button:hover {
    background-color: #576099 !important;
}

body[data-path="friends-path"] .toolbar .drawer:not(.notifications--active) .drawer__tabs .js-btn-friends {
    background-color: #576099 !important;
}

.headerbar {
    background-color: #7578b3 !important;
}

.invitation__header {
    padding-left: 0px;
}

.layout-mode .stage:not(.omnibar--open) .editor--focused.omnibar .editor {
    width: 95%;
}

.layout-mode.omnibar.editor {
    border-radius: 15px;
    margin-right: 0px;
    max-width: 95%;
}

.noise--active:not(.notifications--active) .drawer__tabs .js-btn-noise {
    background-color: #576099 !important;
}

.notification__avatar .avatar-container .avatar {
    height: 3.5em;
    width: 3.5em;
}

.notifications--active .drawer__tabs .js-btn-notifications {
    background-color: #576099 !important;
}

.omnibar__avatar .avatar-container a.current-user-avatar {
    -moz-box-shadow: 0 0 5px 2px #ffffff;
    -webkit-box-shadow: 0 0 5px 2px #ffffff;
    box-shadow: 0px 0px 5px 2px #ffffff;
}

.panel:not(.panel--offset) {
    background-color: #7578B3;
}

.post-comment.js-collapse {
}

.post.system-post:before {
    background-color: transparent;
}

.post__avatar a.avatar {
    border-color: #ffffff;
    border-style: solid;
    border-width: 2px;
    color: #ffffff;
    font-family: Consolas;
    font-weight: bold;
    height: 3.5em;
    margin-left: -15px;
    margin-top: -8px;
    width: 3.5em;
}

.preference--delete a[data-dispatch="deleteAccount"] {
    border-radius: 15px;
    width: 120px;
}

.preference--delete a[data-dispatch="deleteAccount"]:not(:hover) {
    background-color: #2e366b;
}

.preference__group input[type="radio"]+label {
    background-color: #2e366b;
}

.preference__group input[type="radio"]:checked+label {
    background-color: #b5b9ff;
}

.preference__group label[class="collection_radio_buttons"][for*="false"] {
    border-bottom-right-radius: 15px;
    border-top-right-radius: 15px;
    padding-right: 5px;
}

.preference__group label[class="collection_radio_buttons"][for*="true"] {
    border-bottom-left-radius: 15px;
    border-top-left-radius: 15px;
    padding-left: 5px;
}

.preference__legend span:nth-child(1) {
    color: #ffffff;
    font-family: Consolas;
    font-weight: bold;
}

.preference__legend span:nth-child(2) {
    color: #c9d8ff;
}

.profile__avatar .avatar-container .avatar--large {
    border: solid;
    border-color: #ffffff;
    border-width: 3px;
}

.search__filters {
    background-color: #2e366b;
}

.search__filters button {
    -webkit-transition: background-color 0.8s ease-in-out;
    background-color: #2E366B;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
    border-width: 0;
    color: #ffffff;
    font-family: Consolas;
    font-size: 100%;
	padding-left: 0px;
	padding-right: 0px;
	text-align: center;
}

.searchbar.searching {
    background: -webkit-linear-gradient( top, #2e366b 0px, #2e366b 183px, #808CD6 183px, #808CD6 100%);
}

.settings form p {
    color: #ffffff;
}

.svgicon.icon--standard {
    fill: white;
}

.svgicon.icon--standard.icon--bolt .svg__fg {
    stroke: white;
}

.svgicon.icon--toolbar {
    fill: white;
}

:not(.omnibar__avatar) .avatar-container a.avatar {
    border-color: #ffffff;
    border-style: solid;
    border-width: 2px;
    color: #ffffff;
    font-family: Consolas;
    font-weight: bold;
}

[data-applied="null"] .search__filters button[data-filter="recommendation"] {
    background-color: #7578b3;
}

[data-applied="null"] .search__filters button[data-filter="recommendation"]:hover {
    background-color: #7578b3 !important;
}

[data-applied="random"] .search__filters button[data-filter="random"] {
    background-color: #7578b3;
}

[data-applied="random"] .search__filters button[data-filter="random"]:hover {
    background-color: #7578b3 !important;
}

[data-applied="recommendation"] .search__filters button[data-filter="recommendation"] {
    background-color: #7578b3;
}

[data-applied="recommendation"] .search__filters button[data-filter="recommendation"]:hover {
    background-color: #7578b3 !important;
}

[data-applied="relationship"] .search__filters button[data-filter="relationship"] {
    background-color: #7578B3;
}

[data-applied="relationship"] .search__filters button[data-filter="relationship"]:hover {
    background-color: #7578B3 !important;
}

[data-applied="user"] .search__filters button[data-filter="user"] {
    background-color: #808CD6;
    border-width: 0;
}

[data-applied="user"] .search__filters button[data-filter="user"]:hover {
    background-color: #808CD6 !important;
    border-width: 0;
}

a {
    color: #ffffff;
}

a.btn {
    background-color: #2e366b;
    border-color: white;
    border-radius: 15px;
    color: white;
}

a.postbar__link {
    color: #ffffff;
    font-family: Consolas;
}

a.toolbar__discover span {
    color: #ffffff;
}

a.toolbar__invite span {
    color: #ffffff;
}

a.toolbar__settings span {
    color: #ffffff;
}

a.user-mention {
    font-family: Consolas;
}

a.wtf-toggle {
    background-color: #000000;
    color: #ffffff !important;
}

div.wtf--active a.wtf-toggle:after {
    content: " >"
}

article.comment-editor--focused .post-comment .comment__editor .comment__editable {
    background-color: #54549c;
    border: 0px;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
    color: white;
}

article.post {
    background-color: #8a97b3;
    border-radius: 15px;
    border-style: solid;
    border-width: 2px;
    margin-bottom: 20px;
    padding: 2em;
}

article.post:not(.comment-editor--focused) .post-comment .comment__editor .comment__editable {
    background-color: #121252;
    border: 0;
    border-radius: 15px;
    color: #ffffff;
}

aside.toolbar {
    background-color: #2e366b;
}

body {
    color: #ffffff;
    font-family: Helvetica;
}

button {
    -webkit-transition: background-color 0.4s ease-in-out;
}

div.editable.bscode a {
    color: #3b6deb;
    text-decoration: underline;
}

div.editor {
    background-color: #ffffff;
    border-radius: 15px;
    color: #000000;
}

div.editor__region {
    border-radius: 15px;
    padding: 0px;
}

div.featurebar {
    background-color: #000000;
    color: #ffffff;
    font-family: Consolas;
}

div.invitation__icon .svgicon {
    border-color: #ffffff;
    border-width: 2px;
    position: absolute;
    z-index: 1;
}

div.invitation__icon .svgicon .svg__bg {
    fill: #2e366b;
}

div.modal-panel section h1 {
    color: #ffffff;
}

div.modal-panel section h2 {
    color: #ffffff;
}

div.modal-panel.modal--active {
    background-color: #474747;
}

div.notification__result p {
    color: #ffffff;
}

div.omnibar {
    background-color: #2e366b;
    border-bottom-width: 2px;
}

div.panel-containment {
    background-color: #7578b3;
}

div.post-comment:not(.in) {
    height: 0px;
}

div.post-content h4 {
    color: #d6d6d6;
    font-family: Consolas;
}

div.post-content p code {
    background-color: #121252;
    border-color: #ffffff;
    border-radius: 15px;
    border-style: dotted;
    border-width: 2px;
    font-family: Consolas;
}

div.post-content p i {
    font-family: Helvetica;
}

div.post__avatar .avatar-container a.avatar {
    margin-left: -15px;
    margin-top: -8px;
}

div.preference__group a {
    -webkit-transition: all 0.4s ease-in-out;
}

div.region__tools {
    float: right;
}

div.toolbar__group {
    color: #ffffff;
}

div.wtf {
    background-color: #000000;
    color: #ffffff !important;
}

div.wtf-links {
    background-color: #000000;
}

h1 {
    font-family: Consolas !important;
}

h1.heading {
    color: #ffffff;
    font-family: Consolas !important;
}

h2 {
    font-family: Consolas !important;
}

h2.heading a {
    color: #ffffff;
    font-family: Consolas;
}

h2.heading--normal {
    color: #ffffff;
    font-family: Consolas;
    font-size: 150%;
}

input.btn {
    background-color: #2e366b;
    border-bottom-right-radius: 30px;
    border-color: white;
    border-top-right-radius: 30px;
    border-width: 1px;
    color: white;
    margin-left: -65px;
}

input.searchbar__field {
    background-color: #ffffff;
    border-radius: 15px;
    color: #000000;
    font-size: 200%;
    margin-top: 35px;
    padding-left: 15px;
}

label {
    color: #ffffff;
    font-family: Consolas;
    font-weight: bold;
}

label.collection_radio_buttons {
    -webkit-transition: all 0.4s ease-in-out;
}

label.emoji-toggle {
    background-color: #2c2c8f;
    border-bottom-right-radius: 15px;
    border-top-right-radius: 15px;
}

nav.drawer.peops--in.noise--active {
    background-color: #576099 !important;
}

nav.friend-noise-group button[data-capture="friendClicked"] {
    border-bottom-left-radius: 15px;
    border-top-left-radius: 15px;
}

nav.friend-noise-group button[data-capture="launchBlockUser"] {
    border-bottom-right-radius: 15px;
    border-top-right-radius: 15px;
    padding-right: 3px;
}

nav.navbar {
    background-color: #000000;
    font-family: Consolas;
}

path.svg__bg {
    color: #ffffff;
}

path.svg__fg {
    color: #ffffff;
}

section.posts {
    background-color: #7578B3;
    padding-top: 20px;
}

span.nsfw-flag {
    background-color: #7d0000;
    border-color: white;
    border-radius: 10px;
    border-width: 1px;
    margin-right: 5px;
}
}
