@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jkanime.net") 
{
/*---------------All---------------*/
*
{
    text-shadow:none!important;
    box-shadow:none!important;
    border-radius:0px!important;
}
@font-face {

  font-family: 'Open Sans';

  font-style: normal;

  font-weight: 300;

  src: local('Open Sans Light'), local('OpenSans-Light'), url(http://fonts.gstatic.com/s/opensans/v10/DXI1ORHCpsQm3Vp6mXoaTXhCUOGz7vYGh680lGh-uXM.woff) format('woff');

}
/*-------logo-------*/
img[src$="http://cdn.jkanime.net/assets/images/logo.png"]
  {
height:100px!important; /*width of new image*/
padding-left:250px!important; /*height of new image*/
width:0!important;background-image:url("http://k30.kn3.net/D31C7840F.png")!important
  }
/*-------Animes por letra-------*/
.lettitle,.animelet li a
  {
    color: #1D4F73;
  }
/*-------Homepage-------*/
#global
  {
    background: #181818 repeat-x scroll center top!important;
  }
#content
  {
    background: #1B1B1B repeat-y scroll center top!important;
  }
#bg_foot
  {
    background: transparent repeat-x scroll left bottom!important;
  }
#letters_bg
  {
    background: transparent none no-repeat scroll center top;
    border-bottom: 1px solid #363636;
  }
/*-----Search-----*/
.src_box
  {
    background-color: #1B1B1B;
    border: 0px;
    color: #696969;
    margin: 4px 0px 0px 95px;
  }
.searchform
  {
    visibility: visible !important;
    margin-top:-100px;
    margin-left:625px;
    float:left;
  }
/*-------Content-------*/
.capitulos_left,.capitulos_right
  {
    padding: 10px 0px 0px 0px;
  }
    /*--Emisión Destacada / Top animes - Programación--*/
#slidebox
  {
    width: 630px;
  }
.slideback,.ratedback
  {
    background: #000000 none repeat scroll 0% 0%;
  }
.top_title1
  {
    color: #353535;
    background: #141414 none repeat scroll 0% 0%;
    width: 295px!important;
  }
/*--Emisión destacada--*/
.slidebox_title
  {
    background: transparent url("http://k33.kn3.net/0DB9306EE.png") no-repeat scroll left top;
  }
/*--Programación--*/
.rtbox_title
  {
    background: transparent url("http://k33.kn3.net/6D08A4B06.png") no-repeat scroll left top;
  }
.mCSB_scrollTools .mCSB_buttonUp
  {
    background-position: -15px 0px;
  }
.mCSB_scrollTools .mCSB_buttonDown
  {
    background-position: -15px -20px;
  }
.mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar
  {
    background: #2193C7 none repeat scroll 0% 0%;
  }
.ratedback
  {
    height: 600px;
    background: #181818 none repeat scroll 0% 0%;
  }
.ratedul li
  {
    border-bottom: 1px solid #262626;
  }
div#slider.nivoSlider,#slider,.slideback
  {
    width: 610px!important;
  }
.ratedwhite
  {
    height: 580px;
    background: #181818 none repeat scroll 0% 0%;
  }
/*--ova/especiales--*/
.spec
  {
    border-top: 1px solid #262626;
  }
.spec_a
  {
    height: 50px;
  }
.spec_title
  {
    width: 190px;
    height: 32px;
  }
.spec_type
  {
    background: transparent none repeat scroll 0% 0%;
  }  
/*-----Portada/Cover-----*/
.home_portada_bg,.home_portada_bg2
  {
    background: url("http://k31.kn3.net/89B127870.png") no-repeat scroll left top rgba(0, 0, 0, 0)
  }
.big_portada_title
  {
    color: #39ACE5;
  }
/*-----Ultimos-----*/
.latest_title
  {
    padding: 0px 5px 0px 15px;
    width: 222px;
    color: #454545;
    background: #141414 none no-repeat scroll left top;
    border-bottom: 1px solid #262626;
  }  
#latest_animes_menu
  {
    background: #181818 none repeat-y scroll left top;
    border: 1px solid #181818;
  }
.latestul
  {
    background: #181818 none repeat scroll 0% 0%;
  }
.latestul li
  {
    border-bottom: 1px solid #262626;
  }
.latestul li:hover
  {
    background: #303030 url("http://cdn.jkanime.net/assets/images/right_menu_arrow.png") no-repeat scroll 5px 7px;
    border-bottom: 1px solid #262626;
  }
.latestul li a
  {
    color: #535353;
  }
/*------------Resultado busqueda------------*/
.srcpage_box
  {
    background-color: #1B1B1B;
    border: 0px;
  }
.listpage .search
  {
    background-color: #161616;
    border: 0px;
  }
.srcpage_title
  {
    background: #141414 no-repeat scroll left top;
    width: 955px;
  }
/*--otros animes que quizá te gusten--*/
.optional_title
  {
    color: #535353;
    background: #161616 no-repeat scroll left top;
    text-align: center;
    width: 958px;
  }
/*--Por genero--*/
.listpage .search
  {
    width: 968px;
  }
/*--siguiente--*/
.listnavi .listsiguiente
  {
    color: #006FA5;
    background-color: #141414;
    border: 1px solid #141414;
  }
/*------------Show Chapter------------*/
.btn_nav,.btn_nav_n
  {
    color: #1D4F73;
  }
.video_nav > div
  {
    background: #141414 !important;
    border: 1px solid #141414;
    margin-right: 10px;
    height: auto !important;
    width: auto !important;
  }
.video_nav > div:hover
  {
    border: 1px solid #1D4F73;
  }
.video_nav > a:before
  {
    content: "Capítulos";
    background: #141414 !important;
    display: inline-block;
    color: #1D4F73;
    padding: 13px 22px 10px 50px;
    font-family: "Open Sans";  
    font-weight: 300 !important;  
    font-size: 20px;  height: 28px;
    padding: 10px 15px !important;
    border: 1px solid #1D4F73;
  }
.video_nav > a:hover:before
  {
    border: 1px solid #FFB400;
    color: #FFB400;
  }
.video_nav img
  {
    display: none;
  }
.video_nav > div > a, .btn_nav_n
  {
    padding: 10px 15px !important;
    font-weight: 300;
    font-family: "Open Sans";
    cursor: pointer;
  }
/*--pelicula--*/
.lista_title_uniq
  {
    background: #141414 none repeat scroll 0% 0%;
    color: #454545;
    margin: 10px 0px 0px 10px;
    padding: 0px 0px 0px 15px;
    width: 698px;
  }
/*--pelicula 2--*/
videobox_conten
  {
    width: 992px;
  }
.video_option_act
  {
    width: 950px;
  }
the_video-1
  {
    width: 992px!important;
  }
/*-----Player default size-----*/  
iframe.player_conte, div#the_video-1.the_video lgv_1
  {
    width: 970px!important;
    height: 534px!important;
  }
embed.player_conte
  {
    width: 970px;
    height: 534px;
  }
/*---Player options---*/
.vervideo
  {
    background-image: none!important;
    margin: 0px 0px 0px -40px!important;
    width: 500px!important;  
  }
.video_actions
  {
    background-color: transparent;
    border-color: transparent;
  }
.videobox
  {
    background-color: transparent;
    border: 0px;
  }
.video_option a
  {
    background-color: transparent;
    border: 1px solid #1D4F73;
  }
.video_option a:hover
  {
    background-color: transparent;
    border: 1px solid #FFB400;
  }
/*--Select Language--*/
.video_actions
  {
    margin: 0px 0px 0px 255px;
  }
#select_lang
  {
    background-color: #181818;
    border: 0px;
  }
/*--Download Chapter--*/
.dwldclass,.dwldclass
  {
    margin: -40px 0px 0px 650px!important;
    border: 1px solid #FFB400;
  }
.rs_fullwd
  {
    border: 1px solid transparent;
  }
/*--chaat--*/
.chat_title
  {
    padding: 0px 0px 0px 25px;
    width: 230px;
    color: #545C65;
    background: #141414 no-repeat scroll left top;
  }
.comment_like
  {
    background-color: #141414;
    border-color: #141414 #141414 -moz-use-text-color;
    margin: 13px 0px 0px 10px;
  }
/*------------Anime page------------*/
/*--info--*/
.descrip_title
  {
    padding: 0px 27px 0px 12px;
    color: #353535;
    background: #181818 no-repeat scroll left top;
    border: 1px solid #262626;
  }
.descripbox
  {
    background-color: #181818;
    border: 1px solid #181818;
  }
.separedescrip
  {
    color: #535353;
  }
/*--sinopsis--*/
.sinopsis_title
  {
    margin: 0px 0px 12px 0px;
    width: 710px;
    height: 34px;
    color: #353535;
    background: #181818 no-repeat scroll left top;
    border: 1px solid #262626;
  }
.seoblock
  {
    width: 711px!important;
    background-color: #181818;
    border:1px solid #181818;
  }
.sinoptext p
  {
    color: #535353;
  }
/*--Lista de episodios--*/
.lista_title
  {
    padding: 0px 0px 0px 10px;
    width: 705px;
    color: #353535;
    background: #161616 no-repeat scroll left top;
  }
.listbox
  {
    background: #181818 none repeat scroll 0% 0%!important;
    background-color: #181818;
    border: 1px solid #181818;
    width: 712px;
  }
.listpage li
  {
    background-color: #161616;
    border: 1px solid #161616;
    width: 705px;
  }
.listpage li:hover
  {
    background-color: #242424;
    border: 1px solid #242424;
    width: 705px;
  }
.listnavi .listpag
  {
    background-color: #141414;
    border: 1px solid #FFDB00;
  }
/*-----TAGS------*/
.genres a
  {
    color: #454545;
    border: 1px solid #1D4F73;
    background-color: transparent;
  }
.genres a:hover
  {
    border: 1px solid #FFB400;
    background-color: transparent;
  }
/*-----removes: ads and things not important-----*/
embed
  {
    width:0px;
    height:0px
  }
div#head_pbm,.squareads,.ads_home,div#head_pb,div#moveboxr,div#reco,.header_text,#footer,.tag_video,.cont_top,.separepubli,.home_end_block,.expand_s,.src_btn,.feed_box,.fbk_title,.latest_end,.video_share,.seoblock_title,.seo_portada,.seotext,.search_right
  {
    display:none!important;
  }
.publibox
  {
    background-color: transparent!important;
    border: transparent!important;
    background: transparent none repeat scroll 0% 0%!important;
    width: 300px!important;
  }
#top_menu 
  {
    width: 0px;
    height: 0px;
  }
.sliderproup,.sliderprodown
  {
    background-image: none;
  }

}
/*[[fixfb]]*/
