css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filehippo.com") {
  
/* Make the fonts cleaner. Easier to read. */
  body {
      font-family:calibri !important;
      background-color: #1d1d1d !important
  }
  #content-wrapper {
      max-width: 1150px !important;
      background-color: #fefefe !important;
  }

/* Hide Stupid Ads, and other annoying spammy divs. */
  .techbeat-widget-wide-container,.platform .sprite,#techbeat-widget,#sidebar-left,.platform.news,#ad-slot-1,#ad-slot-2,#ad-slot-3,#ad-slot-4,
  .program-header-download-link.long.download-manager-enabled, .footer-link-list, #footer-copyright-notice br, .adsense-words {
      display:none !important
  }

/* Style the 'Direct Download link to look like the correct download link. The whole 'download manager' crap is gross. */
  #direct-download-link-container {
      background:-moz-linear-gradient(center top,#3cb758,#03a228) repeat scroll 0 0 rgba(0,0,0,0);
      border:1px solid #11912f!important;
      color:#fff !important;
      height:30px !important;
      line-height:1 !important;
      text-align:right !important;
      padding:12px 15px 0 !important;
  }
  .program-header-download-link-container,.program-header-buy-link-container {
      width:200px !important
  }
  #direct-download-link-container a {
      color:#fff !important;
      font-size:16px !important;
      font-weight:700 !important;
  }
  #direct-download-link-container a:hover,#direct-download-link-container a {
      text-decoration:none !important;
  }
  
/* Fix alignment issues with headers. Style shit black! */
  #navbar-first {
      background-color:#333 !important
  }
  #navbar-second {
      background-color:#444 !important
  }
  .program-title-container {
      width:800px !important
  }
  #searchbar-top {
      margin-left:0 !important
  }
  #program-header .breadcrumbs {
      margin-left:-140px !important
  }
  #program-sidebar {
      width:200px!important
  }
  .tabs-container {
      width:800px!important
  }
  #sidebar-left,#category-header {
      float:none!important;
      border-bottom:none!important
  }
  .platform.active {
      background-color:#777!important;
      border-bottom:3px solid #aa5454!important
  }
  .platform.active,.platform:hover {
      height:20px!important
  }
  #navbar-second {
      height:25px!important
  }
  .platform,.update-checker-results-link {
      line-height:20px !important;
  }
  
/* Fixes width issues on home page. */
  #latest-programs-column, #popular-programs-column{
      width: 440px !important;
  }
  #categories {
      width: 910px !important;
  }
  #footer-copyright-notice {
      width: 100% !important;
  }
  #searchbar-bottom {
      margin-bottom: 5px !important
  }
  #footer-copyright-notice {
    margin-left: 0px !important
  }
  
/* Update logo with text instead of default logo. Less resources. Less clutter */
  .logo {
      background:none!important;
      width:110px!important
  }
  .logo:before {
      content:"FHIPPO";
      display:block !important;
      font-family:calibri !important;
      font-size:26px !important;
      font-weight:bolder !important;
      color:#fefefe !important;
  }
  
/* Other Misc. Tweaks  */
  div.site-content-wrapper div#content-main div#main-column {
      width: 97% !important;
  }
  .pager-container {
      font-size: 20px !important;
      margin-top:15px !important;
      margin-bottom:25px !important;
      background: #e0e0e0 !important;
      border-top: none !important;
      display: inline-block !important;
      text-transform: uppercase !important;
      border-radius: 4px;
      padding-right: 0px;
  }
  .pager-container span, .pager-container a {
      margin: 0 0 0 -10px !important;
      padding: 15px !important;
      background: #e0e0e0 !important;
  }
  .pager-container a, .pager-container span {
      color: #888 !important;
      text-decoration:none !important;
      background: #e0e0e0 !important;
      font-weight: bolder;
  }
  .pager-container a:hover, .pager-container span:hover {
      color: #222 !important;
      text-decoration:none !important;
      background: #ddd !important;
  }
  .pager-container .pager-current-page {
      color: #990a0a !important;
  }
  .program-entry-download-link {
      background: -moz-linear-gradient(center top , #6ACA5F, #4BBE3D) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
      color: #fff !important;
      font-size:11px !important;
      text-transform: uppercase !important;
      font-weight: normal;
      padding-top: 3px !important;
      padding-bottom:4px !important;
      font-weight: bolder;
  }
  .program-entry-download-link:hover {
      background: -moz-linear-gradient(center top , #6ACA5F, #4BBE3D) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
      color: #fefefe !important;
      color: #333 !important;
  }
  .download-icon {
      background-position: -10px -97px !important;
  }

}
