@-moz-document domain("kafan.cn") {
  /*自动翻页调整*/
  p.autopagerize_page_info{background:#FFF !important;padding-left:5px !important;}/*自动翻页条背景*/
  #separatorline.emptb{display:none !important;}/*隐藏自动翻页条下面的空白分隔条*/
  /*搜索条透明处理*/
  #scbar table{opacity:0.75 !important;}
